    @Override
    public void onCreate(SQLiteDatabase db) {
        String sqlElement = "CREATE TABLE Element (\n" +
                "    id INTEGER PRIMARY KEY AUTOINCREMENT,\n" +
                "    name TEXT NOT NULL,\n" +
                "    image INTEGER,\n" +
                "    description TEXT\n" +
                ");";
        db.execSQL(sqlElement);

        String sqlPlanet = "CREATE TABLE Planet (\n" +
                "    id INTEGER PRIMARY KEY AUTOINCREMENT,\n" +
                "    name TEXT NOT NULL,\n" +
                "    image INTEGER,\n" +
                "    description TEXT\n" +
                ");";
        db.execSQL(sqlPlanet);

        String sqlZodiac = "CREATE TABLE Zodiac (\n" +
                "    id INTEGER PRIMARY KEY AUTOINCREMENT,\n" +
                "    name TEXT NOT NULL,\n" +
                "    image INTEGER,\n" +
                "    description TEXT\n" +
                ");";
        db.execSQL(sqlZodiac);

        String sqlTarotCard = "CREATE TABLE TarotCard (\n" +
                "    id INTEGER PRIMARY KEY AUTOINCREMENT,\n" +
                "    name TEXT NOT NULL,\n" +
                "    image INTEGER,\n" +
                "    cardNumber INTEGER,\n" +
                "    otherName TEXT,\n" +
                "    keyword TEXT,\n" +
                "    overview TEXT,\n" +
                "    job TEXT,\n" +
                "    love TEXT,\n" +
                "    finance TEXT,\n" +
                "    health TEXT,\n" +
                "    spirit TEXT\n" +
                ");";
        db.execSQL(sqlTarotCard);

        String sqlTarotCardElement = "CREATE TABLE TarotCard_Element (\n" +
                "    tarotCardId INTEGER,\n" +
                "    elementId INTEGER,\n" +
                "    PRIMARY KEY (tarotCardId, elementId),\n" +
                "    FOREIGN KEY (tarotCardId) REFERENCES TarotCard(id) ON DELETE CASCADE,\n" +
                "    FOREIGN KEY (elementId) REFERENCES Element(id) ON DELETE CASCADE\n" +
                ");";
        db.execSQL(sqlTarotCardElement);

        String sqlTarotCardPlanet = "CREATE TABLE TarotCard_Planet (\n" +
                "    tarotCardId INTEGER,\n" +
                "    planetId INTEGER,\n" +
                "    PRIMARY KEY (tarotCardId, planetId),\n" +
                "    FOREIGN KEY (tarotCardId) REFERENCES TarotCard(id) ON DELETE CASCADE,\n" +
                "    FOREIGN KEY (planetId) REFERENCES Planet(id) ON DELETE CASCADE\n" +
                ");";
        db.execSQL(sqlTarotCardPlanet);

        String sqlTarotCardZodiac = "CREATE TABLE TarotCard_Zodiac (\n" +
                "    tarotCardId INTEGER,\n" +
                "    zodiacId INTEGER,\n" +
                "    PRIMARY KEY (tarotCardId, zodiacId),\n" +
                "    FOREIGN KEY (tarotCardId) REFERENCES TarotCard(id) ON DELETE CASCADE,\n" +
                "    FOREIGN KEY (zodiacId) REFERENCES Zodiac(id) ON DELETE CASCADE\n" +
                ");";
        db.execSQL(sqlTarotCardZodiac);
        //init data
        initDataPlanet(db);
        initDataElement(db);
        initDataZodiac(db);
        initDataTarotCard(db);
        initRelationships(db);
    }



