    private void initDataTarotCard(SQLiteDatabase db) {
        // Tạo danh sách các bài Tarot
        TarotCard[] cards = new TarotCard[]{
                new TarotCard("The Fool", R.drawable.fool_0, 0, "Chàng khờ", "điên rồ, khởi đầu vô định, lãng phí, ngây thơ, ngạo mạn", "Lá bài này biểu hiện những khởi đầu mới mang ý nghĩa Tinh thần sâu sắc – ví dụ, bắt đầu trải nghiệm thiền định hoặc khởi đầu một mối quan hệ, trái ngược hoàn toàn với việc mua một đôi giày mới. Sự xuất hiện của The Fool có thể dự báo một khởi đầu tươi mới quan trọng.", "Những người khác có thể không hiểu hoặc không ủng hộ ý tưởng mới và/hoặc đề xuất mới của bạn, nhưng nếu bạn biết rằng mình đúng thì cứ tiến tới và cố gắng giúp họ hiểu quan điểm của bạn. Điều này đôi khi sẽ biểu thị một vị trí mới – hay thậm chí là lúc bạn phải “biến quách đi một mình” và bắt đầu công cuộc kinh doanh của chính bạn.", "Khi The Fool xuất hiện thì rất có thể bạn chưa sẵn sàng cho một mối quan hệ mang tính ràng buộc, trong khi trên thực tế những người khác lại đang thúc ép bạn làm như vậy. Nhưng đây là một dấu hiệu tích cực cho mối quan hệ tình cảm của bạn; nhiều điều hay ho dường như đang chờ chực. Chỉ là đừng để ham muốn vui vẻ của bạn khiến bạn bỏ qua “những điều thực” (tình yêu chân chính, đích thực, sâu nặng) khi nó đến với bạn nhé.", "Trong chuyện tài chính, The Fool là một dấu hiệu tích cực. Bạn có thể trông đợi những sự tăng trưởng, và hơn thế nữa, bạn sẽ tìm thấy trong chính bản thân rằng dù có gì xảy ra về tài chính đi nữa thì bạn vẫn sẽ ổn và sẽ là “kẻ chiến thắng sau hết”. Tin tưởng bản thân, làm việc chăm chỉ, và bạn sẽ làm tốt hơn bạn tưởng đấy.", "Có thể bạn sẽ gặp vài tai nạn nhỏ khi The Fool xuất hiện; hãy “ở hiện tại” và đừng làm những thứ ngốc nghếch. Dù vậy, Tổng quan, khi lá bài này xuất hiện thì sức khỏe của bạn có vẻ đang đi lên. Nếu bạn có vấn đề về sức khỏe, bạn rất có thể tìm thấy người và phương tiện bạn cần để nhanh chóng hồi phục hoàn toàn. Những suy nghĩ tích cực rất quan trọng và có thể dễ dàng đến với bạn lúc này.", "Khi lá bài này xuất hiện, bạn có vẻ mong muốn thử trải nghiệm nhiều cách tiếp cận khác nhau đến thế giớiTinh thần để xem chúng “hợp” thế nào. Không có gì là sai trái với việc khám phá tìm hiểu này, tuy nhiên đừng để cái gì cuốn mình đi quá xa trong lúc “trải nghiệm sơ qua”. Lá bài này có thể biểu thị một nhu cầu mạnh mẽ đòi có kiến thức sâu hơn về Tinh thần, và bạn bè cùng gia đình bạn có lẽ sẽ không hiểu nó đến từ đâu. Dù sao đi nữa, thì bạn vẫn phải làm những gì tốt nhất cho bạn thôi. "),
                new TarotCard("The Magician", R.drawable.magician_1, 1, "Pháp sư", "kỹ năng, ngoại giao, mục tiêu, ý chí", " Bạn có thể để ý thấy nhiều đồng phương tương tính (synchronicity) hướng thẳng đến những sự kiện Tinh thần sâu sắc xảy ra mặc mức độ “quan trọng” của sự xuất hiện hời hợt của chúng. Bạn nên tìm xem liệu bạn có kiến thức, sức mạnh và cảm hứng cần thiết để vượt qua những thử thách của mình hay không. Nhìn chung thì đây là quãng thời gian rất tích cực cho bạn đấy.", " Có một sự cải thiện trong công việc/sự nghiệp của bạn. Lá bài này chỉ ra một thời điểm xuất sắc để tìm một công việc mới hoặc yêu cầu và nhận thăng tiến trong công việc hiện tại của bạn nếu có thể. Đặc biệt nếu công việc của bạn nằm ở mảng sáng tạo thì đây là lúc những đề xuất và tác phẩm của bạn sẽ được đánh giá rất cao và có thể đưa bạn đến những thứ “to lớn hơn và tốt đẹp hơn rất nhiều”.", "Nếu bạn đang tìm kiếm một cuộc tình mới, đây là lúc bạn kiểu như gặp ai đó mới mẻ. Nếu bạn đang ở trong một mối quan hệ lâu dài, mối quan hệ sẽ có thể thăng tiến lên đến một mức mới, sâu sắc hơn của những cam kết và tận hưởng. Đây là lúc cực kỳ thích hợp để ra ngoài và gặp gỡ mọi người.", "Tiền bạc có thể đang tăng tiến khi lá bài này xuất hiện – thậm chí nếu thực tế có vẻ như không mấy khả thi trong quá trình và thời điểm trải bài. Thứ gì đó sẽ xuất hiện cho phép bạn kiếm được nhiều tiền hơn, dù đó là một ý tưởng, một cơ hội làm vài việc “ngoài luồng” hay thậm chí là một vị trí mới. Có niềm tin với bản thân và khả năng thành công của bạn nhé.", "Nếu bạn đang cảm thấy ốm o thì chắc sẽ sớm hết thôi; lá bài này biểu thị sức mạnh và/hoặc sức mạnh quay trở lại. Nếu bạn tập tành vài thứ mà không thấy khá hơn, có lẽ bạn sẽ tiến bộ lớn nếu thực hiện vài liệu pháp thay thế (tư vấn dinh dưỡng, xoa bóp, ăn kiêng, v..v).", "Đây là lúc cực kỳ thích hợp cho bạn thực hiện một vài dạng nghiên cứu Tinh thần trước đây bạn từng nghĩ tới nhưng không có cơ hội để theo đuổi. Bạn cũng sẽ có lợi từ những nhóm thảo luận có gốc gác Tinh thần. Một người thầy tinh thần có thể xuất hiện trong đời bạn hiện tại. Người này sẽ là một người thầy xuất sắc và sẽ giúp bạn nhìn đời bằng một con mắt khác."),
                new TarotCard("The High Priestess", R.drawable.high_priestess_2, 2, "Nữ Thượng Tế","bí ẩn, huyền bí, kiến thức", "Đây là lúc để dựa vào trực giác của bạn và hiểu biết từ tâm khảm nội tâm , hơn là từ nhận thức hay trí tuệ. Tập trung vào  những giấc mơ. Sự hợp nhất rất có thể chính là lúc này. Khi The High Priestess xuất hiện trong trải bài của người hỏi là nam giới thì khả năng cao anh ấy đang rất ham muốn một người phụ nữ mà anh ấy khó có thể có được.","Khả năng là một bất ngờ thú vị sẽ sớm đến với bạn trong công việc. Cũng có thể là một cơ hội công việc mới. Bạn cũng có thể sẽ tìm thấy hệ thống hoặc quy trình mới làm cho cuộc sống của bạn ít áp lực và đơn hơn. Hãy tránh xa những câu chuyện phiếm ở chỗ làm, đừng dính vào nó.","Đây có thể là khoảng thời gian kỳ diệu và bí ấn cho tình yêu. Nếu bạn đang hẹn hò cô nàng nào đấy, thì có thể cô ấy là người cách xa bạn và bạn khó mà với tới được. Hãy tin vào bản năng của mình. Nếu bạn là một cô gái, có thể có ai đó đang liên tục ra tín hiệu và muốn tiến tới với bạn. Hãy khôn ngoan đưa ra quyết định – chuyện này cũng nhiều hứa hẹn đó!","Ai đó đang không hoàn toàn thành thật với bạn về tình hình tài chính. Bạn đang thiếu thông tin. Hãyđào sâu và tự mình điều tra mọi việc. Hãy đảm bảo khi làm những việc liên quan đến giao dịch tài chính, chỉ đưa những thông tin cần thiết và không gì khác ngoài thông tin cần thiết.","Có nhiều thứ về sức khoẻ của mình mà bạn chưa biết. Nếu bạn cảm thấy có điều gì đó không ổn với cơ thể mình, thì hãy tin vào điều đó và chăm sóc bản thân. Bạn sẽ cần phải khám qua nhiều bác sĩ để biết tình hình hiện tại. Nếu có bài tập hay liệu pháp nào làm bạn cảm thấy không được ổn thì đừng làm. Hãy lắng nghe cơ thể mình. Đảm bảo là bạn đang cung cấp cho cơ thể đủ chất dinh dưỡng và tập thể dục đều đặn.","Những thông điệp có thể đến với bạn từ mọi nơi.  Hãy nhìn vào thế giới Tinh thần theo cách mà bạn chưa từng làm trước đây. Hãy nói chuyện với những người có trải nghiệm và niềm tin khác với bạn. Hãy ra khỏi vùng an toàn của mình và nó sẽ làm bạn thấy mình nhận được rất nhiều."),
                new TarotCard("The Empress", R.drawable.empress_3, 3,"Nữ hoàng", "nữ tính, người mẹ, vẻ đẹp, quan tâm", "Mọi người bị bạn thu hút nhiều hơn bình thường (trong mọi khía cạnh), rất có thể ai đó sẽ tìm đến trước cửa nhà bạn, mong bạn cho họ lời khuyên hay đơn giản là cho họ bờ vai để tựa vào. Điều này rất có lợi vì bạn sẽ có thể nhìn thấy mình từ góc nhìn của những người xung quanh – một người khôn ngoan và biết cho đi. Hãy chia sẻ cho người khác những phương diện mà khó ai khác có được trừ bạn, luôn hiểu rằng sự khôn ngoan và quan tâm của bạn là rất quý giá.","Mọi việc sẽ rất ổn. Ý tưởng của bạn, đam mê của bạn, cách bạn làm việc sẽ truyển cảm hứng mạnh mẽ cho những người xung quanh. Hãy để tiếng nói bên trong bạn được quyết định. The Empress chỉ dẫn chúng ta để cho niềm vui soi đường và những thứ khác sẽ theo sau đó.", "Đây là lúc tình yêu đầy hứa hẹn sẽ tới. Người mà bạn đang tìm kiếm tự dưng tìm đến bạn (bạn bè hoặc người yêu), hãy chú ý, mọi thứ đang rất hứa hẹn. Nếu bạn đang một mình, đây là lúc tình yêu đích thực có thể xuất hiện. Nếu bạn có một mối quan hệ hiện tại thì nó sẽ trở nên ngọt ngào hơn, sâu sắc hơn và thực hơn.", "Lá bài này biểu thị thời điểm lý tưởng để đầu tư, hãy nghe theo bản năng. Tiền sẽ đến rất nhiều. Hãy biết bỏ ra những gì mình đang có để lấy lại nhiều hơn. Hãy chia sẻ sự giàu có của bạn với những người xung quanh và bạn sẽ còn giàu có hơn nữa.", "Nếu bạn đang muốn có con, đây là dấu hiệu rất tốt, điều bạn muốn đang tới. Nếu bạn chưa sẵn sàng làm cha mẹ và việc mang thai chưa phải là điều bạn trông chờ, vậy hãy cẩn thận. Nếu đang có vấn đề sức khoẻ, hãy cân nhắc kỹ càng xem cơ thể bạn đang cố nói với bạn điều gì? Hãy thử những liệu pháp thiên nhiên hoặc phương pháp vi lượng đồng căn.", "Trực giác của bạn rất có thể đang cố gửi thông điệp cho bạn. Điều quan trọng là hãy lắng nghe kĩ càng ngay bây giờ và nếu điều đó đòi hỏi bạn dành thời gian một mình, dù vẫn có người đang cần bạn hiện diện, thì ở một mình là ưu tiên cao hơn. Lưu tâm đến bất kì thông điệp hay cảnh báo nào có thể đến với bạn quan giọng nói bên trong."),
                new TarotCard("The Emperor", R.drawable.emperor_4,4,"Hoàng đế", "trật tự, người cha, quyền lực, bảo vệ", "Có sự xuất hiện của một người đàn ông cao tuổi trong hoàn cảnh hiện tại của bạn. Có thể là đồng nghiệp, ba bạn hay người như ba bạn. Có thể người này có tình cảm đặc biệt dành cho bạn. Đừng loại bỏ người này ra chỉ vì vấn đề tuổi tác. Bạn có thể cảm thấy người này nắm quyền “thiết luân luật” trong mọi thứ và có thể làm bạn cảm thấy ngột ngạt không thể chịu đựng nổi, nhưng người đó chỉ đang cố cho bạn thấy rằng luật lệ được tạo ra là có lý do và luật có vị trí của nó – nó sẽ giúp bạn tốt hơn. Trên tất cả, người này dạy bạn rằng lợi ích của logic và cấu trúc sẽ vượt trội những mong muốn và cảm xúc nhất thời.","Công việc đang tốt và công sức của bạn hoàn toàn không bị phớt lờ. Để tâm đến tính tổ chức, hoàn thành đến cùng công việc, và hãy suy nghĩ logic. Thành quả của bạn sẽ cực kì ưu tú. Nếu bạn đang tìm việc. Đây là thời kì cần khách quan và tỉnh táo. Hãy bước ra ngoài và nhìn mình từ góc nhìn của người khác, trả lời câu hỏi tại sao họ phải thuê bạn chứ? Bạn khả năng cao sẽ tìm được việc nếu bạn đủ logic và kiên trì lúc này đây.","Có thể bạn sẽ yêu ai đó hơn tuổi. Người này có thể bị thu hút bởi yếu tố kỷ luật, logic, và tính tổ chức. Sức hút có thể không đến từ hai phía ngay vào lúc đầu nhưng nếu bạn kiên nhẫn và bền bỉ, rất có thể nó sẽ phát triển.","Lá bài này thể hiện bạn cần kỉ luật, logic và sắp xếp tài chính của bạn tốt hơn, bạn chú tâm vào điều gì khi cuộc sống phát triển. Bạn đã trân trọng đồng tiền mình làm ra chưa? Bạn có biết bạn có bao nhiều? Chúng đi đâu? Và tương tự. Đừng tập trung vào những khoản còn thiếu, vì rồi nó sẽ sinh ra. Hãy tập trung vào những gì bạn có và hãy là người quản gia tốt nhất cho tiền của mình.","Bạn có quyền có một cơ thể khoẻ mạnh. Hãy đảm bảo tính khoa học với những việc bạn làm cho cơ thể. Nề nếp và kỉ luật sẽ rất hữu ích. Ví dụ, nếu bạn đang mắc kẹt với vấn đề sức khoẻ, hãy viết ra các triệu chứng, đi kiểm tra  rồi xem xem yếu tố nào trong lối sống có bạn gây ra chuyện đó. Cân nhắc việc chấn chỉnh lại chế độ ăn uống để kiểm tra xem loại thức ăn nào cơ thể bạn phản ứng không tốt.","Hãy dành thời gian cho bản thân để thoả mãn nhu cầu phát triển Tinh thần của mình. Đừng chỉ tập trung vào những khva cạnh như logic, nguyên do, trật tự, hãy để những thông tin khác được cân nhắc. Logic có thể dẫn bạn đến thế giới Tinh thần, nếu bạn cho phép. Việc suy tư và thiền định (vốn là điều rất cần kỉ luật) sẽ trở nên dễ dàng với bạn nếu bạn chịu dành thời gian và công sức."),
                new TarotCard("The Hierophant", R.drawable.hierophant_5,5,"Đại trưởng lão", "người thầy, hôn nhân, kỉ luật, niềm tin","Đây là lá bài nghiêng nhiều về khía cạnh tinh thần/Tinh thần – mọi thứ liên quan đến tập quán, niềm tin, tôn giáo, v..v mà bạn biết đều có liên quan. Rất có thể có sự căng thẳng giữa niềm tin Tinh thần của bạn, “việc đúng nên làm” và niềm tin của những người xung quanh. Hãy tin vào niềm tin của mình. “Quy tắc” và “hệ thống” để tiến hành có thể đóng vai trò chính ở hiện tại. Một hoạt động thường xuyên dưới bất kì dạng nào đều có thể có ích lúc này, thậm chí đơn giản chỉ là việc xem phim mỗi tối Thứ Bảy với bạn bè cũng sẽ đem lại nhiều ích lợi.","Người thầy, người hướng dẫn mà bạn đang tìm kiếm sẽ xuất hiện, hoặc bạn sẽ trở thành người thầy, người hướng dẫn cho ai đó. Đây là thời điểm để gia nhập vào một nhóm nào đó, dù bạn có cho rằng mình không phải kiểu người hợp với đội nhóm đi nữa, vì bạn sẽ nhận ra rằng bạn có rất nhiều thứ cần học từ họ (theo nghĩa rất tích cực nhé!). Công việc sẽ tiến triển tốt đẹp cho bạn khả năng tổ chức mọi thứ và là theo quy tắc.","Chuyện tình cảm có vẻ đang tốt đẹp, dù có vẻ hơi thiếu “mới mẻ” một chút. Bạn nên cư xử theo cách mà bạn nghĩ là mọi người sẽ cư xử trong tình huống của bạn. Cảnh báo là bạn không nên làm gì đó lạ lẫm và khác thường.","Đây là lúc nên chơi theo luật và thực tế. Hãy dùng cách thông thường và truyền thống để quản lý tiền bạc. Tìm kiếm lời khuyên từ những người làm trong ngân hàng, các giám đốc tài chính hay bất cứ ai nhiều kinh nghiệm trong chuyện này hơn bạn. Đây là thời điểm để đầu từ, với điều kiện là nói phải ổn định và bền vững. Đây không phải lúc để thử vận may với các kiểu trái phiếu hay cổ phiếu “bất thường” này nọ.","Kỷ luật và quy củ là thứ bạn đang cần. Nếu bạn gặp trở ngại về sức khoẻ, kỉ luật, nề nếp và cam kết thì có cách cho chuyện đó cả. Cách chữa trị “truyền thống” có khả năng cao sẽ thành công ở thời điểm này.","Bạn đang rất lưu tâm đến chuyện này (kể cả khi hiện tại bạn không có tín ngưỡng hay theo giáo phái nào). Im lặng và để được tâm hồn bạn được rộng mở. Vấn đề ở đây là lập ra quy tắc cho nó – mỗi ngày, mỗi tuần hay mỗi tháng; bất cứ khi nào bạn có thời gian. Bạn sẽ cảm nhận sự thay đổi về thế giới quan như kết quả của quá trình này."),
                new TarotCard("The Lovers",R.drawable.lovers_6,6,"Tình nhân","chọn lựa, tình yêu, cảm hứng", "Có thể bạn đang cảm thấy“loạn cào cào” với một mối quan hệ hay một tình huống trong cuộc sống. Rất có thể trái tim bảo một phương mà lý trí lại kêu một hướng, việc nên làm (trong phạm vi đạo đức) là nghe theo trái tim bạn. Một số người nói rằng thật ra chỉ có 2 dạng thức tồn tại bên trong bạn: “nỗi sợ” và “tình yêu”. Khi có thể, nhất định hãy chọn “tình yêu”.","Bạn đang cần đưa ra quyết định. Phân tích mọi thông tin đang nắm, rồi đưa ra quyết định tốt nhất. Có thể lúc đầu bạn sẽ thấy là sắp có chuyện chẳng lành xảy ra. Hãy cẩn thận khi để công việc và tình yêu lẫn lộn vào nhau; nên trước khi ra quyết định, hãy chắc chắn bạn đã rõ những yếu tố liên quan.","Tình yêu (nhấn mạnh là tình yêu nhé) đang đến với bạn và chạy vào ngay trung tâm cuộc sống của bạn. Có thể là một mối quan hệ mới, hoặc một biến cố tạo nên sự liên kết mạnh mẽ với người hiện tại/người cũ. Dù là trường hợp nào thì bạn cũng sẽ rất hạnh phúc và thậm chí những người xung quanh có thể thấy được vầng hào quang hạnh phúc bao quanh bạn. Chuyện này hoàn toàn nghiêm túc. Rất có thể, dù là mối quan hệ mới chớm nở, hai bạn đã có thể nghĩ đến chuyện gặp gia đình đối phương và tính chuyện “trăm năm”. Một lần nữa, hãy chia sẻ niềm hạnh phúc của mình với những người đang cần. Cuộc sống của bạn sẽ ngập tràn hạnh phúc.","Chuyện tình cảm quá đẹp có thể khiến bạn mất kiểm soát với chuyện tài chính. Có những thứ có thể chờ, có những thứ thì không, vậy nên hãy đảm bảo là bạn lưu tâm đúng mức và đúng lúc với vấn đề này."," Nếu đang gặp vấn đề sức khoẻ, thì người có thể giúp bạn đang đến. Có thể là bác sỹ, y tá, nhà trị liệu tuỳ thuộc vào lựa chọn của bạn trong cuộc sống. Một lần nữa, hãy đối mặt với thách thức, nghĩ về sự am hiểu của bạn đối với tình hình hiện tại và biết ơn những gì bạn đang có, thực hiện các trị liệu/chữa lành nếu cần thiết."," Một lần nữa, làm việc với một người đặc biệt, như người hướng dẫn hay người lãnh tụ có thể giúp bạn nhiều thứ trong hành trình Tinh thần. Tuy nhiên, đừng tuân theo một cách mù quáng, hãy biết đặt ra nghi vấn mọi thứ, nghe theo trái tim và linh tính mách bảo, chỉ nghe những gì hữu ích và kệ phần còn lại. Một người cầm tay dẫn lối có thể sẽ đến. Hãy nhớ câu nói của người xưa: “Khi người học trò sẵn sàng, người thầy sẽ xuất hiện.”"),
                new TarotCard("The Chariot",R.drawable.chariot_7,7,"Cỗ xe", "chinh phục, chiến thắng, sức khỏe", "Lá bài hướng đến sự quyết đoán và tham vọng, hoàn thành mọi việc, kiểm tra và chứng mình những gì bạn có. Đây là dấu hiệu bạn nên tiến về phía trước với bất cứ thứ gì bạn có trong tâm trí và trái tim, rồi cố gắng hết sức. Mặc dù vậy, hãy nhớ rằng bạn cần phải dùng cả bản lĩnh và trái tim thì mới có thể thành công thực sự được.","Trong công việc, quan trọng là đừng để những thứ nhỏ nhặt choáng lấp tâm trí bạn. Sẽ có thể có người chống đối lại bạn (vô tình hay hữu ý). Đừng để những điều đó làm phiền bạn. Hãy làm những gì tốt nhất có thể , và đừng tốn thời gian chú tâm xem người khác nghĩ gì hay nhận xét gì về nỗ lực của bạn. Nếu bạn đúng (và rất có thể là vậy), rồi họ sẽ được thấy. Bạn có thể đang cảm thấy sự rạo rực trong lòng và sẵn sàng tiến tới.","Trong tình yêu, có thể bạn đang bị xúi giục đi xa và đi nhanh và/hoặc ai đó đang thúc ép bạn làm như vậy. Dù sao thì, hãy trao đổi thẳng thắn nhất có thể về giới hạn và mong muốn của bạn, hãy trung thực với bản thân về các khả năng có thể xảy ra. Nếu có người ngỏ lời mà bạn không muốn thì cứ đơn giản nói cho họ biết bạn không hứng thú. Cảm giác khó chịu lúc đầu lại có thể có lợi cho cả hai sau này.","Nhìn chung khi xét đến khía cạnh tài chính thì có vẻ mọi thứ vẫn ổn. Có thể bạn đang cân nhắc mua sắm phương tiện di chuyển – như mua xe, mua vé máy bay để đi đến nơi mà bạn muốn đi lâu rồi. Hãy ưu tiên trả các khoản nợ nếu có. Bạn có thể làm bây giờ, nếu bạn giữ vững lối sống kỷ luật.","Nhìn chung sinh khí của bạn rất tốt và bạn sẵn sàng bật dậy để lao đi bất kì lúc nào. Thời điểm này là hoàn hảo để bắt đầu hoặc tăng cường kế hoạch rèn luyện thân thể. Bạn thực tế có khả năng làm tốt hơn bạn mong đợi rất nhiều đấy.","Bạn có thể sẽ đi đâu đó (gần cũng được) để giúp bản thân tăng thêm nhận thức về thế giới Tinh thần, và bạn đang ở trạng thái có thể học rất nhanh về chủ đề Tinh thần. Hãy tham dự các buổi hội thảo, nói về các vấn đề Tinh thần còn nhiều lạ lẫm với bản thân và nằm ngoài phạm vi học hỏi của bạn trước giờ. Bạn sẽ nhận lại nhiều hơn bạn có thể ngờ nếu bạn thử những điều mới mẻ này, đây có thể là thời gian bạn cần nghiền ngẫm nhiều"),
                new TarotCard("Strength", R.drawable.strength_8,8,"Sức mạnh", "sức mạnh, tinh thần, kiên trì, bản năng", "Điểm mấu chốt là dành thời gian cho việc thiền định và suy ngẫm, kể cả trong trường hợp bạn đang có một mối quan hệ bền chặt và mười chín đứa con đi nữa! Đừng cho phép bản thân chìm trong nỗi sợ và những mối ưu tư. Những thứ bên ngoài sẽ ổn thôi, điều tối quan trọng là giữ cho đầu óc bạn tỉnh táo.","công việc vẫn ổn. Nếu bạn đang tìm kiếm công việc, hãy biết rằng bạn có đủ tiền bạc và kĩ năng để tìm ra nó. Nếu bạn đang có vông việc, có khả năng sẽ có biến chuyển, đừng ngần ngại đàm phán để có được những gì mà bạn xứng đáng. Bạn có thể sẽ bất ngờ bởi những gì mà bạn thu được. Nếu không có khả năng nào được tăng lương hay thăng tiến thì hãy suy nghĩ nghiêm túc về việc chuyển việc hoặc viễn cảnh tự mở ra công việc kinh doanh của riêng mình. Bạn có nhiều lựa chọn hơn bạn nghĩ.","Tình yêu của bạn sẽ tốt đẹp cả thôi, nếu bạn được ngỏ lời, thì đây có vẻ là mối quan hệ gắn bó mà hai bên hỗ trợ và cùng đem lại lợi ích cho nhau. Nếu bạn đang tìm người yêu, thì đây là thời điểm tốt hơn bình thường để tìm người đó. Trước hết bạn phải tự tin, nên hãy bước ra ngoài và hoà vào dòng người. Bạn có thể sẽ ngạc nhiên với cách mọi thứ sẽ diễn ra đó.","Tiền bạc đang đi theo hướng tốt. Hãy chắc chắn là bạn không quên để dành một ít, dù lâu rồi bạn không làm vậy và cảm thấy cần dùng tiền để chăm lo cho bản thân và người bạn yêu thương. Bạn có thể đã sẵn sàng để mua những thứ lớn như nhà hay xe. Và tất nhiên là đừng mượn nhiều hơn số tiến mà khả năng bạn có thể trả đấy.","sức khoẻ và sinh khí của bạn đang rất tốt. Thời điểm này rất tốt để điều chỉnh những thói quen chưa tốt để bảo vệ sức khoẻ. Bắt đầu với những mục tiêu nhỏ như đi bộ 10 phút mỗi ngày có thể giúp bạn cải thiện rất nhiều. Bạn sẽ cảm thấy khỏe mạnh hơn và sung sức hơn hiện tại nữa đấy.","Dù bạn không có đức tin thì lúc này cũng là thời gian dành cho đời sống tinh thần. Để thật sự mạnh mẽ, bạn cần chú ý đến cả 3 yếu tố cấu thành con người chúng ta: cơ thể, tâm trí và linh hồn. Hãy chắc rằng bạn dành thời gian đủ cho cả ba. Khi mọi chuyện khó khăn thì việc này sẽ gặp nhiều trở ngại, nếu bạn nhận được lá bài này ở thời điểm bạn xuống dốc, thì hãy tin rằng mọi thứ sẽ tốt lên nhanh thôi. Lá bài này để nhắc nhở bạn rằng bạn mạnh mẽ hơn bạn nghĩ nhiều."),
                new TarotCard("The Hermit", R.drawable.hermit_9,9,"Ẩn sĩ","cẩn trọng, khôn ngoan, đa nghi", "Cũng như Strength, bạn có chìm sâu vào tâm trí mình và cần thời gian một mình – đừng lo, cứ làm vậy đi. Dù chỉ là 5 phút đi loanh quanh nhà để dọn dẹp đầu óc thôi cũng được. Đây là lá bài kiểu “cố gắng hơn cả mức cao nhất để tìm ra sự thật”. Chỉ là, hãy chắc chắn rằng “làm điều đúng” nghĩa là nó cũng đúng với bản thân bạn, và đừng có chỉ biết chăm chăm vào mong muốn và nguyện vọng của người khác.","Thành công và sự công nhận từ người khác đang trong tầm tay bạn. Hãy làm hết sức để mọi thứ rõ ràng và hiệu suất cao. Điều này sẽ giúp bạn dù bạn chưa nhìn rõ được nó giúp như thế nào. Hãy làm điều đúng kể cả khi không ai nhìn thấy. Hãy nhớ bạn luôn tự theo sát những gì bản thân làm!","Có thể chuyện tình ngày trước sẽ nhen nhóm trở lại. Bạn có thực sự muốn vậy không? Có vẻ như một mội quan hệ lãng mạn không phải là ưu tiên hàng đầu của bạn lúc này. Nếu bộ não nói rằng bạn muốn một mối quan hệ lãng mạn vào lúc này, thì hãy quan sát xem xét hành vi của mình xem có đồng nhất với sự phân tích đó không? Nếu đang có một mối quan hệ cam kết rõ ràng, thì có thể là hai người đang đi theo những hướng khác nhau. Hãy đảm bảo là bạn dành thời gian và công sức để giữ mối liên kết với mối quan hệ, từ đó giúp cho mối quan hệ phát triển.","Đây là lúc để thận trọng với các giao dịch tài chính và quyết định, The Hermit xuất hiện thì có vẻ như tiền nong không phải ưu tiên số một với bạn lúc này. Nếu đang đầu tư, hãy đặt tiền vào các cổ phiếu/trái phiếu có tính an toàn cao. Đây không phải lúc để làm liều.","Thói quen ổn định sẽ có ích lúc này (ví dụ như đọc sách 1 giờ mỗi tối thứ Bảy chẳng hạn). Vấn đề sức khoẻ có vẻ như xuất phát từ lối sống của bạn nhiều hơn. Ví dụ như lưng đau nhiều khi là do bạn đang “gồng gánh” quá nhiều trách nhiệm, cảm xúc, vấn đề hay những thứ tương tự vậy trên mình. Hãy chăm sóc bản thân.","Khả năng cao là sẽ có người xuất hiện với vai trò người thầy hoặc là người dẫn dắt bạn, dù người đó sẽ không”dạy” bạn trực tiếp. Bạn sẽ học được nhiều từ người đó ở nhiều cấp độ. Hãy ưu tiên cho nhu cầu rút lui khỏi cuộc sống thường nhật trong một khoảng thời gian – chuyện này sẽ không kéo dài mãi. Bạn sẽ sớm cảm thấy mình quay lại với xã hội, tuy nhiên đừng ép bản thân phải ở cạnh nhiều người lúc này, trừ khi quá cần thiết. Tháng này bạn sẽ cảm thấy được phục hồi và tỉnh táo hơn hẳn, đặc biệt là ở khía cạnh tinh thần"),
                new TarotCard("Wheel of Fortune", R.drawable.wheel_of_fortune_10,10,"Bánh xe vận mệnh", "số mệnh, may mắn, chuyển đổi", "Lá bài này luôn nhắc nhở chúng ta rằng “mọi thứ rồi sẽ qua”. Dù là chuyện tốt hay chuyện xấu thì sẽ qua cả thôi. Nếu như đột nhiên bạn cảm thấy mình như “trên đỉnh thế giới” thì hãy nhớ rằng cũng như mọi thứ khác, rồi điều đó sẽ thay đổi. Hãy sống ở hiện tại, trân trọng những điều mình được ban tặng, và hãy hiểu rằng, có nhiều thứ trong cuộc sống không thuộc quyền kiểm soát của bạn, nhưng chuyện đó cũng tốt thôi, chẳng sao cả. Hãy hoà mình với sự thay đổi và tư duy tích cực.","Khả năng cao là bạn sẽ bỏ việc nếu bạn đang đi làm, dù công việc đó tốt đi nữa vì trái tim bạn đã không còn dành cho nó. Bạn được giấc mơ thôi thúc, dù đó là gì. Nếu bạn có mong muốn làm việc gì đó khác hơn, nhưng không biết ai khác hiểu về việc bạn muốn làm – hãy dũng cảm tìm ai đó, hỏi họ có thể ăn trưa với bạn hay dành cho bạn khoảng bốn tiếng không. Bạn sẽ ngạc nhiên khi một lời nhờ vả có thể được chấp thuận thường xuyên như thế nào.","Có thể mối quan hệ hiện tại cần được “nói chuyện nghiêm túc” hoặc nó không còn đáp ứng mong muốn của bạn nữa. Điều này không có nghĩa là mối quan hệ hiện tại của bạn đã chắc chắn “từ bỏ”. Chỉ là bạn cần hành động nghiêm túc để đảm bảo mọi thứ ổn. Hãy nói về cảm xúc của bạn, mong muốn sâu kín của bạn. Nếu bạn đang tìm một mối quan hệ, trước tiên hãy cực kì rõ ràng về mẫu người mà bạn muốn, rồi ra ngoài và cho mình cơ hội – online hay để bạn bè giới thiệu với ai đó phù hợp cũng được.","Lá bài này là điềm lành cho bất kì ai đang gặp khó khăn về tài chính dạo gần đây. The Wheel of Fortune có nghĩa là hoàn cảnh của bạn đang thay đổi rõ rệt cho điều tốt hơn. Tuy nhiên, nếu bạn đang sống dư dả, hãy cân nhắc khả năng ngược lại, có thể số tiền bạn kiếm được sẽ giảm chút ít. Nếu vậy, hãy chuẩn bị cho những ngày khó khăn. Về lâu dài, tất nhiên là mọi thứ sẽ lại thay đổi.","Ngại thay đổi sẽ ảnh hưởng không tốt đến bạn. Hãy làm bất cứ việc gì để kiểm soát stress. Khi gặp vấn đề, hãy trở về những điều căn bản: dinh dưỡng hợp lý, nghỉ ngơi điều độ và tập thể dục thường xuyên. Thiền định và Yoga sẽ là lựa chọn hoàn hảo.","Quan trọng là chúng ta phải hiểu rằng, chúng ta góp phần tác động để tạo nên những chuyện xảy đến trong đời. Hiếm khi nào có thể có chuyện mọi thứ “tự nhiên ập đến” và chúng ta chỉ là những “nạn nhân đáng thương” của hoàn cảnh. Hãy nhận phần trách nhiệm một cách công bằng, và sống tiếp. Đôi khi, đúng là mọi chuyện tự nhiên ập đến, nếu vậy, hãy nhớ, bạn chẳng bao giờ đơn độc và đi tìm sự giúp đỡ. Sự giúp đỡ bạn cần luôn sẵn ở đó."),
                new TarotCard("Justice", R.drawable.justice_11,11,"Công lý", "công bằng, lý trí, hòa giải", "lá bài này gợi lên mong muốn tìm kiếm sự công bằng, sự cân bằng và sự hoà hợp. Hãy cố gắng hết sức để tạo ra sự cân bằng và hợp lý. Bao gồm cả cách nghĩ và cách đối xử với bản thân. Nếu bạn trực tiếp tham gia vào một tình huống cần phán quyết, khả năng cao mọi thứ sẽ được giải quyết tốt.", "Bạn nên chắc chắn rằng công việc và cuộc sống đang ở đúng chỗ. Quá chú trọng vào mặt nào cũng làm mặt kia mất cân bằng. Với đa số mọi người, sẽ có mặt này dễ hơn mặt kia, hãy đảm bảo mặt còn lại cũng được chăm sóc kĩ càng với từng đó năng lượng và lưu tâm. Cân bằng là tiêu chí cần thiết để đi tới.","Nếu bạn đang tự hỏi không biết mình và người yêu có đi tiếp với nhau được không thì có thể câu trả lời sẽ là đường ai nấy đi. Ngược lai nếu bạn đang một mình, có thể bạn sẽ sớm có quan hệ nghiêm túc rất sớm thôi. Hãy rõ ràng bạn muốn gì từ một mối quan hệ, rồi bạn sẽ sớm có được nó.","Đây là lúc để chơi với những cơ hội. Thông thường, khi lá bài này xuất hiện với chuyện tiền bạc, nó có thể nghĩa là của trời cho sẽ đến với bạn. Dù bạn có không tham gia thương vụ nào liều lĩnh đi nữa, thì có thể tiền mà bạn không ngờ sẽ đến. Như vậy là rất tốt. Hãy dùng nó khôn ngoan.","Chú trọng đến cân bằng trong cuộc sống, sống ở thì hiện tại. Điều độ không phải là thứ chỉ dành cho thầy tu, đó là phần nhất thiết phải có cho cuộc sống tự chủ và khoẻ mạnh. Có thể bạn đang mất cân bằng – đặc biệt là nếu đang có vấn đề sức khoẻ, hãy tìm cách chữa trị. Bạn có khả năng làm được chuyện đó.","Cảm nhận sự hiện diện ở thì hiện tại. Bạn có cảm thấy liên kết với trái đất và những người xung quanh. Bạn cảm thấy mình có thuộc về thế giới này không? Cân bằng một lần nữa là điều cần lưu tâm ở đây. Chọn góc nhìn toàn diện về cuộc sống sẽ giúp bạn cầm trịch mọi thứ."),
                new TarotCard("The Hanged Man", R.drawable.hanged_man_12,12,"Người treo ngược", "chiêm nghiệm, hy sinh, hiến dâng", "Đã đến lúc để cho những thái độ sống và mong ước không còn hữu ích biến mất khỏi cuộc sống của bạn. Sự hy sinh sẽ mang lại những chuyển biến tích cực hơn những thứ bạn cứ giữ khư khư. Trên hết, đây là lúc để suy ngẫm, chiêm nghiệm, đặc biệt là khi bạn đang mong muốn nhìn thấy điều gì là hữu ích cho mình. ", "Chẳng có gì đặc biệt, và bạn đang chán ngấy. Nếu vậy, cần xem xem bạn có thể làm gì để thay đổi không. Nếu không, đừng tự đổ lỗi cho bản thân chuyện mọi thứ cứ ì ạch. Mọi thứ sẽ thay đổi. Chỉ là chưa phải theo dự tính mà bạn muốn thôi. Thư thả đi, còn không, hãy tìm công việc khác.","Một lần nữa, phông nền chủ đạo là “buông bỏ”. Ví dụ, buông bỏ người mà bạn không chạm tới được có thể sẽ tạo cơ hội cho người phù hợp xuất hiện với bạn. Nhưng đó chỉ là một ví dụ. Rất có thể bạn cần buông bỏ cách nhìn nhận đối với một mối quan hệ mà bạn cho rằng “mình chẳng thể hạnh phúc nếu thiếu anh ấy/cô ấy. Nếu bạn đang có mối quan hệ lâu năm, có thể hai bạn đang ở ngã ba đường. Nếu bạn cảm nhận mọi thứ vẫn tuyệt hảo thì nên tìm hiểu xem đối tác của bạn có cảm nhận giống vậy không.","Nếu bạn đang túng thiếu, thì đến lúc học cách cho đi, phải, hãy cho đi để mở rộng dòng chảy, dù tất cả những gì bạn có chỉ là một mẩu bánh mì. Hãy hiểu rằng luôn có ai đó ở cạnh sẵn sàng chìa tay ra sẻ chia với bạn mẩu bánh mì hiếm hoi đó. Những việc đơn giản như thế này có thể xoay chuyển bánh xe và mang sự thịnh vượng của bạn sang một giai đoạn tốt hơn.","Bạn có thể thay đổi triệt để góc nhìn nếu bạn cho rằng tình trạng thể chất của mình đang không ổn. Nói vậy không có nghĩa là bạn phải phớt lờ hết mọi dặn dò của bác sĩ, chỉ là hãy nghiên cứu, nghiền ngẫm nó sâu hơn. Rất có thể có những chuyện bạn nghĩ bạn biết rồi nhưng sự thật không phải vậy, nên làm cách khác thì hơn. Sức khoẻ sẽ tốt lên, nhưng đó là cả một quá trình.","Việc quan trọng là buông bỏ những suy nghĩ tiêu cực, niềm tin hạn chế bản thân. Bạn vẫn nhớ nguyên lý của việc “tự kỉ ám thị” chứ, nên hãy cẩn thận với những gì bạn cho phép bản thân nói và nghĩ về cuộc đời. Hãy chắc rằng những “ám thị” bạn đưa ra chí ít tương đồng với những gì bạn muốn."),
                new TarotCard("Death", R.drawable.death_13,13,"Thần chết", "kết thúc, thay đổi, phá hủy, chuyển đổi", "Bạn càng học cách chấp nhận và bỏ bớt mong muốn kiểm soát bao nhiêu thì khoảng thời gian này sẽ càng dễ dàng hơn với bạn. Thường những thứ mà bạn cần buông bỏ chính là những niềm tin và thái độ hạn chế bản thân. Nhiều khi bạn chẳng thể biết được góc nhìn của bạn đã tổn thương bạn nhiều thế nào, và nếu đúng thế, thì lúc này chính là hồi chuông cảnh tỉnh. Năng lượng chủ đạo lúc này không chỉ là thay đổi hay huỷ hoại, mà theo sau nó chính là sự hồi sinh. Dù một cánh cửa đóng lại nghĩa là một cánh cửa khác được mở ra, nhưng liệu bạn có đủ can đảm để bước qua?","Nếu bạn ghét công việc của mình, hay đang làm một công việc dưới sức, nhưng bạn lại không muốn nghỉ việc vì thu nhập ổn định, thì có thể cuối cùng bạn cũng bị ép tới mức phải bỏ. Tất nhiên, điều này sẽ dẫn dắt bạn tới “điều tốt đẹp hơn,” nhưng sao bạn lại không tự mình lựa chọn rời đi? Bạn có thể được lợi nhiều nhất khi thay đổi triệt để. Hãy quyết đoán. Cứ nhảy đi. Bạn có thể làm được.","Nếu bạn đang bám lấy một mối quan hệ mà bạn chẳng vui vẻ gì, chỉ vì bạn không muốn đánh mất cảm giác đã quen thuộc, hay bởi vì bạn ghét phải một mình, thì nghĩa là bạn đang chẳng giúp gì cho mình hay người kia cả. Hãy nói chuyện thẳng thắn, nếu đối phương không muốn cũng không thể thay đổi hoặc bạn như vậy, thì có lẽ là đến lúc bỏ nó đi rồi. Nếu bạn đủ can đảm để rời bỏ vòng an toàn của mối quan hệ này, bạn sẽ tìm được một mối quan hệ khác tốt hơn nhiều. Nhưng trước hết, bạn phải đối mặt với vấn đề đã, và cố gắng giải quyết nó. Không phải mọi mối quan hệ đều có thể “sửa” được. Đừng đâm đầu vào tường quá lâu. Bạn sẽ cảm nhận được khi nào là “đủ”.","Bạn có thể sẽ phải hy sinh đôi chút để đối mặt với khó khăn tài chính sắp tới. Bạn có thể làm được. Đừng ngần ngại nhờ giúp đỡ nếu cần thiết. Bạn không cần phải mắc kẹt và chịu đựng một mình, mọi chuyện cũng không cần đi theo hướng đó, mặc dù khả năng cao là bạn sẽ phải chịu mất mát về tài chính vì mất việc, cổ phiếu mất giá, v.v. Thì cũng đừng trốn tránh thực tại, nếu chuyện này tới. Xác định chính xác bạn đang phải đối mặt với cái gì , đối mặt với nó, rồi làm những thứ cần làm thôi. Cuối cùng, bạn sẽ thu lại nhiều hơn nhờ bài học này. Đường dài bạn sẽ ổn thôi.","Kiểm soát stress là cách để đảm bảo sức khoẻ trong giai đoạn này. Tím cách giải quyết mọi vấn đề thể chất, đừng né tránh. Nghỉ ngơi đầy đủ, đừng đầu độc bản thân với với bia rượu và những thứ tương tự.  Nếu cần hồi phục, hãy chú ý cả phương diện thể chất và tinh thần. Đặc biệt nếu bạn còn độc thân, lúc này, hãy tìm cảm giác “được chạm” (sờ mó), mát xa, cái ôm từ những người bạn thân. Những điều này sẽ giúp bạn rất nhiều.","Hãy vun trồng những thái độ tích cực. Hiểu rằng dù bạn có đang lạc giữa “màn sương của tâm hồn” đi nữa, thì rồi mọi chuyện sẽ qua. Đừng chống lại sự thay đổi. Cho phép bản thân cảm nhận những gì nó thực sự cảm thấy, nhưng đừng đắm chìm trong nỗi tuyệt vọng; nếu bạn cần sự giúp đỡ để kéo mình ra khỏi bất kì nỗi đau hay mất mát nào, hãy bước ra ngoài."),
                new TarotCard("Temperance",R.drawable.temperance_14,14,"Tiết chế","thích nghi, cân bằng, tiết chế","Nhìn chung mặc dù tất cả mối quan hệ của bạn gần như đang hoàn toàn tốt, bạn có thể cần nhìn lại các vấn đề cá nhân sâu xa hơn của mình và xem liệu chúng có đang ngáng đường bạn hay không. Và nếu có thì làm cách nào để khắc phục được chúng và kết hợp với chúng. Ý tưởng hiện tại là làm những gì để tìm thấy sự cân bằng trong bản thân bạn, trong các mối quan hệ của bạn, với những mục tiêu và niềm đam mê của bạn.","Vạch ra mục tiêu lớn, nhưng đừng quá mong đợi ở bản thân mình hoặc người khác những gì mà bạn hoặc họ không thể đáp ứng được. Làm việc với tính kiên nhẫn và sức chịu đựng, và bạn sẽ nhận lại được những gì xứng đáng với mình về lâu dài. Các thành quả bạn đạt được sẽ không bị bỏ sót đâu.","Thường thì trước khi bạn có thể thật sự có được sự yên bình và từng trải trong các mối quan hệ của mình, thì bạn cần có sự yên bình trong chính bản thân mình. Hãy xem lại những gì bạn cảm thấy tội lỗi, lỗi lầm mà bạn cảm thấy mình đã tạo ra trong quá khứ. Sau đó tự tha thứ cho chính bản thân mình, và để chúng tan đi.","Dường như có một sự cân bằng tốt giữa những gì đang ra đi và những gì sắp đến. Tuy nhiên, nếu bạn cần gia tăng hoặc tập hợp lại các khoản thu nhập của mình, bạn sẽ phải đòi hỏi những gì bạn xứng đáng nhận được. Hãy can đảm và kiêu hãnh, thì bạn sẽ không gặp phải thất bại đâu.","Cân bằng và điều độ là chìa khóa. Liệu bạn có ăn uống tốt, ngủ đủ, chơi, làm việc và tập thể dục điều độ? Hãy xem liệu mình có đang mất cân bằng để tìm cách điều chỉnh lại. Sự điều độ là phương án hoàn hảo cho sức khỏe của bạn lúc này."," Đây là khoảng thời gian quan trọng để suy ngẫm và lắng nghe tiếng nói hướng dẫn từ bên trong bạn, và nếu bạn cần sự giúp đỡ, hãy cứ yêu cầu. Hãy hỏi từ thượng ngã (high self) của bạn và từ những người khác nữa."),
                new TarotCard("The Devil", R.drawable.devil_15,15,"Ác quỷ", "mù quang, tham vọng, bạo lực", "Điều quan trọng là bạn cần nhớ rằng trong bất kỳ hoàn cảnh nào, bạn vẫn luôn có sự lựa chọn. Trước hết, đừng để người khác nói với bạn rằng những lựa chọn của bạn có giới hạn. Nếu bạn vấp phải kết luận đó thì hãy chắc rằng nó xuất phát từ chính bạn. Một điều nữa cũng rất quan trọng là phải nhớ rằng bạn có thể giải phóng bản thân mình khỏi bất kỳ giới hạn nào đang cầm chân mình, và bạn nên chọn cách làm như vật ở bất kỳ thời điểm nào. Hiện tại bạn có thể có cảm giác rằng bạn không kiểm soát được cuộc sống của mình; đôi khi điều này xảy ra là kết quả từ những hành động của chính bạn, nhưng thường là hậu quả của sự trì trệ. Vì vậy, hãy hành động, hãy dấn bước vào bất kỳ điều gì bạn có thể để mang lại cho mình cảm giác kiểm soát được. Thậm chí nếu chỉ đơn thuần đưa ra lựa chọn về nơi bạn sẽ dự định ăn tối. Những bước đi nhỏ cũng rất quan trọng.","Bạn có thể cảm thấy rất rõ cảm giácbị trói buộc vào một công việc mà bạn không thích nhưng không thể thấy được cách thức rõ ràng nào để rời bỏ vì cảm giác được đảm bảo với công việc đó. Hãy xem xét liệu việc đánh đổi vậy có đáng không; thực chất của vấn đề như ngày làm, tuổi tác, việc nhiều hay ít, trợ cấp, v.v . có thực sự được đảm bảo hay không? Nếu bạn chọn ở lại, hãy nhận thức rằng đây là một lựa chọn mà bạn đang đưa ra,  chứ không có gì hay ai khác áp đặt bạn. Nếu bạn cảm thấy việc đánh đổi này không đáng, hãy dập tắt những cảm giác này và nghĩ về điều bạn có thể làm để thay đổi hoàn cảnh. Bạn sẽ không bị trói buộc trừ khi bạn cho phép bản thân mình bị trói buộc.","Nếu bạn đang trong một mối quan hệ tình cảm lâu dài, thì một trong hai bên hoặc cả hai có thể đang trong hoàn cảnh như thể cảm thấy như đang bị trói buộc. Nếu bạn đang có những dấu hiệu như vậy, hãy đi nói rõ chuyện đó nếu bạn muốn giữa vững mối quan hệ. Chúng ta luôn có lựa chọn. Hãy chủ động để chuyện thần kỳ xảy ra. Nếu bạn đang tìm kiếm tình yêu của mình, đây có thể là tín hiệu rằng bạn đang tìm kiếm trong vô vọng, một thái độ rõ ràng đẩy những đối tượng có-khả-năng-yêu-bạn ra xa. Nếu bạn rơi vào trường hợp đó, hãy xem xét dành một khoảng thời gian ngắn để thoát khỏi mối quan hệ đó – để tìm kiếm và hiểu bản thân mình tốt hơn. Hãy quay lại cuộc tìm kiếm của bạn khi bạn cảm thấy không còn cấp bách nữa.","Mọi thứ có thể hoặc sẽ rất căng thẳng trong tương lai gần. Hãy cố đừng cáu gắt với hoàn cảnh của mình, thay vào đó hãy giải quyết trong bình tĩnh và sáng suốt. Hãy đi từng bước một. Nếu bạn cần giúp đỡ về tài chính, đừng ngần ngại hoặc quá kiêu hãnh để đưa ra yêu cầu. Sự giúp đỡ luôn sẵn sàng với bạn. Nếu bạn cảm thấy tình hình tài chính đang dư dả ở hiện tại thì đây vẫn là thời điểm nhắc nhở bạn tiết chế về tài chính. Hãy tiết kiệm để phòng những ngày gian khó!","Đây là thời điểm bạn cần đề phòng bị làm việc quá sức, quá mệt, và quá căng thẳng. Chuyên cần tập thể dục cũng như làm những việc giúp được bạn tập trung vào những vấn đề người khác đang đối mặt thay vì của chính bạn đem lại nhiều lợi ích với bạn. Nếu bạn đang dính phải vấn đề sức khỏe mãn tính, đừng để nó đè nén bạn; bạn còn nhiều thứ khác trong cuộc sống hơn là chỉ có bệnh tật, và bạn sẽ được chữa chị tốt hơn bằng cách lợi dụng những thử thách về sức khỏe như một động lực để phát triển.","Việc giữ một quan niệm tích cực là điều vô cùng quan trọng trong lúc này. Những lời xác tín tích cực rất quan trọng và sẽ có lợi cho bạn. Hãy nhìn những người xung quanh bạn. Liệu họ là người lạc quan, tích cực giữa mọi người, hay họ là những người thích chỉ trích thái quá, những người đi bắt lỗi người khác khiến cho bạn phiền muộn? Gieo suy nghĩ, gặt hành động. Cuộc đời quá ngắn ngủi, mà tâm trạng lại dễ lây truyền cho nhau."),
                new TarotCard("The Tower",R.drawable.tower_16,16,"Tòa tháp", "sụp đổ, tranh cãi, nguy cơ", "Có vẻ như có một số người / hoàn cảnh mà bạn tin rằng sẽ luôn vì bạn, luôn là nơi hay kẻ mà bạn có thể dựa dẫm vào, trở nên thay đổi không còn được như xưa nữa, và bạn không còn có thể trông cậy vào những con người hay hoàn cảnh đó được nữa. Vấn đề này không phải là điều gì đó quá kinh khủng; sự thay đổi là bản chất của cuộc sống, và việc cố gắng ngăn chặn nó cũng giống như bơi ngược dòng nước mà không có mái chèo. Nếu bạn đang mơ tưởng điều này, đây có thể là lúc quay về với thực tại. Bạn sẽ gặp rắc rối nếu bạn cứ thả hồn mình như vậy. Bạn sẽ có thể thay thế chúng với những giấc mơ và mục tiêu thực tế và dễ đạt được hơn nếu một số ảo tưởng tan vỡ trong tháng này. Việc mơ mộng cũng quan trọng, tuy nhiên cũng quan trọng không kém là sống thực tế. Bạn sẽ vượt qua được khoảng thời gian này ổn thỏa, đặc biệt nếu bạn biết rằng mình có mọi nguồn lực mà bản thân mình đang cần để giải quyết các vấn đề cuộc sống và để thực hiện những đều bạn cần thực hiện.","Năng lượng vào thời điểm có khuynh hướng dẫn tới các tranh cãi hay xung đột; hãy cố giữ cho tính khí bạn trong tầm kiểm soát. Những xoay chuyển đảo ngược bất chợt có thể xảy ra lúc này. Đây không phải là một dấu hiệu khiến bạn sẽ phải hoảng sợ. Tuy nhiên, nó lại là một dấu hiệu cảnh báo trước để bạn chuẩn bị. Ngay cả khi bạn nghĩ rằng mình đang có một công việc hoặc một nguồn thu nhập ổn định nhất thế giới, bạn sẽ làm gì nếu tất cả chúng biến mất vào ngày mai? Nơi nào bạn có thể chuyển đến để làm việc ngay lập tức nếu bạn bị bắt buộc phải ra đi? Việc suy nghĩ thông suốt ngay từ bây giờ, khi bạn chưa trong tình trạng khủng hoảng, có thể là lợi thế lớn với bạn phòng khi bạn bị tuột dốc. Nếu bạn cảm thấy chán ghét công việc hiện tại, hãy suy nghĩ nghiêm túc để quyết định thay đổi. Bạn đáng được hưởng hạnh phúc mà!","Đây có thể là (nhưng không nhất thiết) một dấu hiệu của một mối quan hệ có khả năng kết thúc. Nếu bạn đang trong một mối quan hệ mà bạn thực sự không muốn dẫn đến kết thúc, thì đây là thời điểm quan trọng dành cho việc kiểm soát mối nguy và dành cho việc nói chuyện rõ ràng với nhau. Hãy tìm hiểu xem đối tượng của bạn đang cảm thấy như thế nào. Đừng suy diễn hay tự đưa ra giả thuyết, mà hãy tìm hiểu rõ ràng.","Đây không phải là thời điểm dành cho những cuộc đầu tư mạo hiểm, đánh cược, hay dựa vào xổ số để giải quyết các vấn đề của bạn. Ngay cả khi bạn biết những tin tức không tốt lành mấy, thì cũng hãy tìm hiểu chính xác xem bạn đang phải đối mặt với vấn đề tài chính nào. Nó có thể không đến mức tệ như bạn nghĩ đâu. Tuy nhiên, tránh những rắc rối vẫn là tốt hơn cả. Hãy giải quyết vấn đề theo một cách đơn giản.","Việc chú ý những gì bạn đang làm rất cấp thiết vào lúc này. Hãy cẩn thận. Thần kinh bạn có thể bị căng thẳng, hãy làm bất cứ điều gì cần thiết để bạn bình tĩnh lại và ở trong trạng thái ổn định. Việc tập thể dục có thể giúp ích cho bạn. Nếu bạn biết được những gì bạn đang làm (và biết được về chứng dị ứng của mình, hay những phê chuẩn của bác sỹ) thì hãy cân nhắc việc thử thư giãn bằng thảo mộc. Hãy cẩn thận với thuốc và thức uống có cồn trong thời điểm nay, nếu bạn đang dùng chúng.","Đây cũng là thời điểm để giữ một thái độ tích cực nhất có thể. Những hiểu biết và nhận thức mới mẻ có thể đến với bạn trong chớp mắt."),
                new TarotCard("The Star",R.drawable.star_17,17, "Ngôi sao", "hy vọng, thấu hiểu, nỗ lực", "Lá bài này là một dấu hiệu hoàn toàn tốt. Bạn có lẽ đã sẵn sàng để duy nghĩ tích cực; đây là thời điểm để làm điều gì đó thật đặc biệt. Những thay đổi nào bạn muốn làm hoặc muốn thấy trong cuộc đời mình? Hãy viết ra một danh sách. Bạn có thể hoàn thành gần như bất kỳ thứ gì nếu bạn bắt tay vào làm ngay lúc này. Hãy suy nghĩ lớn.","Công việc hiện tại bạn sẽ rất tốt. Nếu bạn không hài lòng ở vị trí hiện tại, đây là lúc bạn có thể mong đợi những cơ hội mới ập vào cuộc đời mình nếu bạn mở lòng để đón chúng. Hãy sẵn sàng cho những cơ hội đó. Hãy xem lại CV của mình, đọc hướng dẫn và chiến lược về cách tạo ra những thay đổi tích cực trong sự nghiệp, và khi cơ hội gõ cửa, hãy chuẩn bị để trả lời."," Đây là thời điểm tuyệt vời để gặp một ai đó mới nếu bạn đang loay hoay tìm kiếm một mối quan hệ mới. Nếu bạn đang sẵn sàng yêu, hãy ra ngoài và hòa nhập với mọi người. Một mối quan hệ mới có thể xảy đến bất cứ lúc nào. Nếu bạn đã gắn kết với một mối quan hệ nào đó từ trước, thì mối quan hệ của bạn có thể chuyển lên một tầm mới, cao hơn và hòa hợp nhau hơn.","Sự xuất hiện của The Star là một dấu hiệu cực kỳ tích cực. Bạn sẽ làm kinh tế tốt hơn bạn tưởng. Đây là thời điểm tốt để đương đầu với rủi ro (chỉ với những thứ bạn đủ sức) để mưu cầu nhiều tiền hơn trong công việc của bạn, hoặc để bán được hàng hóa. Bạn sẽ có mọi thứ bạn muốn, khả năng là có nhiều hơn. Hãy tận hưởng chúng.","Đây sẽ là một thời điểm tuyệt vời về sức khỏe. Nếu bạn đang chờ đợi kết quả kiểm tra một số bệnh, chúng sẽ gần như là kết quả rất tốt. Bạn sẽ có bình tĩnh và nghị lực. Hãy chăm sóc tốt cho bản thân mình.","Bạn đang tiếp cận rất gần với tinh thần của mình ngay lúc này, nên đây sẽ là một thời điểm tuyệt vời để bạn tiếp xúc với những người khác và chia sẻ kinh nghiệm và niềm vui của mình. Những nỗ lực của bạn đang làm sẽ được đền đáp gấp nhiều lần. Thế giới cần ánh sáng và hạnh phúc của bạn. Hãy luôn tỏa sáng cho mọi người."),
                new TarotCard("The Moon", R.drawable.moon_18,18,"Mặt trăng", "huyễn hoặc, ảo tưởng, lừa dối","Bạn chắc chắn đang ở trong một giai đoạn tâm linh và trực giác hơn bao giờ hết, vì vậy điều quan trọng là bạn tin vào bản năng và cảm giác của mình, cho dù bạn không biết chúng từ đâu đến. Cũng vậy, khi lá bài này xuất hiện, nó không đem lại cho bạn sự hồ nghi, mà giúp bạn nhận ra rằng mọi thứ không phải lúc nào cũng như vè bề ngoài mà bạn thấy. Nếu bạn cảm thấy bạn không thể tin ai đó, thì rất có thể đúng là họ không đáng tin. Hãy tin vào bản năng của mình. Nếu bạn đang chờ câu trả lời của ai đó, có thể bạn sẽ mất nhiều thời gian để nhận được đấy. Hãy cố gắng kiên nhẫn.","Không may là mọi việc có thể không rõ ràng một chút nào. Hãy hỏi và tập lắng nghe chủ động để đảm bảo rằng bạn và đồng nghiệp hiểu nhau hoàn toàn. Một lần nữa, sự kiên nhẫn là cần thiết. Hãy bình tĩnh, và cụ thể là khi bạn cảm thấy như đang bị công kích thì hãy tin vào đồng nghiệp hoặc người giám sát của bạn. Sự thật có thể không tệ như bạn nghĩ đâu.","Lá bài này chỉ đến khoảng thời gian nhiều cảm xúc trong tình cảm. Mọi việc có thể nhanh chóng trở nên sôi sục và/hoặc không thoải mái. Hãy nghỉ ngơi nếu bạn thấy cần. Đừng cố suy diễn, và hãy tranh cãi một cách công bằng, nếu bạn buộc phải tranh cãi. Hãy chọn cách bạn tranh cãi đảm bảo rằng bạn đối xử với người yêu như là bạn bè. Đừng gấp gáp chấm dứt hay bắt đầu một mối quan hệ mới. Đây có lẽ không phải là lúc thích hợp cho cả hai việc này.","Nếu bạn đang chờ đợi những quyết định về mặt tài chính do người khác thực hiện, thì sự xuất hiện của lá bài này có thể ngụ ý rằng bạn sẽ phải chờ đợi lâu hơn nữa. Đây không phải là lúc để ra những quyết định kinh doanh nếu không có những dẫn chứng thật cụ thể và chắc chắn.","Hãy luôn nghe theo trực giác của bạn đối với các vấn đề về sức khoẻ. Nếu một chẩn đoán (cho dù là dương tính hay âm tính) mà bạn không cảm thấy đúng, hoặc bạn cảm thấy không thoải mái với dịch vụ chăm sóc sức khoẻ hiện tại, hãy chọn một giải pháp khác hoặc một dịch vụ khác. Đối với những người uống rượu hoặc đang nghiện các chất kích thích, đây là khoảng thời gian chắc chắn là dễ dàng nhất để bạn từ bỏ. Bạn vẫn có thể sử dụng chúng nếu cần nhưng cũng cần chú ý tiết chế điều độ vừa phải thôi.","Đây là khoảng thời gian mang đậm tính tâm linh và là thời điểm tuyệt vời để đọc một cuốn sách, thực hiện trị liệu hồi phục, hoặc những phương thức trợ giúp hữu ích từ những hoạt động tâm linh khác. Bạn đang nghiêng về phía tâm linh hơn bình thường; hãy chú ý đến những suy nghĩ và cảm xúc của bạn, và với cả những thông tin chớp nhoáng mà bạn không biết chúng từ đâu ra. Đây cũng là thòi gian tuyệt vời để tìm đến những quyển sách về tâm linh nổi tiếng thế giới – bạn sẽ học được nhiều thứ hơn bình thường đấy!"),
                new TarotCard("The Sun", R.drawable.sun_19,19,"Mặt trời","vinh quang, danh tiếng, hạnh phúc","Mọi thứ nói chung là có thể đang tiến triển rất tốt cho bạn. Tuy nhiên, mặc dù tài chính đang có vẻ rất tốt khi bạn rút được lá bài này, điều đó không có nghĩa là bạn nên mất tập trung vào những điều thật sự quan trọng trong cuộc đời bạn, và cần biết rằng tiền bạc không nằm ở vị trí cao trong danh sánh những thứ đó. Đây là thời điểm tốt để hòa nhập, gặp gỡ những người mới, và giao lưu bạn bè.","Nếu bạn đang tìm kiếm một công việc mới, thì lá bài này là dấu hiệu nó đang đến rất gần. Điều này rất quan trọng, cho dù điều gì xảy ra, hãy giữ cho cái tôi mình luôn được kiểm soát và chia sẻ thành quả công việc cho những người làm cùng bạn cho dù bạn đã làm tất cả hoặc hầu hết công việc. Nói tóm lại, công việc của bạn đang vững vàng và đang hướng tới những cơ hội và tiến triển mới.","Nếu bạn đang trong một mối quan hệ nghiêm túc thì đây chính là lúc bạn và người yêu đang cần có không gian riêng hơn bao giờ hết. Đây không phải là lúc để nhõng nhẽo, dựa dẫm, hoặc quá cần nhau. Còn nếu bạn đang tìm kiếm tình yêu – chắc chắn nó đang ở rất gần.","Chuyện tiền bạc có thể thuận lợi hơn bình thường. Mặc dù vậy, hãy chuẩn bị cho những khoản chi phí bất ngờ. Nếu bạn đang chờ đợi một quyết định do người khác đưa ra, bạn sẽ không phải chờ đợi lâu hơn nữa, và quyết định đó rất có khả năng sẽ có lợi cho bạn.","Sức khỏe và cảm xúc về tinh thần khỏe mạnh của bạn đang ở đỉnh cao. Đây là lúc tuyệt vời để nghỉ ngơi và hưởng thụ cuộc sống nếu bạn có thể. Nghỉ ngơi ở đây mang nghĩa vừa là về tâm linh vừa là về thể chất – nếu bạn nhìn lại, bạn có thể sẽ thấy rằng thái độ của bạn đang rất tích cực dạo gần đây. Điều này có thể trực tiếp ảnh hưởng đến cơ thể của bạn. Hãy tìm hiểu xem bạn đã làm đúng việc gì và phát huy thêm!","Đây là lúc bạn có thể nhìn thấy ý nghĩa thật sự – xin nhấn mạnh là sự thật – về cuộc sống, ý nghĩa đó bao hàm rất nhiều vẻ đẹp, niềm vui và hạnh phúc trong cuộc đời, nếu bạn tìm kiếm đủ nhiều và tìm đúng chỗ. Hãy lan tỏa thông điệp rất quan trọng này. Đây là lúc tuyệt vời để thử nghiệm những cách thức tiếp cận tâm linh khác nhau để xem chúng có hiệu quả với bạn không."),
                new TarotCard("Judgement", R.drawable.judgement_20,20,"Phán xét", "tái sinh, thức tỉnh","Nói chung, nếu bạn đang có xu hướng đi thẳng đến kết luận, bạn nên dừng lại. Nếu những người khác cũng làm điều tương tự đối với bạn hoặc với những người bạn yêu quí, thì bạn cũng chẳng làm được gì nhiều, ngoại trừ nhận ra đó chính là vấn đề của họ, không phải của bạn.","Nỗ lực của bạn đang được người khác theo dõi. Đây là thời điểm quan trọng để bạn đầu tư nỗ lực vào những chi tiết của công việc. Rốt cuộc thì đây cũng có thể là điều tốt. Nếu bạn đã và đang làm việc chăm chỉ và hết sức mình, thì nỗ lực của bạn có khả năng sẽ được đền đáp. Nếu bạn đang làm việc một cách uể oải, đây là lúc bạn phải làm việc nghiêm túc, trước khi bạn phải nhận bất kỳ tổn thất vĩnh viễn nào khác đến sự nghiệp của mình.","Nếu bạn đang có một mối quan hệ, đây là khoảng thời gian thành-hoặc-bại. Đây là lúc để bạn làm thật rõ những gì bạn muốn từ cuộc sống hoặc từ mối quan hệ của bạn, và sau đó là tiến tới thực hiện nó. Nếu bạn đang tìm kiếm tình yêu, một mối quan hệ mới có khả năng sẽ đến trong tương lai gần, nhưng bạn cần phải thận trọng để đảm bảo rằng bạn đang không đưa ra kết luận quá vội vàng (cho dùng là tích cực hay tiêu cực) về tình yêu mới của ban và/hoặc ngược lại. Chậm mà chắc.","Tiền bạc và các hợp đồng đang tiến về phía bạn. Mọi việc sẽ tiến triển theo hướng rất quan trọng và tích cực. Đừng vội vã phá vỡ các nguyên tắc, nếu không thì bạn có thể trả giá đắt. Hãy để sự thận trọng bù đắp cái giá phải trả đó thay bạn.","Đây là lúc quan trọng để bạn bỏ lại phía sau quá khứ và những việc sai lầm mà người khác gây ra cho bạn, đặc biệt là khi bạn đang phải chống chọi với một căn bệnh mạn tính nào đó. Bỏ lại phía sau việc bạn muốn được cho rằng những gì bạn làm là đúng sẽ giúp bạn cảm thấy tốt hơn. Bên cạnh đó, nếu có ai đó làm gì sai với bạn, thì họ đã sai rồi, bạn không phải cứ giận dữ và chứng tỏ rằng họ sai. Hãy nghĩ tích cực một chút.","Bạn có thể thấy bản thân mình đang tìm kiếm những ý tưởng và định hướng tâm linh mà bạn chưa bao giờ mảy may nghĩ đến. Hơn nữa, bạn có thể tìm thấy nhiều sự thật và sự thông thái hơn ở đó mà bạn chưa bao giờ mơ đến. Hãy rộng mở với những ý tưởng và trải nghiệm mới. Chúng có thể có ích cho bạn theo những cách mà bạn không thể ngờ tới. Và bạn sẽ không bao giờ biết được cho đến khi bạn thực sự trải nghiệm."),
                new TarotCard("The World", R.drawable.world_21,21,"Thế giới", "hoàn thành, bản chất", "Lá bài này có thể xem là lá bài của sự hoàn thành. Bạn đã làm việc chăm chỉ, và bạn đang gần cán đích theo một nghĩa nào đó. Bạn sắp hoàn thành rồi, đừng bỏ cuộc! Bạn gần như chắc chắc cần có một kỳ nghỉ dưỡng xứng đáng; đừng ngại ngùng gì mà không đi. Bạn đang tiến gần đến với “con người thật sự” của mình, ở những cấp độ cao nhất.","Nói về công việc, khả năng cao là bạn đang làm việc quá chăm chỉ trong khi lại có quá ít sự công nhận cũng như tiền bạc nhận được. Điều này thật sự quan trọng nếu bạn đang trong tình huống phải tự mình “lên tiếng” đôi chút, để đảm bảo rằng người ta không “vô tình” bỏ qua những thành tựu tích cực của bạn. Bạn cũng cần thăm dò bên ngoài để tìm kiếm những thông tin bổ ích về cách người ngoài thực hiện công việc mà bạn đang làm như thế nào – việc này có thể dẫn bạn đến những trải nghiệm rất thú vị.","Nếu bạn đi du lịch trong thời gian này, và nếu bạn đang “tự chào bán” bản thân cho một mối quan hệ, thì bạn có thể gặp được ai đó trên chuyến hành trình. Ngược lại, nếu bạn đang trong một mối quan hệ nghiêm túc được một thời gian, có thể bạn sẽ nhận thấy rằng mối quan hệ này đã “bước lên một tầm cao mới”. Mọi câu chuyện liên quan đến tình cảm sẽ rất thú vị, tất nhiên là theo một hướng tốt. Đừng ngại ngùng khi hòa nhập vào đám đông hoặc nói chuyện với người lạ.","Nếu tiền bạc luôn là một rào cản đối với bạn, vậy thì đây là một dấu hiệu tốt cho thấy rằng cái “vật ngáng đường” này sẽ sớm biến mất và bạn sẽ lại thấy mình trở nên “nhiều tiền lắm của”. Tuy nhiên, đừng có điên cuồng mua sắm khi rào cản biến mất. Đây là khoảng thời gian quan trọng để bạn bắt đầu để dành và bảo vệ mình phòng những lúc khó khăn quay lại thì bạn đã chuẩn bị sẵn sàng rồi. Nhưng dù sao thì ngay tại thời điểm này, bạn có thể thoải mái hơn khi nghĩ về tiền rồi.","Sức khỏe của bạn đang cải thiện. Có thể bạn sẽ được giúp đỡ bởi một thầy thuốc không sử dụng thuốc tây thông thường, ví dụ như châm cứu hay tập yoga. Điều này không phải để thay thế liệu pháp dùng thuốc mà là để kết hợp với các phương thức điều trị truyền thống. Bác sĩ của bạn có thể không vui vì điều này, nếu giả sử ông/cô ta nói với bạn rằng bạn không thể tập yoga vì điều này có thể “gây cản trở” việc chữa trị chứng viêm khớp của bạn, thì trong trường hợp đó, tốt nhất là bạn nên đi tìm một bác sĩ khác. Hãy nghe theo “trực giác” của mình về những gì bạn cho là tốt nhất cho sức khỏe của mình. Hãy tìm kiếm những người thầy có nhiều kiến thức hơn bạn. Học hỏi hết sức mình. Hãy tự mình ủng hộ mình."," Có khả năng trong thời gian này bạn sẽ được khai sáng những hiểu biết tâm linh quan trọng. Hãy luôn giữ một quyển sổ trong tay và ghi chú lại những điều này. Chia sẻ chúng với những người khác (kể cả chia sẻ trực tuyến). Chúng ta đều đang đi trên cuộc hành trình tâm linh, và những hiểu biết của bạn có thể cũng sẽ có ích cho những người khác."),
                new TarotCard("Ace of Wands", R.drawable.ace_of_wands,22,"Ách gậy","sáng tạo, sáng chế","Lá bài này mô tả sự vui vẻ, lạc quan, và hi vọng về tương lai bạn – với căn nguyên tốt! Bạn sẽ cảm thấy tràn đầy năng lượng và cảm giác tích cực, hoặc sớm nhận ra cảm giác này. Lá Ace of Wands cũng cho biết rằng đây là thời điểm tốt để bắt đầu một số điều mới. Bạn sẽ bắt đầu với điều gì?","Nếu bạn đang tìm kiếm một vị trí mới khi lá bài Ace of Wands xuất hiện, vậy thì hãy biết rằng một điều gì đó mới mẻ và rất tích cực sẽ sớm tìm đến bạn. Hãy can đảmđưa ra yêu cầu về điều bạn muốn hoặc cần trong lĩnh vực công việc,sự nghiệp của bạn. Bạn sẽ có thể gặt hái được nhiều thành công hơn những gì bạn kỳ vọng.","Nếu bạn đang “lẻ bóng”, lá bài này có thể hàm ý một khởi đầu của một mối quan hệ yêu đương mới đang tìm đến bạn. Hãy chuẩn bị tinh thần cho chính bạn bằng mọi cách mà bạn cảm thấy ổn để sẵn sàng đón nhận tình yêu. Nếu bạn đã có một mối quan hệ tình cảmnhiều hứa hẹn, thì lá bài Ace of Wands cho bạn biết rằng mối quan hệ này sắp sửa trải qua một “sự khởi đầu mới” và hai bạn sẽ hiểu biết nhiều hơnlẫn nhau. Hãy bộc lộ bản thân mình.","Sự xuất hiện của lá bài này thường là dấu hiệu của một chuyển biến tốt hơn về mặt tài sản, và đôi khi nó có thể hàm ý những món quà về tiền bạc, hoặc sự thừa kế từ một nguồn mà bạn không hề chờ đợi (nhưng điềunày hiển nhiên không có nghĩa rằng một ai đó phải chết để bạn nhận được sự thừa kế này). Món quà này không hoàn toànxuất hiện dưới hình thức tiền bạc.Bất kỳ món quà nào có giá trị với cả người cho và người nhận thì đều có thể chính là “món quà”mà lá Ace of Wands muốn nói đến.","Bạn sẽ sớm đạt được những nét tích cực đầy tươi mới về sức khỏe. Đây là thời điểm tuyệt vời để bắt đầu một chế độ chăm sóc sức khỏe mới. Hãy bắt đầu tại chính nơi của bạn. Nếu lời khuyên nói đến việc tập thể dục trong hai phút, hoặc ăn rau củ trong một bữa ăn trong tuần, thì đó chính xác là điều bạn nên làm. Kết quả của những nỗ lực này sẽ rất đáng kể. Đừng trì hoãn những việc này.","Hãy nhớ rằng tâm trí và cơ thể của bạn có mối liên kết chặt chẽ hơn nhiều so với bạn nghĩ Cơ thể tác động đến tâm hồn và ngược lại. Lá bài này hàm ý một sự ảnh hưởng mới về khía cạnh tinh thần đang đến với cuộc sống bạn. Hãy suy nghĩ về các loại hình vai tròhỗ trợ cho tinh thần của bạn. Nếu bạn chưa có, hãy tìm và áp dụng một loại hình như vậy. Sự trợgiúp luôn sẵn sàng dành cho bạn, nếu bạn mở lòng ra đón nhận."),
                new TarotCard("Two of Wands", R.drawable.two_of_wands,23,"Hai gậy", "quyền lực, cân nhắc, can đảm", "Nhìn chung 2 of Wands nhắc chúng ta luôn giữ lý trí về việc cho và nhận cần thiết cho mọi mối quan hệ gặt hái được thành công. Nếu bạn có cảm giác như mọi thứ đang mất cân bằng đối với bạn trong lúc này, thì hãy yên tâm rằng chúng sẽ dần được cải thiện theo hướng tốt đẹp hơn. Hãy tập trung vào những mong muốn bạn đang tìm kiếm, và duy trì hay tìm kiếm sự tích cực.","Bạn sẽ kết hợp mọi thứ lại với nhau theo những phương cách mới lạ. Công việc sắp có vẻ trôi chảy hơn rất nhiều. Còn nếu bạn đang kiếm việc, thì lá bài này là thông điệp cho biết bạn sắp tìm được công việc phù hợp với mình rồi đấy. Đừng để bất kỳ ai phá hủy hay cản những bước tiến của bạn. Bạn sẽ có tổ chức hơn và cân bằng hơn những gì bạn có thể nhận thấy được về công việc.","Nếu bạn đã có một mối quan hệ gắn kết, thì mối quan hệ này sẽ dần bình đẳng hơn. Hãy tập trung vào những ý tưởng về tính bình đẳng và cân bằng và chia sẻ chúng với người yêu của bạn. Nếu bạn đang tìm kiếm tình yêu, vậy thì một người nào đó có thể rất tốt với bạn sẽ có khả năng đã là một phần trong cuộc đời bạn rồi. Đừng đánh giá người khác qua vẻ bề ngoài. Nếu ai đó quan tâm đến bạn, hãy cho họ một cơ hội. Mối quan hệ này có thể là điều bạn đang tìm kiếm.","Thu nhập và chi tiêu của bạn đang tiến tới một trạng thái cân bằng tốt hơn. Lá bài 2 of Wands hàm ý sự công bằng và bình đẳng, nên đừng ngần ngại yêu cầu điều bạn thực sự hưởng được hoặc đáng được hưởng. Bạn sẽ cảm thấy ngạc nhiên một cách thích thú với khả năng cải thiện tình hình tài chính của bản thân.","Chủ đề cân bằng đặc biệt đúng với lá bài này trong một câu hỏi đề cập đến sức khỏe. Năng lượng hoạt động nhằm cân bằng các chakra (luân xa) và / hoặc cơ thể / tâm trí / tinh thần của bạn sẽ đặc biệt hiệu quả và hữu ích cho sức khỏe bạn lúc này. Liệu bạn có đang tập trung đủ cho cả ba khía cạnh trên hay chưa? Nếu chưa, vậy thì đây chính là thời điểm để bạn cân bằng lại đấy. Các lợi ích sẽ lớn hơn bất kỳ mức độ nỗ lực nào mà bạn có thể đầu tư công sức. Sức khỏe bạn sẽ gần như chắc chắn tốt hơn nhiều so với những gì bạn đang lo sợ."," Một mối quan hệ hợp tác về tình thần với một người khác có thể rất có lợi cho bạn lúc này. Hãy nhớ rằng bạn cũng luôn luôn có mối quan hệ hợp tác về tinh thần với chính mình. Nhớ dành thời gian và không gian để quan tâm cả về tinh thần và cảm xúc của chính bạn. Đây là một yếu tố cần thiết để có một mối quan hệ tinh thần thành công với những người khác. Hãy bắt đầu trước hết với bản thân mình."),
                new TarotCard("Three of Wands", R.drawable.three_of_wands, 24, "Ba gậy", "phát triển, nỗ lực, thuận lợi", "Nhìn chung 3 of Wands là lá bài hàm chỉ về công việc, theo một hướng cực kỳ tích cực. Ngay cả khi bạn làm việc tại nhà đi nữa thì lá bài cũng vẫn có nghĩa rằng những nỗ lực của bạn đã được nhìn nhận và được đánh giá cao. Hãy đừng ngần ngại dành cho bản thân mình đôi chút tự hào.","Khi lá 3 of Wands xuất hiện trong bối cảnh câu hỏi về công việc, nó cho bạn biết rằng bạn có quyền tự hào vì được đánh giá cao về hiệu quả công việc, ngay cả khi bạn chưa hoàn toàn thấy được những phần thưởng như bạn mong đợi từ sự nhìn nhận này. Nếu bạn đang tìm kiếm công việc, bạn sẽ sớm tìm được một vị trí tốt hơn là bạn đang mong đợi. Hãy giữ đầu ngẩng cao và luôn biết rằng bạn có nhiều điều để thể hiện.","Các mối quan hệ tình cảm của bạn đều ổn, miễn là người yêu của bạn sẵn lòng đối xử với bạn một cách bình đẳng. Nếu không, đây có thể là thời điểm để “bước qua cuộc tình” / từ bỏ tình yêu sau khi bạn đã giải thích mọi chuyện rõ ràng. Nếu gần đây bạn gặp một người mới, thì lá bài này cho bạn biết rằng người ấy đánh giá bạn khá cao. Và nếu bạn đang tìm kiếm tình yêu nhưng cảm thấy chưa có bất kỳ triển vọng nào, thì thực tế bạn có lẽ đang tiêu tốn quá nhiều thời gian và nỗ lực cho sự nghiệp, công việc của mình. Hãy suy nghĩ về việc cho phép bản thân thêm thời giạn để tận hưởng cuộc sống cá nhân","Mọi thứ đều tăng trường xét về mặt tài chính. Công việc nặng nhọc của bạn đang trong qua trình thu hoạch thành quả cho dù bạn chưa thấy được điều này. Tuy nhiên, đừng quá hào phóng với bản thân về mặt tài chính. Hãy tiết kiệm đôi chút phòng khi khó khăn, và hãy biết san sẻ của cải của mình. Bạn có thể làm tốt hơn về mặt tài chính hơn những gì bạn đã từng mơ ước.","Các tin tức về sức khỏe có khuynh hướng sẽ rất tốt khi lá bài này xuất hiện. Các nỗ lực về sức khỏe có vẻ đang bắt đầu đơm hoa kết trái. Đừng ngần ngại xem xét những phương pháp khác để điều trị hoặc giải quyết các thách thức hiện tại về sức khỏe bản thân, tất nhiên là theo lý trí.","Bạn ít nhất đã trường thành ở mức độ nào đó, và có tầm nhìn hiểu biết sâu sắc về tinh thần. Ngay cả khi đã được như vậy, bạn vẫn cần nhận biết rằng nếu bạn biết tập trung hoàn toàn, thì bạn sẽ lớn dần thêm xét về mặt tinh thần, và do đó, quan điểm tinh thần của bạn có thể thay đổi và phát triển theo thời gian. Đừng e ngại sự thay đổi và những thông tin mới."),
                new TarotCard("Four of Wands", R.drawable.four_of_wands,25,"Bốn gậy", "nơi trú ẩn, nơi nương tựa", "Mặc dù đây là một lá bài rất tích cực trong một giải bài, nhưng đồng thời cũng là lời kêu gọi phải giữ cho đầu óc tỉnh táo và nhạy bén. Dù bạn có xứng đáng và nên dành cho bản thân một chút niềm vui, thì vẫn hãy biết rằng vạn vẫn đang trong quá trình phát triển và hiện tại không phải lúc để nghỉ ngơi hoàn toàn để tận hưởng thành quả. Đôi khi lá bài này cũng có thể hàm ý rằng bạn sẽ dời đến một nơi ở mới.","Mọi khía cạnh nhiều khả năng sẽ rất tốt đối với bạn xét về công việc. Tuy vậy, dù là bất kỳ việc gì bạn làm, cũng đừng nên ăn mừng thành công ban đầu vội hay rơi vào cảm giác giống như mọi chuyện đã và sẽ diễn ra thật dễ dàng. Tất nhiên bạn đã gặt được thành công và sự công nhận nhưng đừng quên rằng bạn sẽ phải tiếp tục cố gắng để giữ vị trí và tiếp tục phát triển mở rộng bản thân mình.","Xét về tình yêu, lá bài này có thể hàm ý rằng mối quan hệ của bạn sắp đến được giai đoạn cam kết trọn vẹn, và có thể là một al1 bài tiềm năng cho một sự kiện cưới hỏi. Nếu bạn đang tìm kiếm tình yêu, lá bài cho thấy rằng những sự kiện đặc biệt (như đám cưới, tiệc tùng, lễ kỷ niệm) là những dịp và địa điểm rất tốt để bạn gặp gỡ một ai đó đặc biệt. Nếu bạn được mời đến một sự kiện giống như vậy, và bản thân đang chưa có người yêu, vậy thì hãy cố gắng tham gia và ghi điểm nhé.","Vấn đề tài chính của bạn có vẻ đang tiến triển tốt. Tuy nhiên, đây thực chất là kết quả của quá trình làm việc chăm chỉ của bạn từ trước đến nay, chứ không đơn thuần và may mắn như trúng vé số. Hãy luôn tin tưởng vào bản thân và giữ nghị lực. Bạn sẽ nhận được phần thưởng xứng đáng.","Nếu bạn đang chờ đợi kết quả xét nghiệm, bạn có thể sớm vui mừng vì những thông tin trong đó sẽ rất tích cực. Nếu có điều gì đó về sức khỏe đang làm phiền lòng bạn, và bạn chưa thể tìm ra giải pháp, thì hãy biết rằng luôn có sự giúp đỡ dành cho bạn. Hãy kiên trì thử tìm kiếm chúng!","Nếu bạn bè và gia đình của bạn không chấp nhận vấn đề tinh thần của bạn, thì cũng đừng để điều đó làm phiền lòng bạn. Bạn có lý do để nhìn nhận mọi việc như vậy. Hãy mở lòng để xem xét những lời khuyên của người khác, nhưng hãy biết rằng cuối cùng bạn phải (và có quyền để) quyết định suy nghĩ của chính mình."),
                new TarotCard("Five of Wands", R.drawable.five_of_wands,26,"Năm gậy", "xung đột, cạnh tranh", "Khi lá bài này xuất hiện, bạn cần tin vào bản thân mình mạnh mẽ hơn bạn đã từng tin. Hãy giữ đầu ngẩng cao và có niềm tin rằng bạn sẽ giành được những thứ thuộc về bạn. Bạn có thể đang suy nghĩ cân nhắc về một thay đổi trong sự nghiệp. Hãy biết bằng bạn hoàn toàn có thể thành công.","Hãy nhận thức rằng bạn đang đối mặt với việc cạnh tranh, nhưng nếu bạn gặp may mắn, bạn có thể sẽ thành công. Cách duy nhất để bạn chắc chắn gặp thất bại là không dám thử. Hãy cạnh tranh một cách công bằng cho dù mọi người xung quanh bạn không làm vậy. Người chơi xấu dường như sẽ thắng thế trong ngắn hạn, nhưng như Gandhi đã nói, loại chiến thắng ấy sẽ không bao giờ tồn tại mãi mãi.","Người bạn đang để mắt đến có thể đang bị theo đuổi bởi những kẻ khác. Ngay cả như vậy thì bạn cũng hãy cố gắng hết sức mình. Bạn cần biết rằng bạn đã làm tất cả những gì có thể cho mối quan hệ này. Lá bài này cũng có ý nghĩa khác rằng bạn sẽ được theo đuổi bởi một hay vài người lãng mạn khác đấy.","Dù có thể bạn đang có cảm giác rằng tình hình tiền bạc đang eo hẹp, nhưng hãy thử phân tích tình cảnh tài chính của bạn một cách điềm tĩnh. Bạn có lẽ không thể đáp ứng được mọi nghĩa vụ của mình ngay lúc này, nhưng điều đó không có nghĩa rằng bạn sẽ không bao giờ có thể đáp ứng được chúng. Hãy làm những gì bạn có thể, và luôn thành thật với hoàn cảnh. Mọi thứ sẽ sớm tốt đẹp hơn về mặt tài chính, nhiều khả năng chỉ trong một vài tuần nữa thôi.","Bạn có thể cảm thấy nản chí trong việc tìm kiếm những câu trả lời về sức khỏe của mình. Nếu bạn đang bị kẹt trong một vấn đề nào đó về sức khỏe, thì cần nhớ rằng việc quá vội vàng có thể không phải là phản ứng tốt nhất vào lúc này. Đôi khi giải pháp tốt nhất là lách qua những khó khăn. Ít nhất hãy cho bản thân mình nghỉ ngơi trong vòng vài giờ. Bạn vẫn còn những giờ khác để sử dụng cho các mối lo nghĩ. Thực tế nhiều khả năng là bạn đang làm việc quá sức, và điều đó sẽ ảnh hưởng xấu đến sức khỏe của bạn đấy","Sẽ không dễ phát triển tinh thần nếu bạn đang thiếu định hướng. Hãy dành thời gian để nghỉ ngơi, đừng làm gì hết vào lúc này. Hãy tạo ra khoảng không gian cho bản thân mình tĩnh tâm và lắng nghe âm thanh từ môi trường xung quanh. Cuộc sống không phải lúc nào cũng cần đến bon chen, tranh đấu. Mọi người đôi lúc cần không gian riêng để được là chính mình. Hãy trao cho bản thân mình món quà mang tên thời gian và không gian, và từ đó bạn sẽ thấy và cảm nhận được sự phát triển về tinh thần."),
                new TarotCard("Six of Wands", R.drawable.six_of_wands,27,"Sáu gậy", "thắng lợi, nỗ lực, niềm tin","Nhìn chung, lá 6 of Wands là lá bài thiên về niềm hạnh phúc và sự tán dương. Bạn sắp tới sẽ nhận thấy một số kết quả tích cực cho những nỗ lực của mình. Hãy cho bản thân mình nghỉ ngơi hoặc đối xử tốt với bản thân mình theo một cách nào đó. Bạn chắc chắn sẽ xứng đáng được như vậy.","Nếu bạn đang tìm kiếm một vị trí công việc mới, thì lá bài này là một dấu hiệu rất tích cực. Bạn sẽ tìm thấy một điều gì đó tốt hơn so với điều bạn đang hi vọng. Nếu bạn hiện đang làm việc tại một ví trí đáng mong muốn thì mọi chuyện sắp tới sẽ còn tốt hơn những gì bạn đã từng mong đợi. Những nỗ lực của bạn đang được đánh giá cao. Hãy tiếp tục phát huy những thành quả trong công việc bạn đang làm.","Trong một mối quan hệ đã gắn kết, lá bài này hàm ý rằng mọi thứ đang tiến triển tốt. Bất chợt bạn có thể sẽ nhận ra ở chính mình cảm giác gần gũi hơn và hạnh phúc hơn với đối phương – người yêu của mình so với trước đây. Nếu bạn không được hấp dẫn hay đủ tự tin, thì sự xuất hiện của lá bài này có thể hàm ý rằng bạn sắp bước vào một mối quan hệ với một ai đó mà bạn đang mong đợi. Đây là một dấu hiệu rất tích cực.","Mọi thứ sẽ tốt lên rất nhiều về mặt tài chính. Bất kế điều gì đang xảy ra xung quanh bạn, mọi thứ sẽ luôn diễn biến rất tốt với bạn. Tuy nhiên, hãy thận trọng trong cách hành động liên quan đến bất kỳ món lợi tài chính nào. Đặc biệt trong những ngày này, điều quan trọng là bạn nên chuẩn bị trước cho mình các khoản dự phòng để phòng những thời điểm khó khăn. Tuy vậy, bạn có thể kỳ vọng tình hình tài chính của bản thân sẽ còn tiếp tục tăng trưởng.","Nếu bạn đang lo lắng về sức khỏe, thì hãy thư giãn đi. Mọi thứ sẽ không xấu hay tệ hại đối với bạn hoặc người mà bạn nhờ giải bài sức khỏe giúp. Chỉ cần cố gắng duy trì được sức mạnh tinh thần tiềm ẩn và điều đó sẽ giúp duy trì sức khỏe tốt cho người mà bạn đang hỏi. Hãy vững tin","Về tinh thần, lá bài 6 of Wands có thể hàm ý về việc tận hưởng niềm vui từ sâu thẳm tâm hồn và tình yêu cuộc sống. Bạn đã đi đúng hướng rồi. Hãy tận hưởng niềm vui đang lan tỏa sâu bên trong mình, và chia sẻ cho những người khác. Thế giới cần những món quà mà chỉ có bạn mới có thể trao được."),
                new TarotCard("Seven of Wands", R.drawable.seven_of_wands,28,"Bảy gậy", "bối rối, lo lắng, mẫu thuẫn","Đừng ngần ngại làm sáng tỏ vị trí của bạn với mọi người. Khi có được suy nghĩ rõ ràng, bạn sẽ có thể hỗ trợ những người khác bằng cách đi vào chi tiết vấn đề của họ. Lá bài này có thể hàm ý rằng một thay đổi đang đến – và đó là một thay đổi tích cực – trong cuộc sống cá nhân hoặc công việc của bạn. Lá bài này cũng chỉ tình trạng không bị phụ thuộc và suy nghĩ cho bản thân.","Nếu bạn đã từng muốn tự thân vận động, tự mình thuê mình, thì đây là lúc để suy nghĩ kỹ về điều này. Bạn có đủ điều khiện để tạo dựng sự nghiệp cho riêng mình, nếu bạn quyết tâm dành thời gian và nỗ lực thì điều đó sẽ xảy ra thôi. Tuy nhiên, hãy lý trí! Nếu bạn có thể bắt đầu công việc kinh doanh của mình trong khi vẫn có thể thu xếp đi làm ở một nơi khác, vậy thì hãy làm điều đó một cách hợp lý.","Nếu bạn đang trong một mối quan hệ gắn bó và hai bên đã cam kết tương lai, đừng ngần ngại nói ra suy nghĩ của mình với người yêu. Điều này sẽ cải thiện được mối quan hệ của bạn miễn là bạn thận trọng trong cách thể hiện suy nghĩ của mình. Nếu bạn đang tìm kiếm nửa còn lại, thì lá bài này có thể hàm ý rằng bạn sắp gặp một ai đó mới ở cách xa nơi bạn hay thường lui tới. Đôi khi hãy thử một điều gì đó mới mẻ.","Khi lá bài này xuất hiện trong trải bài về tài chính, bạn có thể kỳ vọng về một sự tăng trưởng. Ví dụ bạn sẽ nhận được khoản tiền thưởng mà bạn không ngờ tới, hoặc khám phá ra rằng một số thứ bạn đang sở hữu có giá trị hơn những gì bạn nghĩ. Về ngắn hạn tình hình tiền bạc của bạn sẽ có sự gia tăng. Hãy suy xét về cách bạn sử dụng và đầu tư số tiền này. Hãy suy nghĩ về dài hạn","Bạn có thể đang lo lắng một cách không cần thiết về sức khỏe của mình. Thậm chí khi đối mặt với các thử thách sức khỏe nghiêm trọng, thì việc giữ vững một thái độ tích cực vẫn có thể tạo nên những điều kỳ diệu. Nếu bạn nghi ngờ rằng thể chất của mình đang có vấn đề, vậy thì hãy nhờ một chuyên gia sức khỏe kiểm tra giúp bạn. Nếu vị bác sĩ quen thuộc không kiểm tra hay chẩn đoán ra vấn đề bạn đang nghi ngờ, hãy xem xét liệu có chuyên gia nào khác có thể hỗ trợ bạn không. Hãy tin vào bản năng của mình","Bằng cách đối đầu với nỗi sợ hãi và xoay chuyển chúng thành điều thuận lợi, bạn sẽ trở nên mạnh mẽ hơn và sẵn sàng đương đầu với những trở ngại tiếp theo trong hành trình của mình. Thật vậy, không có sự dũng cảm nào lại thiếu đi nỗi sợ, yếu tố truyền cảm hứng cho lòng can đảm. Nỗi sợ đó không còn không chế bạn nữa. Nếu một cơ hội đang kêu gọi bạn, hãy gạt lo lắng qua một bên và tự tin nắm bắt lấy nó. Nếu bạn cảm thấy đây là thời điểm tốt để tiến đến giấc mơ của mình, hãy quên đi sự ngần ngại và không để bất kỳ điều gì cản trở bạn. Trong ngắn hạn, cho dù các mục tiêu và kỳ vọng của bạn có là gì đi nữa thì cũng hãy hành động để đạt được chúng."),
                new TarotCard("Eight of Wands", R.drawable.eight_of_wands,29,"Tám gậy", "nhanh chóng, táo bạo, tự tin", "Khi lá bài này xuất hiện, dường như không có bất cứ điều gì hiện tại cho thấy sự tiến triển. Nhưng đừng cố gắng thúc ép mọi chuyện, vì như vậy chỉ phản tác dụng lại bạn. Nói một cách ẩn ý, đôi khi những hành động của chúng ta đều phụ thuộc vào chính chúng ta, và tất cả những gì bạn có thể làm là nỗ lực hết sức và chờ đợi. Nếu bạn đã làm hết những gì cần làm, vậy thì hãy kiên nhẫn hơn và chờ đợi.","Bạn có lẽ đang chờ đợi những quyết định được đưa ra bởi người khác. Khả năng rất lớn là kết quả sẽ rất tích cực, tuy nhiên cũng đừng quá thúc ép mọi người trước khi họ sẵn sàng. Hãy kiên nhẫn. Tình trạng đình trệ sẽ sớm chấm dứt, nhưng sớm hay muộn không phụ thuộc vào bạn.","Trong vấn đề tình cảm, khi lá bài 8 of Wands xuất hiện, nhiều khả năng là bạn đang cảm thấy bản thân mình đã sẵn sàng cho một bước tiến triển mới trong quan hệ tình cảm hiện tại (như hôn nhân), trong khi thực tế bạn dường như vẫn chưa sẵn sàng đâu. Đừng thúc ép người khác trước khi họ sẵn sàng. Bạn không thể có được những gì mình cần bằng cách ép buộc. Hãy biết phân tán bản thân nếu cần thiết, và người bạn yêu có thể sẽ tìm đến bạn khi bạn chưa kịp biết, trao đến bạn chiếc chìa khóa bước vào trái tim của người ấy mà bạn đang hy vọng, mà không tốn nhiều công sức.","Có nhiều thứ liên quan đến tình hình tài chính của bạn hiện không mấy tiến triển. Đây không phải là thời điểm tốt để đầu tư có tính toán, trừ khi bạn đã nhìn nhận và xây dựng kế hoạch theo hướng rất lâu dài. Mọi thứ đang trong dòng chảy bất biến. Hãy tiết kiệm tiền bạc, xem xét thói quen chi tiêu của bạn. Tính kiên nhẫn là điều cần thiết ở bạn lúc này.","Có thể bạn đang cảm thấy lo lắng nếu đang trong giai đoạn chờ đợi kết quả khám bệnh hoặc thông tin về một vấn đề sức khỏe mà bạn đang quan tâm. Hãy dành nhiều thời gian để suy nghĩ về những điều tích cực, và hãy biết rằng sẽ cần thêm thời gian để bạn có thể hiểu rõ nhất tình trạng sức khỏe của mình. Hãy tự động viên bản thân, đặt câu hỏi, nói với chính mình về những điều tốt đẹp nhiều nhất có thể. Bác sỹ của bạn vẫn còn nhiều bệnh nhân khác phải chăm sóc. Còn bạn chỉ có chính bản thân mình. Hãy tích cực lên, mọi chuyện rồi sẽ ổn thôi!","Đối với hầu hết chúng ta, tính kiên nhẫn có thể là bài học tinh thần khó đạt được. Tuy nhiên, khi lá bài này xuất hiện, đây là dấu hiệu rõ ràng rằng tĩnh tâm và kiên nhẫn là hai vấn đề bạn cần lấy làm nền tảng. Việc thiền định sẽ có ích với bạn lúc này. Nhưng nếu bạn không thể ngồi yên và thiền trong hai mươi phút, không sao cả, hãy bắt đầu với ba mươi giây. Sự yên bình sẽ thường làm tâm được tĩnh. Hãy tìm kiếm cảm giác đó."),
                new TarotCard("Nine of Wands", R.drawable.nine_of_wands,30,"Chín gậy", "nội lực, sức mạnh từ áp lực, nghịch cảnh thiên tai, ngoan cố, khoe mẽ","Khi lá bài 9 of Wands xuất hiện, điều đầu tiên và tốt nhất mà bạn nên làm là hít thở thật sâu và bình tĩnh. Đừng để những thứ bạn đang lo lắng bao trùm lấy tinh thần bạn. Hãy chỉ dành mỗi ngày một lần, hay mỗi giờ một lần suy nghĩ về mối lo đó nếu bạn bắt buộc phải thế. Ít nhất một trong những điều lo lắng của bạn thực chất chỉ là do trí tưởng tượng của bạn tạo ra. Hãy cụ thể rõ ràng điều đang làm bạn lo sợ và dẹp bỏ nó sang một bên. Khả năng thật sự của vấn đề mà bạn đang cảm thấy lo ngại là gì nếu nó thực sự xảy ra? Hãy thoải mái bản thân, và nếu bạn cần giúp đỡ, đừng ngại đưa ra yêu cầu đó. Việc yêu cầu giúp đỡ là dấu hiệu của một con người mạnh mẽ.","Có thể có đôi chút căng thẳng trong công việc của bạn khi lá bài này xuất hiện. Đừng khiến cho tình hình căng thẳng trở nên nghiêm trọng thêm; hãy suy nghĩ thật kỹ trước khi nói, giữ sự điềm tĩnh cao nhất có thể ở bề ngoài. Hãy làm rõ những điều mà bạn có thể và không thể kiếm soát được và sau đó cố gắng làm cho đầu óc thoải mái một chút. Hãy giải quyết hết sức mình những vấn đề mà bạn có thể kiểm soát được, và rồi mọi chuyện sẽ lại ổn thôi. Nếu bạn đang tự hỏi liệu mình có thể bị mất việc hay không, thì khi lá bài này xuất hiện, câu trả lời có thể là có. Đó là một lời cảnh báo trước để bạn chuẩn bị tinh thần đề phòng. Một khi điều đó xảy ra, bạn sẽ làm gì?","Trong vấn đề tình cảm, một điều rất quan trọng cần nhớ là không có bất kỳ mối quan hệ nào trên thế giới có thể “yên ổn” một cách bất biến. Hãy thử cho người yêu của bạn nhiều không gian hơn. Còn nếu bạn chưa có người yêu, và đang khao khát yêu đương và được yêu, thì hãy hiểu rằng thời điểm này chưa phù hợp cho bạn. Liệu có điều gì đó về bản thân vạn mà bạn muốn thay đổi không? Cần biết đây là thời điểm tốt để kiểm soát lại toàn bộ xúc cảm của bản thân bạn, để khi bạn sẵn sàng cho một mối quan hệ mới, người mà bạn đang thu hút vào cuộc sống của bạn cũng có trọn vẹn cảm xúc hạnh phúc trước tình yêu mới – là bạn. Đừng quá nóng vội!","Về vấn đề tài chính, việc lo lắng sẽ không giải quyết được vấn đề. Hãy thử  bình tĩnh và giải quyết hoàn cảnh và thực hiện những hướng hành động tiềm năng một cách có tính toán. Hãy đi từng bước nhỏ một. Hãy xem xét những cách thức mà bạn có thể áp dụng để tăng được thu nhập của mình, kể cả khi chỉ tăng đôi chút.","Lo lắng, stress, và băn khoăn có thể gây ảnh hưởng đáng kể đến cuộc sống của bạn ở thời điểm hiện tại. Hãy đảm bảo rằng bạn đang đối xử với bản thân mình theo cách tốt nhất có thể. Hãy cân bằng giữa việc tập luyện thể thao, ăn uống và nghỉ ngơi phù hợp. Các bài tập hít thở và thiền sẽ đặc biệt hữu ích với bạn lúc này. Nếu bạn đang chờ đợi các kết quả khám bệnh, đừng lo lắng quá nhiều, và nhớ rằng bạn vẫn còn rất nhiều thứ khác phải lo. Hãy tập trung vào những điều tích cực. Nếu cần hãy viết ra những điều bạn cảm thấy mình may mắn có được. Hãy khởi tạo những mối quan hệ với những người tích cực xung quanh. Bạn sẽ sớm có được câu trả lời mà bạn tìm kiếm.","Hiện tại, bạn có thể chưa thực sự sẵn sàng để khắt khe và đề ra kỷ luật cho bản thân, tuy vậy, đây là thời điểm mà việc giữ cững kỷ luật sẽ giúp bạn đạt được những mục tiêu tinh thần của mình. Hãy biết tập trung, sống có kế hoạch, và đặt ra các yêu cầu cho bản thân ở mức độ cao hơn về các khía cạnh nghiên cứu và cam kết thuộc về tâm linh. Bạn đang có sức mạnh tiềm ẩn bên trong để đạt được bất kỳ điều gì bạn muốn."),
                new TarotCard("Ten of Wands", R.drawable.ten_of_wands,31,"Mười gậy", "áp lực, trách nhiệm, ích kỷ, dối trá, lạc hướng", "Đôi khi lá 10 of Wands hàm ý rằng bạn đang thành công quá mức và sẽ tốt hơn nếu bạn thu hẹp lại phạm vi những gì đang làm. Bạn có lẽ đang mệt mỏi do làm việc quá nhiều, cả về trong cuộc sống cá nhân lẫn công việc, và bạn cần dừng lại và nghỉ ngơi. Hãy ưu tiên cho bản thân mình.","Hiện tại khối lượng công việc của bạn có thể đang ở mức quá nặng đến mức phi thực tế. Đừng ngần ngại yêu cầu sự giúp đỡ hoặc đặt câu hỏi, và nếu người giám sát bạn hoặc đồng nghiệp của bạn đưa ra một yêu cầu mới vượt mức giới hạn của bạn, hãy hỏi họ điều gì bạn có thể ngưng lại hay không làm để dành thời gian cho yêu cầu mới này? Nếu bạn đang tìm kiếm một vị trí công việc mới mà chưa thành công, thì đừng vội nản chí. Hãy tiếp tục cố gắng, và bạn sẽ không thất bại.","Sự xuất hiện của lá 10 of Wands thường biểu hiện một thời điểm khó khăn trong một mối quan hệ. Khó khăn này có thể xuất phát từ việc người yêu của bạn đang trải qua một giai đoạn xấu vốn không ảnh hưởng gì đến mối quan hệ của bạn nhưng lại rất cần sự hỗ trợ của bạn. Tuy nhiên, thường thì khó khăn này cũng hàm ý rằng có một điều gì đó trong mối quan hệ của hai bạn hiện đang cần được xem xét, nhìn nhận, và cân nhắc lại. Sẽ không ai trong hai bạn có lợi nếu giả vờ như không có rắc rối gì đang xảy ra. Nếu bạn vẫn còn đang loay hoay tìm kiếm tình yêu, đừng nản chí. Thời điểm này cuối cùng rồi cũng sẽ đến, nhưng có lẽ đây là thời điểm tốt để bạn dừng cuộc tìm kiếm của mình lại và đơn giản là cho phép bản thân mình thư giãn, và đợi tín hiệu phản hồi lại.","Có lẽ bạn đang cảm thấy quá áp lực và căng thẳng về vấn đề tiền bạc khi lá bài này xuất hiện trong ngữ cảnh câu hỏi về tài chính. Tốt nhất là bạn nên xem xét hoàn cảnh của mình một cách điềm tĩnh và bình thản, và lập ra một kế hoạch. Một người khôn ngoan sẽ luôn “mong đợi điều tốt nhất, nhưng có kế hoạch cho điều tồi tệ nhất”. Bạn nên làm cả hai điều này ngay. Việc né tránh các vấn đề sẽ không khiến chúng biến mất. Hãy là một người sáng tạo trong cách giải quyết vấn đề của bản thân, và đừng ngần ngại yêu cầu sự giúp đỡ nếu bạn cần.","Sự căng thẳng có thể ảnh hưởng nghiêm trọng đến cơ thể của bạn, nhưng có nhiều phương án để bạn có thể đối phó với stress. Trước tiên, hãy giữ một tâm thế bình tĩnh. Sau đó, hãy đảm bảo rằng bạn được nghỉ ngơi và ngủ đủ giấc. Tiếp theo hãy xem xét bổ sung thêm dinh dưỡng nếu bạn thấy cần thiết, vì căng thẳng một phần là do cơ thể bạn thiếu vitamin. Nếu tình trạng mệt mỏi và mất ngủ và các vấn đề thể chất cứ tiếp tục dai dẳng, hay tìm cách chữa trị – ngay cả khi bạn không có nhiều tiền và không có bảo hiểm y tế, bạn vẫn có đó rất nhiều sự giúp đỡ sẵn sàng dành cho bạn. Hãy biết tìm kiếm.","Đừng e ngại kêu gọi sự hỗ trợ nếu bạn thấy cần. Tuy nhiên, bạn cũng nên học cách dừng lại khi cần, và biết khi nào cần giải thoát bản thân khỏi các gánh nặng của mình. Hãy chăm sóc tốt cho bản thân bằng mọi cách là nhiệm vụ đặc biệt quan trọng vào lúc này."),
                new TarotCard("Page of Wands", R.drawable.page_of_wands,32, "Tiểu đồng gậy", "trẻ tuổi, cá nhân, nhiệt tình, táo bạo, nông cạn, do dự", "Nhìn chung, lá bài này thể hiện rằng những gì bạn đã bắt đầu làm từ trước hiện đang tiến dần đến thành công. Lá bài cũng có thể chỉ một nhu cầu về một điều gì đó mới mẻ, ví dụ như một cuộc phiêu lưu mạo hiểm, một giải pháp mới, hay một ý tưởng mới. Page of Wands mang lại cho chúng ta năng lượng tích cực. Nhưng chúng ta cũng cần theo đuổi đến cùng và quyết tâm làm những điều đó.","Về công việc, lá bài này có thể thể hiện một người phụ nữ nào đó sẵn sàng giúp đỡ bạn. Người phụ nữ ấy cũng có thể là một cô bé nhỏ tuổi hơn bạn – nhỏ hơn ở đây có thể chỉ bằng vài ngày hoặc vài tuần. Nếu bạn đang chờ đợi một quyết định về một công việc mà bạn đã nộp đơn ứng tuyển, thì việc lá bài này xuất hiện gần như là một tin tốt. Công việc cũng có thể sẽ tốt hơn và thú vị hơn bạn mong đợi.","Những thông điệp yêu đương và cảm giác được yêu có thể đang đến với bạn. Nếu bạn đang một mình, một người bạn theo đuổi vốn rất ưa mạo hiểm có thể xuất hiện trong thế giới của bạn. Hãy nắm lấy cơ hội đó, nhưng chỉ khi bạn đã sẵn sàng. Nếu bạn đã có một mối quan hệ tình cảm gắn bó nhiều hứa hẹn mà không cảm thấy hạnh phúc, thì đây là thời điểm để bắt đầu quyết định xem liệu mối quan hệ đó có thể được cứu vãn hay không, và bạn sẽ cố gắng hàn gắn trong bao lâu. Cả hai bên cần phải sẵn sàng hợp tác cùng trong trong một mối quan hệ. Nếu người yêu bạn không làm vậy, thì đây có thể là thời điểm để từ bỏ cuộc tình này.","Tình hình tiền bạc của bạn có khuynh hướng tốt dần lên khi lá bài này xuất hiện trong một câu hỏi về tài chính. Lợi nhuận kèm theo tiền lãi từ những gì bạn đã đầu tư vào các phi vụ (theo dạng năng lượng tốt) nhiều khả năng sắp tìm đến với bạn. Đây là thời điểm rất tốt để bắt đầu lên kế hoạch lâu dài, ngay cả khi bạn mới chỉ đôi mươi. Bạn muốn đạt được gì về mặt tài chính năm 30 tuổi? Bạn sẽ khiến mục tiêu đó xảy ra như thế nào?","Về khía cạnh sức khỏe, Page of Wands hàm ý một nhu cầu cần hành động. Bạn sẽ phải nỗ lực đôi chút để cải thiện tình trạng sức khỏe của mình. Hãy xem xét xem liệu bạn có cần luyện tập thể dục nhiều hơn không? Liệu bạn có cần tìm hiểu về các kiến thức dinh dưỡng để đảm bảo mình ăn uống tốt hơn không? Hãy nhớ bạn luôn có sẵn các nguồn tham khảo bổ ích. Không điều gì có thể thay đổi nếu bạn chỉ ngồi một chỗ và mong ước mọi thứ thay đổi theo hướng tốt hơn. Hãy kiểm soát cuộc sống của mình. Ban đầu sẽ không dễ dàng lắm, nhưng rồi bạn sẽ có thể làm được thôi.","Đây có thể là thời điểm của sự sáng tạo dành cho bạn, và là một thời điểm tốt để bạn trở về tuổi thơ của mình – ngày mà những mơ ước, chơi đùa, sáng tạo chiếm trọn thời gian của bạn. Bạn sẽ không bao giờ biết được sự sáng tạo của mình ở thời điểm này sẽ dẫn đến đâu, nhưng dù sao đi nữa, bạn cũng sẽ tận hưởng được niềm vui, đồng thời cuộc chơi này sẽ làm phong phú thêm cho cuộc sống của bạn."),
                new TarotCard("Knight of Wands", R.drawable.knight_of_wands, 33, "Kỵ sĩ gậy", "khởi hành, vắng mặt, chuyến đi, nhập cư, tàn bạo", "Nhìn chung Knight of Cups sẽ cho bạn biết rằng mọi thứ sẽ trở nên tốt hơn. Bạn có thể cảm thấy tràn đầy năng lượng, sẵn sàng để hành động. Lá bài này thường có nghĩa rằng những tin tốt (đặc biệt trong thế giới nội tâm bạn) dường như sắp đến với bạn đấy.","Các dự án công việc gần như sẽ diễn ra theo hướng tích cực và sớm hơn bạn mong đợi khi lá bài này xuất hiện trong một lượt trải bài. Bạn nên tự hào về những thành quả và đóng góp của mình.","Đây là một lá bài cực kỳ tích cực khi xuất hiện trong lượt hỏi về tình yêu. Nếu bạn đang còn độc thân hay chưa có mối quan hệ tình cảm với ai, thì lá bài này thường hàm ý rằng một ai đó sắp gây được ấn tượng với bạn đấy. Còn nếu bạn đang trong một mối quan hệ, lá Knight này có thể hàm ý đến một lời cầu hôn / cuộc hôn nhân. Ít nhất là trong bối cảnh câu hỏi vềtình yêu, lá bài này sẽ luôn mang những thông điệp tích cực, những điều mà bạn sẽ thích nghe. Hãy mở lòng khám phá những giảm giác của mình.","Lá bài này có thể chỉ rằng bạn sẽ sớm nhận được tin tức tốt lành về vấn đề tài chính của bạn. Knight thường hàm ý những thông điệp đầy niềm vui và tích cực. Nếu bạn thắc mắc về vấn đề tài chính và rút được lá này trong trải bài, và nếu không có lá nào khác trong cùng lượt trải mang thông điệp gì khác cụ thể cho vấn đề tài chính của bạn, vậy thìtrường hợp này hầu như chắc chắn không xấu như bạn nghĩ.","Nếu bạn đang chờ đợi kết quả xét nghiệm về sức khỏe và rút được lá bài này, hãy thư giãn và yên tâm rằng tin tức sắp tới sẽ rất khả quan. Nhìn chung về khía cạnh sức khỏe của bạn, lá Knight này có nghĩa rằng bạn sẽ sớm cảm thấy tốt hơn / thậm chí là khỏe mạnh hơn.","Bạn đã sẵn sàng để bước vào những lĩnh vực mới trên hành trình khám phá tâm linh của mình. Knight of Cups thường hàm ý rằng bạn sẽ sớm nhận được một tin tức tốt từ tinh thần (dù điều này đôi khi đến thông qua người khác) và điều này sẽ mở ra những hướng nhìn nhận mới dành cho bạn. Hãy chú ý những điềutrông có vẻ như xảy ra ngẫu nhiên. đó thường là cách mà tinh thần “mặc khải” với chúng ta."),
                new TarotCard("Queen of Wands", R.drawable.queen_of_wands, 34, "Hậu gậy", "thích nghi, nghị lực, bển bỉ, tham vọng, điềm tĩnh, nổi giận không lý do, lừa dối", "Queen of Wands cũng có thể chỉ một người phụ nữ mang nhiều năng lượng thuộc nguyên tố Lửa, như cung Bạch Dương, Sư Tử và Nhân Mã. Khi không hàm ý một người thực, thì lá bài này tượng trưng cho việc đạt được nhiều thành tựu, và thường thì thành tựu đó có liên quan đến công việc hoặc sự nghiệp của người hỏi bài. Queen of Wands mang năng lượng tích cực và cao cấp. Khi lá bài này đại diện cho một người phụ nữ trong cuộc sống của bạn, cô gái ấy có thể sẽ là một người bạn tốt, người luôn ở bên cạnh bạn, ngay cả khi dường như cô ấy hơi quá quan tâm đến bản thân mình. Nếu bạn đang cố thụ thai và lá bài này xuất hiện, thì đây là một trong những dấu hiệu tích cực nhất cho thấy điều đó có thể sắp xảy ra.", " Khi Queen of Wands xuất hiện trong giải bài tarot về công việc, đó có thể là thông điệp cho biết rằng bạn sắp đạt được một thỏa thuận đáng chú ý chỉ trong một khoảng thời gian ngắn nữa thôi. Đây là một dấu hiệu rất tích cực. Nếu bạn đang tìm việc, thì lá Queen có thể hàm ý sẽ có một người phụ nữ giúp bạn tìm kiếm được một vị trí công việc mới. Trên hết, mọi thứ sẽ thăng tiến một cách chuyên nghiệp.","Lá bài Queen of Wands là một dấu hiệu rất tích cực trong bối cảnh một câu hỏi về mối quan hệ yêu đương. Nếu bạn đang tìm kiếm người yêu, lá bài cho biết bạn sẽ gặp được ai đó thích hợp thông qua một người quen biết. Nếu hiện bạn không cảm thấy tự tin là chính mình, hãy tìm cách thoát khỏi mớ cảm xúc hỗn độn và không có lợi này, tin tưởng nhiều hơn vào bản thân và rồi vũ trụ sẽ mang đến bạn một tình yêu phù hợp và những hỗ trợ cần thiết mà bạn muốn và cần.","Bạn có thể đang có cảm giác muốn say sưa mua sắm khi lá bài Queen này xuất hiện. Nếu vậy, hãy tập điều độ lại nếu không bạn sẽ hối tiếc về cách chi tiêu của mình. Điều này không có nghĩa tiền bạc là xấu, nó chỉ có nghĩa rằng bạn chưa suy nghĩ thật kỹ trước khi chi tiền. Hãy bắt đầu tiết giảm từ những khoản chi cho những món đồ đạc xung quanh nhà bạn. Tuy nhiên, tu bổ, trang trí lại nhà cửa không cần phải chi tiêu quá nhiều. Hãy sử dụng đồng tiền một cách sáng tạo.","Nhìn chung lá Queen of Wands là một dấu hiệu rất tích cực về sức khỏe. Mức năng lượng và sức sống của bạn đều tốt đẹp; và nếu hiện tại bạn có thể cho phép mình mang thai, thì cho dù bạn có sẵn sàng hay chưa, Queen of Wands cũng vẫn sẽ là lời dự báo cho bạn biết rằng bạn sẽ được thụ thai sớm. Do đó, nếu chưa sẵn sàng làm cha mẹ ngay lúc này, bạn biết mình nên làm gì để tránh có em bé rồi đấy! Bạn dường như có đôi chút quá khích khi lá Queen này xuất hiện, hãy dành thời gian để nghỉ ngơi và thư giãn, cho dù có bất kể chuyện gì xảy ra.","Có thể bạn bất ngờ trở nên khao khát trải nghiệm về mặt tinh thần khi lá Queen of Wands xuất hiện. Dù không có gì sai trái, nhưng cần nhớ đừng để bản thân bị lôi cuốn vào suy nghĩ rằng bạn đột nhiên nhận thấy câu trả lời “thiêng liêng” cho toàn bộ những gì bạn tìm kiếm cho cuộc đời mình và rồi từ bỏ mọi thứ mà bạn có để gia nhập vào một cộng đồng tinh thần/tâm linh ở một nước khác. Hãy dành thời gian, đọc và suy ngẫm, nói chuyện với nhiều người, trước khi có những thay đổi lớn trong cuộc đời. Những khám phá của bạn là rất đáng trân trọng. Hãy nhớ việc đưa ra những quyết định lớn giữa chừng và đột ngột mang tính thay đổi cuộc sống mà không suy xét kỹ lưỡng sẽ có thể đem lại nhiều bất lợi cho bản thân bạn."),
                new TarotCard("King of Wands", R.drawable.king_of_wands,35,"Vua gậy", "nam tính, mạnh mẽ, nhanh nhẹn nhưng bốc đồng, kiêu ngạo, nóng nảy, hèn nhát", "Nhìn chung King of Wands là một dấu hiệu tích cực. Ngay cả khi lá bài không đại diện cho một người nào đó cụ thể trong cuộc sống bạn, thì nó vẫn hàm ý một người đàn ông nào đó xung quanh bạn luôn muốn trao cho bạn sự giúp đỡ cũng như dành những suy nghĩ tốt về bạn. Bạn sẽ cảm thấy tham vọng và có định hướng mục tiêu, cùng những điều khác hơn thế nữa. Bạn sẽ có đủ tiềm lực tài chính để hoàn thành điều bạn đang nghĩ. Hãy suy nghĩ tích cực.","Trong bối cảnh câu hỏi về công việc, King of Wands có thể chỉ một người đàn ông có khả năng cố vấn cho bạn. Đây là người mà bạn có thể tin tưởng được, dù ở một mức độ nào đó, các kỹ năng về con người của anh ta có thể hơi thiếu tinh tế. Hãy học hỏi những gì có thể từ anh ta nhưng đồng thời cũng hãy chú ý tập trung vào những điểm bạn muốn bản thân mình làm theo định hướng khác với anh ta. Nhìn chung, mọi thứ nên đươc tiến hành một cách khôn ngoan, và nếu bạn đang tìm kiếm việc làm, King of Wands sẽ là một dấu hiệu rất tích cực cho biết rằng bạn sẽ sắp nhận được một vị trí công việc mới mà bạn sẽ hài lòng.","Xét trong bối cảnh về tình yêu, King of Wands là một dấu hiệu tuyệt vời. Ông ta hàm ý rằng một ai đó mà bạn đang quan tâm đến cũng có cảm giác che chở và quan tâm về bạn, và rằng họ cũng muốn làm hết sức có thể để tạo điều kiện cho mối quan hệ giữa người ấy và bạn phát triển tốt  Hiện tại bạn có thể đang tập trung nhiều hơn cho công việc / sự nghiệp của bạn, nhưng hãy dành thời gian cho chuyện tình cảm (ngay cả khi bạn đang cô đơn) nếu không bạn sẽ chỉ biết đến công việc như thứ duy nhất trong cuộc sống mình.","Lá bài King of Wands là một dấu hiệu hoàn toàn tích cực về mặt tài chính. Bạn có thể khám phá ra một khả năng mà bạn đã không biết mình có (và khả năng đó có thể sinh ra rất nhiều tiền cho bạn). Nếu bạn từng có suy nghĩ về việc tự khởi nghiệp, vậy thì đây có thể là một thời điểm rất tốt để bắt đầu. Đừng nên liều lĩnh nhảy vào một lĩnh vực hay khoản đầu tư nào đó nếu bạn chưa đặt mình trong vị trí đó – hãy bắt đầu từ chính bạn. Đọc, nghiên cứu, nói chuyện với những người khác về việc thực hiện cái bạn nghĩ bạn đang muốn làm. Tuy nhiên, đừng sợ hãi những giấc mơ lớn. Lòng dũng cảm bản thân nó luôn có sức mạnh.","King of Wands là lời thông điệp tràn trề sức sống. Nhưng đồng thời lá bài cũng mang một lời cảnh báo đừng làm quá sức hoặc sử dụng sức lực của cơ thể bạn vượt quá khả năng thực tế; sự nghỉ ngơi và hồi phục cũng là một phần quan trọng của sức khỏe. Hãy dành thời gian để kiểm tra lại cảm giác thực sự của bạn là gì, và bạn đang ở đúng vị trí chưa. Sự giận dữ và những cảm xúc tiêu cực có thể không có lợi cho sức khỏe của bạn. Hãy cho phép bản thân bạn cảm nhận được điều bạn đang cảm nhận, nhưng chỉ theo một hướng tích cực.","King of Wands là một dấu hiệu cho thấy rằng bạn đã sẵn sàng trên con đường khám phá khía cạnh tinh thần và tâm linh của mình. Tuy nhiên, đây cũng là những cảnh báo về một khuynh hướng lơ là né tránh và đẩy sự việc đi quá xa có thể xảy đến. Việc phát triển tinh thần (tâm linh) là cả một quá trình, không thể một sớm một chiều mà đạt được mục đích. Hãy cho phép bản thân mình thời gian và không gian để thực hiện điều đó. Nếu ngồi yên và suy ngẫm là điều quá khó khăn với bạn, vậy thì bạn cần dành thời gian và nỗ lực để thực hiện những việc đó. Hãy bắt đầu với 30 giây, tất cả những gì bạn cần làm là tập trung mà thôi. Phương cách suy ngẫm bằng cách đi bộ (walking meditation) cũng có thể rất hiệu quả. Tóm lại, hãy làm những việc có hiệu quả với bạn."),
                new TarotCard("Ace of Cups", R.drawable.ace_of_cups, 36, "Ách cúp", "niềm vui, trù phú, màu mỡ, an lạc, bất ổn", "Mọi thứ đang tốt lên! Nhìn chung, mọi người sẽ đối xử với bạn bằng sự hạnh phúc, tình yêu, và thiện chí thường xuyên hơn trong tương lai gần. Bạn nên có cảm giác tích cực. Đây là thời điểm tuyệt vời để kết bạn.","Lá này có thể biểu hiện một sự thăng tiến/công việc mới, tốt đón chờ bạn. Bạn và công việc của bạn được đánh giá cao và có giá trị – bạn sẽ sớm thấy những dấu hiệu này."," Một lần nữa, lá bài này một điềm báo tuyệt vời về tình yêu; hoặc báo hiệu một mối quan hệ hiện tại sẽ chuyển sang một hướng tốt hơn (giống như một khởi đầu mới đầy khích lệ) hoặc, nếu bạn đang độc thân, một mối quan hệ mới sẽ sớm xuất hiện.", "Tiên đoán tích cực này cũng sẽ bao gồm cả tiền bạc, theo khía cạnh “một điều gì đó mới mẻ”. Nó có thể có nghĩa là bạn sẽ tìm thấy một công việc mới nếu bạn đang trong giai đoạn tìm kiếm công việc, hoặc tìm được một số công việc phụ mà bạn có thể làm thêm nếu bạn đã đang có một công việc chính thức tốt đẹp, hoặc cũng có thể là bạn sẽ nhận được một món quà hoặc một số tiền nào đó, giúp bạn giải quyết được các khó khăn tài chính. Rõ ràng đây cũng là một tin tốt.", "Sức khỏe của bạn có thể sẽ được cải thiện; bạn sẽ cảm thấy bản thân được khỏe mạnh và tràn đầy năng lượng.", "Ace of Cups cho thấy rằng bạn cần “hồi đáp” hay “chia sẻ lại” một số tình cảm mà bạn đã được nhận được trong cuộc sống hay đang trải nghiệm hiện tại. Đây cũng là một hướng kết nối bạn với Đấng Tối Cao… về mức độ tâm linh, thiền định về suy tưởng rằng bạn là một đứa con thân yêu của vũ trụ, và cảm thấy như bạn được ban ơn trợ giúp “từ Đấng Tối Cao”, như Người vẫn luôn ở đó, và tất cả những gì bạn phải làm là tìm kiếm Người."),
                new TarotCard("Two of Cups", R.drawable.two_of_cups, 37, "Hai cúp", "tình yêu, phản chiếu, hài hòa, tình bạn, khờ dại, khao khát", "Nhìn chung đây là một lá bài đem đến thông điệp rằng mọi thứ có khả năng sẽ tiến triển tốt. Bạn sẽ cảm thấy nhận được nhiều sự yêu thương, sự chăm sóc, và hài lòng hơn bình thường.","2 of Cups cho thấy bạn được đánh giá đúng với năng lực của bản thân, bạn sẽ cảm thấy thoải mái và dễ chịu hơn trong công việc một cách rất rõ ràng cho dù bạn không thích công việc của bạn đi chăng nữa. Còn nếu như bạn đang tìm việc, có vẻ như bạn sẽ sớm tìm được thôi.", "Lá này đại diện cho tình yêu chân chính, mối quan hệ cân bằng, trách nhiệm và hầu hết những gì mà chúng ta thường mong muốn khi đề cập đến tình yêu và mối quan hệ. Dù sao thì điều đó cũng không có nghĩa là “dù có thế nào đi nữa” thì mối quan hệ/tình yêu của bạn vẫn sẽ luôn bền vững. Đúng hơn, 2 of Cups chỉ cho thấy các nền tảng, các yếu tố thực tế, sự ổn định và tiềm năng cho tình yêu thật sự cũng như sự quyến rũ đang hiện hữu. Và nếu bạn đang tìm tình yêu cho mình thì hãy thư giãn đi, vì có vẻ như tình yêu sắp gõ cửa nhà bạn rồi đấy.", "Khi xét trên khía cạnh tài chính, 2 of Cups hàm ý sự cân bằng và công bằng. Bạn không hẳn là đang “ngập trong đống tiền”, nhưng ít nhất là trong thời điểm hiện tại và tương lai gần, bạn sẽ thấy rằng, ít nhất thì bạn cũng sẽ có nhiều hơn mức đủ để “đáp ứng nghĩa vụ bản thân”, và đó mới là sự khởi đầu."," Nếu bạn đang phải đối mặt với một vấn đề sức khỏe nào đó, vậy thì có thể bạn cần tìm một người điều trị, người mà bạn tin tưởng cũng như có thể cộng tác tốt. Chính họ sẽ giúp bạn “chữa lành” bệnh. 2 of Cups cũng là một điềm khá tốt khi nói đến sức khỏe – bạn cũng nên giữ một thái độ tích cực, điều này vốn rất quan trọng (và mang tính hỗ trợ) để sức khoẻ của bạn trở nên tốt hơn", "Bạn có thể sẽ cảm thấy nguồn năng lượng của bản thân và vũ trụ như hoà làm một. Đây là một trong các lá đại diện cho luân xa và là cánh cổng kết nối với trái tim. Bạn sẽ cảm thấy tình yêu thương với tất cả mọi người và mọi vật nhiều hơn bình thường. Hãy chia sẻ nguồn năng lượng tốt đẹp ấy, vì thế giới luôn cần nhiều yêu thương hơn nữa!"),
                new TarotCard("Three of Cups", R.drawable.three_of_cups, 38, "Ba cúp", "dư dật, kết luận, niềm vui, hạnh phúc, an ủi, mạo hiểm, chóng vánh", "Nhìn chung, mọi thứ sẽ tiến triển tốt. Bạn có thể kỳ vọng ít nhất một cuộc hội ngộ thú vị với một ai đó hoặc một điều gì đó mà bạn đã xa rời một thời gian. Nó cũng có thể mang nghĩa một buổi lễ kỷ niệm – của riêng bạn hoặc một ai đó mà bạn quan tâm – ví dụ như đầy tháng, đám cưới, hay một dịp vui vẻ khác.", "3 of Cups cũng là một điềm tốt xét trên khía cạnh công việc. Tiền có thể đến với bạn, bạn có thể tìm được một công việc mới và/hoặc được lên chức. Tâm trạng trong thế giới công việc của bạn nói chung tương đối dễ chịu. Nếu bạn đang tìm kiếm công việc, thì bạn sẽ có khá nhiều khả năng thành công (có lẽ khả năng này cao đến mức đáng ngạc nhiên đấy), mọi thứ sẽ diễn ra tốt đẹp với bạn. Do đó, đừng bỏ cuộc nhé.","Mặc dù 3 of Cups có thể mang nghĩa thông báo về một cuộc hội ngộ với một tình yêu đã mất; nhưng không phải lúc nào cũng thế. Bạn phải xem xét cẩn thận các phần còn lại của trải bài trong bối cảnh đó, để quyết định lời giải ấy có phù hợp không. Nếu bạn còn độc thân, việc giải bài rất có thể là bạn có thể sẽ sớm gặp một tình yêu – một người thực sự phù hợp với “hình mẫu” mà bạn đang tìm kiếm, và rằng người này sẽ là một tình yêu mới, chứ không phải là người mà bạn “đã từng ” quen.","Đây là thời điểm quan trọng để không bị lệ thuộc quá nhiều vào tiền bạc. Tiền bạc sẽ ổn thỏa đối với bạn, nhưng có thể sẽ hao hụt nhanh hoặc khá nhanh đấy. Điều quan trọng là phải biết rằng đây là một thế giới vũ trụ đầy phong phú, và rằng nhu cầu của bạn (tài chính và những thứ khác) sẽ được đáp ứng, miễn là bạn thực hiện tốt phần của mình","Đây là một thời điểm tuyệt vời để xem xét những gì bạn đang làm – và có lẽ cần phải làm – để tăng cường sức khỏe và sức sống của bạn. Bạn cần tập thể dục nhiều hơn? Nghỉ ngơi nhiều hơn? Hãy suy nghĩ về điều đó và lên một kế hoạch ăn chơi. Bạn có thể làm tốt hơn – dù thậm chí chỉ tốt hơn một chút – so với những gì bạn đang làm ngay bây giờ.","Có thể việc tiếp cận với người khác trong lúc này là một việc làm quan trọng, đặc biệt là nếu bạn thường dành sự chú ý đối với đời sống tâm linh tinh thần của mình trong sự cô tịch. Việc tiếp cận này có thể dẫn đến một sự thức tỉnh tâm linh. Hãy suy nghĩ về việc bạn có hay không có nhu cầu về một người “thầy tinh thần” (bằng xương bằng thịt) và nếu bạn có, hãy tìm kiếm một người như vậy! Đây có thể là bất kỳ người cố vấn đáng tin cậy nào, không nhất thiết phải là một người mang danh một “lãnh đạo tinh thần”."),
                new TarotCard("Four of Cups", R.drawable.four_of_cups, 39, "Bốn cúp", "buồn chán, không hài lòng, dự cảm tương lai", "Hãy chắc chắn rằng bạn đang biết ơn những gì bạn đang có. Đừng dành quá nhiều thời gian trong việc tiếc nuối hay ước muốn cuộc sống của bạn khác đi. Cuộc sống sẽ thay đổi rất nhiều nếu chúng ta đặt sự tập trung và quan tâm của mình vào nó. Hãy chú ý vào những điều và những người trong cuộc sống mà bạn phải biết ơn. Hãy xem xét về việc viết một cuốn “nhật ký lòng biết ơn”.","Bạn có thể không thích công việc của bạn lắm, nhưng ít nhất nó đem lại cho bạn miếng ăn. Hãy nhớ rằng chúng ta không ai thực sự hoàn toàn bị bế tắc cả.","Suy nghĩ một cách nghiêm túc về việc bạn có đang thực tế với những chuyện liên quan đến tình cảm hay không. Liệu trái tim của bạn đang loại trừ tất cả mọi người khác đến với bạn hay không? Ai là người không thật sự trao cho bạn nhiều tình cảm hoặc gần như lúc nào cũng vắng mặt mỗi khi bạn cần?","Tập trung vào điểm mạnh của mình, về những gì bạn có và có thể làm, không phải những gì bạn không thể làm và không có. Nếu bạn không biết vấn đề tài chính của mình sẽ đi đến đâu, hãy thiết lập các mục tiêu và kế hoạch cụ thể về tài chính của mình, và sau đó vạch ra các bước sẽ giúp bạn đạt được những mục tiêu và kế hoạch đó. Hãy xem xét tình hình tài chính của bạn một cách thực tế (biết chính xác vấn đề tài chính của bạn đang ở đâu) vì chỉ như thế mới có thể giúp bạn kiểm soát được tài chính của mình.","Tập trung vào điều tích cực. Nếu có vấn đề về thể chất, hãy nhớ rằng mọi thứ không phải lúc nào cũng giống như lúc này, nên giữ sức khoẻ một cách sáng suốt. Chú ý đến chế độ ăn uống, tập thể dục, và dành thời gian nghỉ ngơi / ăn uống lành mạnh. Bạn có thể cần phải trao đổi với một cố vấn hay bác sỹ đáng tin cậy hoặc bạn bè để có được sự hỗ trợ trong quá trình cải thiện tình trạng thể chất của bạn tại thời điểm này.","Những mơ tưởng có thể gây hại đến các khía cạnh tinh thần của cuộc sống, như khi chúng ta bị cuốn vào lối tư duy tự ti “tôi yếu kém” hay “tôi ước gì mọi thứ sẽ khác “, nó có xu hướng khiến chúng ta mù lòa không nhìn thấy được vẻ đẹp của những Thực Tại trong cuộc sống chúng ta, ngay bây giờ. Hãy vững tin và bám sát vào thực tại. Nếu bạn không thể viết một “nhật ký lòng biết ơn” và viết ra những điều bạn cảm thấy biết ơn mỗi ngày, thì ít nhất hãy làm một danh sách hai mươi người / tình huống / những thứ trong cuộc sống của bạn mà bạn cảm thấy là những điều tốt đẹp khiến bạn biết ơn vào lúc này. Suy ngẫm cùng những dòng nhật ký này có thể giúp nâng cao “nhận thức tinh thần” của bạn vô cùng nhanh chóng."),
                new TarotCard("Five of Cups",R.drawable.five_of_cups, 40, "Năm cúp", "mất mát, cái chết, thất vọng,sự trở lại, dự cảm sai lầm","Trong hầu hết các bộ bài tarot, hầu hết các mô tả hình ảnh trên mỗi lá bài đều gợi lên những định hướng giải thích rất rõ ràng, nhưng đối với lá bài này thì vấn đề không chỉ đơn giản như vậy. Thông thường, lá 5 of Cups thể hiện hình tượng một nhân vật buồn bã hay đang nhìn xuống mặt nước,xung quanh có năm chiếc cốc. Ba chiếc đang bị đổ, còn hai chiếc kia vẫn đặt đứng thẳng nguyên vẹn. Hình ảnh này hàm ý là rất rõ ràng; bạn sẽ chọn lựa cứ mãi buồn bã với những điều không hay hoặc không may đã xảy ra, hay là bạn sẽ tập trung vào những gì bạn vẫn còn nắm giữ, và dành thời gian, sức lực để tiến về phía trước? Vấn đề đặt ra ở đây chính là việc xem xét xem bản thân bạn đang đặt trọng tâm hay đang chú tâm vào điều gì, và hãy luôn chắc chắn rằng lựa chọn hướng tập trung của bạn chính là điều tốt nhất cho chính bạn.","Nếu bạn đang trong giai đoạn tìm việc làm khi lá bài này xuất hiện trong lượt trải bài, thì hãy cố gắng đừng quá nôn nóng nếu cơ hội đến chậm. Hãy nhớ rằng nhà tuyển dụng thường khá chậm chạp trong các vấn đề như xử lý đơn từ, sơ yếu lý lịch hay tương tự vậy. Hãy chủ động nhưng đừng quá nôn nóng. Khi lá này sẽ xuất hiện (và xuất hiện trong lượt trải về câu hỏi công việc…) thì đó có thể là dấu chĩ cho thấy nhiều khả năng đang thực sự chọn sai công việc rồi. Hãy nhìn nhận xem xét những gì bạn muốn làm, và thực hiện các bước theo hướng làm cho điều đó xảy ra.","Một mối quan hệ liên quan đến tình yêu của bạn có thể vừa kết thúc hoặc có thể là sắp bước vào giai đoạn kết thúc. Bạn cần phải nhận ra rằng “bạn không mất tất cả.” Bạn có lẽ chưa sẵn sàng để bắt đầu một mối quan hệ mới đâu, nên hãy trao cho mình nhiều thời gian và không gian hơn hiện tại… rồi mới trở lại với cuộc sống yêu đương. Sẽ có một ngày khi bạn nhận ra rằng lý do mối quan hệ hiện tạu vừa hay sắp kết thúc chính là để giúp bạn đạt được điều mà bạn “thực sự muốn được”,  tức là tìm thấy và nắm giữ được một mối quan hệ khác thực sự phù hợp với mình, và tiến triển tốt.","Việc lo lắng quá nhiều về tiền bạc thực sự có thể gây tác dụng ngược lại với những gì bạn đang cố gắng đạt được, và việc lo lắng này có thể ảnh hưởng đến sự thịnh vượng về mặt tài chính của bạn. Để giải quyết mối lo nghĩ này, có một cách là hãy cho phép bản thân mình rộng lượng hơn. Có thể bạn không “tệ hại” như những gì bạn nghĩ đâu. Hãy thử cho đi một cái gì đó đi, hay quyên góp một khoản nho nhỏ cho một nơi nào đó mà bạn tin tưởng.", "Điều tối quan trọng là bạn phải luôn giữ được một thái độ tích cực. Nếu bạn chỉ không thể vượt qua những ký ức về những điều xấu kẻ khác làm với bạn, hay bỏ qua những gì đã xảy ra trong quá khứ của mình, vậy thì bây giờ là thời điểm để tìm kiếm sự giúp đỡ trong việc giải quyết những vấn đề đó trước khi bạn gây ra những điều không hay cho chính bản thân mình mình bằng cách giam mình trong quá khứ tối tăm. Quá khứ đã qua đi rồi. Hãy tập trung vào hiện tại và tương lai nhé.","Đây là lúc quan trọng cần sự xuất hiện của sự tha thứ. Bạn không tha thứ cho người khác vì lợi ích của họ, hay bởi vì họ “xứng đáng” được tha thứ, mà bạn cần thiết phải làm điều đó vì lợi ích của riêng bạn, lý do là vì việc dồn nén sự tức giận và nỗi đau trong lòng là điều vô cùng tai hại và tồi tệ cho bản thân. Không chỉ vậy, trong khi bạn vẫn còn mất thời gian giận dữ và đau đớn, người mà bạn tức giận rất có thể đang có được tâm trạng khá tốt đấy! Vì vậy, bây giờ là thời gian để buông xả đi những điều còn lấn cấn trong tâm hồn."),
                new TarotCard("Six of Cups", R.drawable.six_of_cups, 41, "Sáu Cúp", "quá khử, kỉ niệm, tương lai, đổi mới, vượt qua hiện tại", "Điều quan trọng là không nên dành quá nhiều thời gian và công sức để mãi suy nghĩ về “những ngày xa xưa tươi đẹp”. Chúng ta đều có những kỷ niệm hạnh phúc để nhớ về theo thời gian, nhưng nếu chúng ta bắt đầu có suy nghĩ rằng “những năm tháng tốt nhất đã trôi qua”, thì đó rất có thể sẽ trở thành sự thật đấy.","Đây là thời điểm rất tốt cho công việc có liên quan đến sự sáng tạo. Nếu công việc của bạn không mang tính ứng dụng sáng tạo, vậy thì hãy đảm bảo rằng bạn sẽ dành một chút thời gian cá nhân cho việc vẽ tranh, hay viết lách, tạo album ảnh kỷ niệm… nói chung là bất cứ sở thích nào đó thiên về tính sáng tạo. Bạn sẽ nhận thấy công việc bình thường hàng ngày của bạn sẽ gặt hái được nhiều kết quả khả quan hơn đấy.","Nếu bạn đang có một mối quan hệ tình yêu lãng mạn, thì sự xuất hiện của lá bài này có thể chỉ ra rằng một mối quan hệ này cần phải được “tái tập trung” định hướng tới tương lai. Hãy nói về mục tiêu và ước mơ của các bạn. Hãy khám phá và thử nghiệm những điều mới mở. Điều quan trọng là đừng để bản thân mình bị mắc kẹt trong quá khứ ở thời điểm hiện tại. Đôi lúc lá này sẽ hàm ý về việc tái xuất hiện của một người yêu cũ trong cuộc sống của bạn. Hãy suy nghĩ thật kỹ trước khi tái bắt đầu một mối tình cũ nào đó. Cần nhớ lại lý do anh ấy/cô ấy trở thành “người cũ” của bạn.","Nếu bạn đang tìm kiếm phương cách đầu tư tiền bạc, hoặc kiếm thêm một ít tiền, thì hãy đặc biệt chú ý đến những thứ mang tính cổ điển hay hoài cổ, hoặc những lựa chọn đầu tư có ý nghĩa hấp dẫn đối với trẻ em. Tiền có thể đến với bạn từ một người nào đó từ quá khứ của bạn hoặc thông qua thừa kế (nhưng điều này tất nhiên không có nghĩa là chắc chắn một người nào đó gần gũi với bạn sẽ qua đời).","Nếu bạn đang gặp vấn đề liên quan đến sức khỏe, vậy thì sự xuất hiện của lá này có thể có nghĩa là bạn cần phải thực sự hồi tưởng trở lại thời thơ ấu để kiểm tra tận nơi gốc rễ nguyên nhân của vấn đề thể chất mà mình đang gặp phải. (Gợi ý: Yếu tố tình cảm rất thường gắn liền với thể chất). Cách điều trị bằng phương pháp trò chuyện có thể sẽ rất hữu ích. Bạn cần xem xét sắp xếp nói chuyện với các thành viên trong gia đình về các vấn đề thể chất mà họ từng hoặc đang gặp phải, và chắc chắn rằng bạn hiểu rõ ràng về tình hình bệnh sử của gia đình mình.","Bất cứ loại nghi lễ nào cũng đều có thể hữu ích cho bạn xét trên khía cạnh tinh thần tại thời điểm hiện tại. Bạn có thể tìm thấy vẻ đẹp và ý nghĩa trong việc tái hiện lại một số các nghi lễ thiêng liêng mà bạn biết đến khi còn là một đứa trẻ; điều này không có nghĩa là bạn phải quay trở lại thực hiện những nghi thức truyền thống tâm linh đầu tiên từng trải qua và làm theo chúng một cách rập khuôn; thay vào đó, hãy cập nhật và cải tiến, thay đổi các nghi lễ truyền thống cũ. Điều đó sẽ đem lại nhiều lợi ích cho bạn sau này."),
                new TarotCard("Seven of Cups", R.drawable.seven_of_cups, 42, "Bảy Cúp", "thành công huyễn hoặc, ảo tưởng, lười biếng, thất hứa, quyết định sau thời gian dài", "Nhìn chung lá bài này chỉ ra rằng bạn cần thu hẹp lại các mục tiêu của mình. Khi lá bài này xuất hiện, rất có thể bạn đang làm việc quá nhiều, và phần nhiều những gì bạn làm lại không đạt hiệu quả cao. Hãy chỉ chọn những gì quan trọng nhất và làm từng thứ một một cách căn cơ và cẩn thận. Có thể bạn đang có cảm giác như bị quá tải. Hãy nhớ rằng bạn luôn có thể kiểm soát được ít nhất là một vài khía cạnh nào đó cũng như chi phối được mức độ công việc mà bạn lựa chọn.","Nếu bạn đang có một cơ hội hay thảo thuận kinh doanh nào đó mà bạn cảm thấy có vẻ như quá tốt thì rất có thể nó đúng là quá tốt đến mức khó có thể có thật. Hãy xem xem bạn đang đầu tư tiền bạc và niềm tin vào đâu. Nếu bạn đang làm việc hay muốn làm việc trong một môi trường sáng tạo hay tương tự vậy thì sự xuất hiện của lá bài này là dấu hiệu tốt cho thấy sự tiến triển tốt, nhưng nếu công việc hay lĩnh vực công việc bạn đang tham gia thiên về sự chính xác, tính toán, hay mang thiên hướng kinh doanh thì lá bài này lại là lời cảnh báo rằng bạn đang đầu tư quá nhiều thời gian cho tâm trí mình chìm ngập trong những vấn đề trên mây, và bạn sẽ cần phải tỉnh táo, tập trung lại đấy.","Có thể bạn đang nắm được nhiều lựa chọn tình cảm trong tay. Nhưng hãy rõ ràng và cụ thể về những gì bạn muốn trong một mối quan hệ, hãy đánh giá các lựa chọn đó một cách thấu đáo và kỹ lưỡng, hãy thành thật với tất cả những người liên quan (bao gồm cả bản thân bạn), rồi sau đó mới tiến tới. Hãy nhớ rằng việc yêu đương chớp nhoáng chỉ xảy ra ở giai đoạn đầu của một mối quan hệ mà thôi. Không có mối quan hệ nào có thể tồn tại cảm giác “chớp nhoáng” đó mãi được. Những mối quan hệ tốt kể cả mối quan hệ về mặt tinh thần đều sẽ tiến đến giai đoạn trầm lắng và ít hấp tấp hơn vào một lúc nào đó. Tuy vậy điều này không có nghĩa là bạn sẽ cảm thấy nhàm chán với tình yêu của mình đâu.","Đây không phải là thời điểm tốt để đầu tư tài chính. Nhưng nếu bắt buộc phải tham gia đầu tư thì bạn cần tham khảo ý kiến chuyên môn tập luyện dần dần trước. Việc kinh doanh của bạn có thể đang quá phân tán (điều này đặc biệt đúng nếu bạn đang tự kinh doanh riêng). Để có lợi nhuận tối đa, hãy tập trung hơn.","Bất cứ triệu chứng nào đó liên quan đến sức khỏe mà bạn đang gặp phải có thể có một phần nguyên nhân là do thiếu sự tinh tế và nhạy cảm về bản thân, nhưng đương nhiên việc tham vấn ý kiến của bác sỹ cũng là điều cần thiết. Hãy ghi lại những triệu chứng mà bạn gặp kèm theo những suy nghĩ của bản thân về chúng. Tập thể dục sẽ giúp bạn tăng cường cảm giác nhạy cảm về bản thân hơn.","Hãy hình tượng hóa trong tâm trí bạn các mục tiêu quan trọng và những mong muốn mà bạn đã được đáp ứng. Hãy sử dụng những câu tuyên ngôn mang tính tích cực (như “Tôi tự tin, “Tôi sẽ làm được”…) để giúp bạn biến những ước mơ của mình thành hiện thực. Hãy giữ thái độ tích cực. Nếu bạn cần giúp đỡ, hãy đề nghị. Có nhiều mối ảnh hưởng mang tính tích cực xung quanh bạn, chỉ cần biết nên tìm thấy chúng ở đâu mà thôi."),
                new TarotCard("Eight of Cups", R.drawable.eight_of_cups, 43, "Tám cúp", "thành tựu tạm thời, trì trệ, chán nản, từ bỏ, mắc kẹt, nản lòng", "Hãy biết rằng thời điềm của những đổi mới đầy tốt đẹp đang ở chờ bạn phía trước. Nếu “nội tâm” của bạn nói với bạn rằng bạnthật sự cần phải bước ra khỏi một mối quan hệ, một công việc, haymột sự chuẩn bị, dàn xếp nào đó liên quan đến cuộc sống, vậy thì hãy tin tưởng chính mình. Thay vì sự chia lìa này xuất hiện như một sự kiện buồn hay ai đó hoặc điều gì đó rời bỏ bạn, thì thông thường lá bài này luôn hàm ý bạn mới chính là người đưa ra chọn lựa rời bỏ này. Điều thường thấy là bạn phải chấp nhận đóng lại cánh cửa sau lưng trước khi cánh cửa kế tiếp ngay trước mắt có thể rộng mở đón bước bạn.","Công việc hiện tại của bạn có thể không còn thích hợp cho bạn nữa. Bạn có thể cảm thấy buồn chán, hoặc nhận thấy những nỗ lực của mình không được đánh giá đúng. Hãy suy nghĩ thật kỹ lưỡng về điều gì là tốt nhất và phù hợp nhất cho bạn; hãy cố gắng giải quyết tình cảnh hiện tại của bản thân;còn nếu không được, hãy ra đi. Lựa chọn nào cũng sẽ hữu ích cho bạn. Dù vậy, một điều chắc chắn là mọi thứ sẽ không tự thay đổi theo chiều hướng tốt đẹp hơn. Vì vậy nếu bạn thật sự cảmthấy không hài lòng, hãy suy nghĩ chi tiết về những thay đổi cụ thể sẽ cần phải xảy ra để giúp bạn hạnh phúc hơn trong công việc. Còn nếu thật sự bạn cảm thấy ít có khả năng hoặc không thể thay đổi những điều đó, vậy thì hãy tìm kiếm cho mình một vị trí, một công việc mới. Bạn hoàn toàn có thể làm được điều đó.","Bạn có thể nhận thấy rằng một mối quan hệ lâu dài sẽ đến hồi kết thúc. Nếu bạn chắc chắn về điều này, đừng chờ đợi thời điểm nào là đúng đắn để nói ra điều đó, bởi vì sẽ không bao giờ có “thời điểm tốt” để nói với ai đó rằng mối quan hệ giữa bạn và người ấy cần phải kết thúc. Đừngnhảy ngay vào một mối quan hệ mới; trước hết hãy cho mình một khoảng thời gian và không gian để đánh giá mọi việc lại đã.","Nếu bạn đang thật sự cảm thấymệt mỏivới cuộc sống và công việc hiện tại, vậy thì đừng để cho cái cảm giác an toàn khi được nhận lương hàng tháng trói buộc bạn với công việc đó.Nhưng bạn cũng cần phảisuy xét kỹ trước khi ngưng việc. Bảo hiểm sức Khỏe, đặc biệt là tại Hoa Kỳ, là điều đáng để cân nhắc đấy (nếu bạn đi làm thì dĩ nhiên công ty của bạn sẽ chi trả khoản này cho bạn). Tuy nhiên, với một tầm nhìn xa và kế hoạch rõ ràng, cũng nhưviệc chú ý đến từng chi tiết, nếu bạn đang muốn làm việc cho chính mình thì bạn thật sự có thể làm được. Chỉ cần chắc chắn rằng kế hoạch của bạn được sắp đặt tốt và được chuẩn bị một cách toàn diện. Tiền không phải là tất cả mọi thứ.","Sự căng thẳng có thể xảy ra và khiến bạngặp nhiều vấn đề về sức khỏe. Hãy chắc chắn rằng bạn đang chú ý đầy đủ đến chế độ ăn uống, tập thể dục, và ngủ đủ giấc. Hãy giữ một thái độ tích cực và lạc quan.","Bạn có thể cần một chút thời gian riêng tư. Hãy đón nhận khoảng thời gian đó. Bạn sẽ nhìn thấuđược nội tâmvà tìm được sự khôn ngoan trong không gian tĩnh lặng đó, và lắng nghe tiếng nói êm dịu nhỏ nhẹ từtâm khảm."),
                new TarotCard("Nine of Cups", R.drawable.nine_of_cups, 44, "Chín cúp", "hạnh phúc, thỏa mãn, niềm vui, phù phiếm, bị lừa gạt", "Sự xuất hiện của lá bài này thường là một điềm báo của sự hạnh phúc, và những điều tốt đẹp sắp đến. Nó cũng thường có nghĩa là những gì bạn muốn sẽ có nhiều khả năng đến với cuộc sống của bạn. Nếu câu hỏi trong lượt trải bài là câu hỏi dạng có/không, thì lá bài này là dấu chỉ mạnh mẽ nhất của câu trả lời “Có”, nhưng cũng giống như mọi khía cạnh khác của Tarot, đó không phải là một lời khẳng định hoàn toàn chắc chắn và vẫn sẽ có thể bị thay đổi khác đi."," Bạn có thể sẽ gặp được vô số may mắn không ngờ đến. Nếu bạn có một giấc mơ lớn hoặc đang thực hiện một dự án nào đó vốn đang bị đình trệ, vậy thì trong tháng này tiến độ thực hiện có thể sẽ tiến triển đến hồi kết thúc. Nếu bạn được mời phỏng vấn vào một vị trí nào đó, thì lá bài này có thể hàm ý rằng rốt cuộc thì thời điểm mà bạn thật sự mong đợi sẽ đến.","Lá này là một dấu chỉ rất tốt cho cuộc sống tình yêu của bạn. Nếu bạn đang trong một mối quan hệ tình cảm, thì mối quan hệ đó nhiều khả năng sẽ trở nên sâu sắc hơn, tốt hơn và ngọt ngào hơn. Nếu bạn còn độc thân thì đây là thời điểm tuyệt vời để gặp gỡ mọi người. Hãy bước ra ngoài, hòa nhập, trò chuyện với những người mà bạn gặp được mà không cần phải kỳ vọng vào bất kỳ kết quả tiềm năng nào cả . Mục đích ở đây chỉ là để giải stress mà thôi.","Vì 9 of Cups là một lá bài mang ý nghĩa tích cực vô cùng mạnh mẽ, nó cũng mang hàm ý tốt về tình hình tài chính của bạn. Khi lá này xuất hiện, nó có thể hàm ý rằng tình hình tài chính bạn đang hình dung sẽ có thể xảy ra, mặc dù có lẽ không hoàn toàn chính xác theo cách mà bạn muốn cho lắm.","Nói chung, đây có thể sẽ là thời điểm mà tình hình sức khỏe của bạn rất thoải mái. Nếu gần đây bạn đã phải thực hiện một xét nghiệm nào đó thì kết quả xét nghiệm rất có thể thuận theo ý muốn của bạn. Hãy sử dụng nguồn năng lượng tốt của lá bài này để củng cố hoặc cải thiện các thói quen của bạn, thêm vào đó hoặc tăng cường thêm các bài tập thể dục hoặc làm giàu kiến thức về dinh dưỡng cho bản thân.","Đây là thời điểm quan trọng để nhìn nhận và cải thiện việc ứng dụng những câu tuyên ngôn tích cực (như “Tôi thấy tự tin” hay “Tôi chắc chắn mình sẽ làm được”…), những suy nghĩ hình dung vào cuộc sống bạn và cách thức chúng có thể làm phong phú thêm cuộc sống của bạn trên mọi lĩnh vực. Hãy tin tưởng chính mình. Hãy ước mơ lớn, rồi thực hiện những gì cần thiết để làm cho chúng trở thành sự thật."),
                new TarotCard("Ten of Cups", R.drawable.ten_of_cups, 45, "Mười cúp", "thành công trọn vẹn, an toàn, bền vững, xung đột, khác biệt", "Nhìn chung, Ten of Cups hàm ý đến một đời sống gia đình hạnh phúc (ngay cả khi bạn còn độc thân), và sự hạnh phúc đó còn dành cho những người mà bạn quan tâm nhất, như cha mẹ, anh chị em, con cái… Hình ảnh lá bài cũng phản ánh sự hài lòng về mặt tinh thần. Cho dù xuất hiện trong bối cảnh câu hỏi thế nào đi nữa thì đây cũng là một lá bài rất tích cực.","10 of Cups cho thấy bạn đã đạt đến vị trí không thể thay thế ở công việc hiện tại. Đây là một điều tốt, nhưng đừng quá phấn khích, hầu như chẳng ai có thể thay thế cả! Có vẻ như bạn đang đi đúng hướng đấy, chỉ cần nhớ đừng có thỏa mãn và mất tập trung thôi. Hãy luôn làm việc tốt","Nếu bạn đang lo lắng về một mối quan hệ tình cảm và rút được lá bài này trong lượt trải bài, vậy thì đây là dấu hiệu tốt rằng bạn thật sự không cần lo lắng gì đâu. Bạn và người yêu bạn đều đang cùng nhìn về một phía. Nếu bạn đang trong một mối quan hệ nhưng vẫn chưa hứa hẹn chính thức về tương lai, và đang mong chờ điều đó xuất hiện; vậy thì hãy chuẩn bị đi vì ngày đó gần đến rồi đấy. Đừng phá hỏng mọi thứ bằng cách gây chuyện khi mà chẳng có điều gì phải lo lắng.","Tiền bạc có thể không phải là vấn đề (hay ít nhất không phải là vấn đề quá to tát) đối với bạn khi bạn nhận được lá bài này trong một câu hỏi về tài chính. Ten of Cups hàm ý đây là giai đoạn phát đạt của bạn. Chỉ cần nhớ là phải để tiền phục vụ bạn hiệu quả xứng đáng với công sức bạn đã bỏ ra. Hãy chia sẽ gia tài của mình đến mức độ mà bạn cảm thấy có thể và thấy thoải mái khi làm vậy, dù là thoải mái ở bất cứ khía cạnh nào. Chúng ta sống cùng nhau cuộc đời này mà.","Sức khỏe của bạn nhiều khả năng sẽ tốt lên đáng kể, nếu như hiện tại bạn đang có bệnh hay vấn đề nào đó liên quan đến sức khỏe.; còn nếu sức khỏe bạn hiện đang ổn thì lá bài này hàm ý tình trạng đó vẫn sẽ được duy trì trong thời gian tới. Bạn cũng có thể suy nghĩ sâu xa hơn khi là bài này xuất hiện, như xem xét những việc làm trong khả năng có thể giúp tiếp tục cải thiện sức khỏe, như ăn uống đủ dinh dưỡng, tập thể dục thể thao, cũng như đánh giá các nguyên nhân có thể gây tổn hại sức khỏe bản thân mà bạn không kiểm soát được (ví dụ như một căn bệnh di truyền trong gia đình). Hãy cứ thoải mái tinh thần nếu như bạn đang rơi vào trường hợp sau vì có lo nghĩ cũng không ích lợi gì, điều đó nằm ngoài tầm kiểm soát của bạn mà.","Hãy sống cho hiện tại, quan tâm đến ngày hôm nay. Bạn sẽ có tất cả nếu phát huy nguồn năng lượng tốt cũng như duy trì một thái độ tích cực. Điều quan trọng là hãy chia sẽ những thái độ đó cho mọi người quanh bạn, nhất là những ai đang tuyệt vọng và cần một nguồn năng lượng tích cực."),
                new TarotCard("Page of Cups", R.drawable.page_of_cups, 46,"Tiểu đồng cúp", "người trẻ tình cảm, nhẹ dạ và cố chấp trong cảm xúc, thiếu kiên định", "Nhìn chung, lá bài này thể hiện một nhu cầu được chơi đùa, mơ ước và có một niềm vui nho nhỏ. Khi lá bài này xuất hiện, nó đôi khi có nghĩa rằng bạn đang sống quá nghiêm túc đấy. Margot Fonteyn nói “Một điều quan trọng tôi học được trong những năm qua là sự khác biệt giữa việc quan trọng hóa công việc và việc giữ thái độ nghiêm túc với chính mình.  Quan trọng hóa và nghiêm túc trong công việc là điều bắt buộc, nhưng còn quá nghiêm túc với chính mình thì lại là thảm họa”. Hãy nhớ điểm khác biệt đó.","Ngay cả khi mọi thứ đã luôn tồi tệ với công việc mà bạn đã gắn bó trong 10 năm qua, thì sự xuất hiện của lá bài này cho thấy rằng bạn vẫn có lý do để hy vọng và mong đợi một tương lai tích cực. Cho dù bạn là ai, bạn đều có nhiều lựa chọn nghề nghiệp. Cuộc sống có thể thay đổi nhanh chóng, và nhiều khi những thay đổi đó theo hướng tích cực. Nếu bạn không hài lòng với công việc hiện tại, hãy biết rằng mình vẫn đang nắm giữ chìa khóa để thay đổi nó.","Một người trẻ tuổi hơn (có thể là chỉtrẻ hơn bạn vài ngày) có thể là một phần trong viễn cảnh tình yêu của bạn. Hãy nhớ rằng tình yêu không phân biệt tuổi tác, cho nên đừng xem nhẹ bất cứ ai dù người đó có trẻ hơn bạn! Lá bài này trong ngữ cảnh tình yêu có thể như thế này: ai đó đang muốn nhanh chóng gây ấn tượng với bạn. Hãy cho họ một cơ hội.","Nếu tài chính của bạn đang phải chắt bóp vì những quyết định bởi một ai đó khác, thì sự xuất hiện của lá bài này nói rằng thời điểm đang gần kề. Vấn đề tài chính của bạn có thể xoay chuyển theo hướng tốt hơn. Nếu bạn đang tính mua sắm một hay vài thứ mắc tiền, đừng suy nghĩ tằn tiện quá!", "Hãy lắng nghe cơ thể bạn với sự quan tâm sâu sắc. Nó đang cho bạn biết điều gì? Ví dụ, nếu bạn đang bị đau lưng, liệu có phải bạn đang mang vác (theo nghĩa đen hoặc nghĩa bóng) quá nặng?","Bạn có thể cần xem xét lại những nghi thức tâm linh của mình nếu những truyền thống của bạn đang dần mất hiệu lực. Điều này có thể hữu ích để cho phép bản thân có thời gian và không gian cân nhắc nơi nào bạn có thể theo đuổi các thông tin tinh thần lôi cuốn bạn. Hãy khám phá. Hãy đọc những thứ về tinh thần từ những hệ thống niềm tin / văn hóa khác thay vì của chính bạn."),
                new TarotCard("Knight of Cups", R.drawable.knight_of_cups, 47, "Kị sĩ cúp", "tốt với mọi người, thiếu kiên định tình cảm, giải pháp, trì trệ, lợi dụng lòng tin", "Nhìn chung, lá bài này thể hiện một nhu cầu được chơi đùa, mơ ước và có một niềm vui nho nhỏ. Khi lá bài này xuất hiện, nó đôi khi có nghĩa rằng bạn đang sống quá nghiêm túc đấy. Margot Fonteyn nói “Một điều quan trọng tôi học được trong những năm qua là sự khác biệt giữa việc quan trọng hóa công việc và việc giữ thái độ nghiêm túc với chính mình.  Quan trọng hóa và nghiêm túc trong công việc là điều bắt buộc, nhưng còn quá nghiêm túc với chính mình thì lại là thảm họa”. Hãy nhớ điểm khác biệt đó.","Ngay cả khi mọi thứ đã luôn tồi tệ với công việc mà bạn đã gắn bó trong 10 năm qua, thì sự xuất hiện của lá bài này cho thấy rằng bạn vẫn có lý do để hy vọng và mong đợi một tương lai tích cực. Cho dù bạn là ai, bạn đều có nhiều lựa chọn nghề nghiệp. Cuộc sống có thể thay đổi nhanh chóng, và nhiều khi những thay đổi đó theo hướng tích cực. Nếu bạn không hài lòng với công việc hiện tại, hãy biết rằng mình vẫn đang nắm giữ chìa khóa để thay đổi nó.","Một người trẻ tuổi hơn (có thể là chỉtrẻ hơn bạn vài ngày) có thể là một phần trong viễn cảnh tình yêu của bạn. Hãy nhớ rằng tình yêu không phân biệt tuổi tác, cho nên đừng xem nhẹ bất cứ ai dù người đó có trẻ hơn bạn! Lá bài này trong ngữ cảnh tình yêu có thể như thế này: ai đó đang muốn nhanh chóng gây ấn tượng với bạn. Hãy cho họ một cơ hội.","Nếu tài chính của bạn đang phải chắt bóp vì những quyết định bởi một ai đó khác, thì sự xuất hiện của lá bài này nói rằng thời điểm đang gần kề. Vấn đề tài chính của bạn có thể xoay chuyển theo hướng tốt hơn. Nếu bạn đang tính mua sắm một hay vài thứ mắc tiền, đừng suy nghĩ tằn tiện quá!","Hãy lắng nghe cơ thể bạn với sự quan tâm sâu sắc. Nó đang cho bạn biết điều gì? Ví dụ, nếu bạn đang bị đau lưng, liệu có phải bạn đang mang vác (theo nghĩa đen hoặc nghĩa bóng) quá nặng?","Bạn có thể cần xem xét lại những nghi thức tâm linh của mình nếu những truyền thống của bạn đang dần mất hiệu lực. Điều này có thể hữu ích để cho phép bản thân có thời gian và không gian cân nhắc nơi nào bạn có thể theo đuổi các thông tin tinh thần lôi cuốn bạn. Hãy khám phá. Hãy đọc những thứ về tinh thần từ những hệ thống niềm tin / văn hóa khác thay vì của chính bạn."),
                new TarotCard("Queen of Cups", R.drawable.queen_of_cups, 48, "Hậu cúp", "ngay thẳng, tận tâm, thiếu quyết đoán, vấn đề rắc rối", "Nhìn chung, Queen of Cups chỉ năng lượng thuộc tính nữ vốn ở sẵn đó trong góc tâm hồn bạn, rằng những người phụ nữ xuất hiện trong cuộc sống bạn đang hỗ trợ rất nhiều cho bạn và / hoặc một người phụ nữ sẽ luôn có mặt lúc bạn cần. Queen of Cups cũng nhắc chúng ta đối xử bản thân mình với sự yêu thương và lòng trắc ẩn. Bất kỳ bạn là ai và bạn ở đâu, có những thứ trong bạn rất xứng đáng nhận được sự yêu thương và tôn trọng.", " Trong bối cảnh công việc, Queen thường chỉ một người phụ nữ sẽ luôn giúp đỡ bạn. Queen nhắc nhở chúng ta vận dụng tình yêu thường và lòng trắc ẩn vào công việc của mình, và có thể ngay cả khi chúng ta làm việc trong một phân xưởng đầy máy móc thay vì con người. Mọi thứ có lẽ tốt hơn bạn nghĩ trong thế giới công việc của bạn khi lá bài tarot này xuất hiện. Hãy giữ suy nghĩ tích cực!", "Khi Queen of Cups xuất hiện trong một câu hỏi thuộc bối cảnh tình yêu, lá bài thường hàm ý về một viễn cảnh rất tích cực về một mối quan hệ. Nếu bạn đang đơn thân (chưa có người yêu) và lá bài xuất hiện, vậy thì đây là một dấu hiệu tốt khuyến khích bạn đi ra ngoài và kết bạn, bởi vì một mối quan hệ lãng mạn mới rất tích cực đang trong tầm tay của bạn. Khi Queen of Cups xuất hiện, bạn sẽ rất dễ được lôi cuốn vào chuyện yêu đương. Hãy giữ cân bằng và giữ vững quan điểm của bạn. Ngay cả khi bạn hào hứng với tình yêu mới, điều quan trọng là cần phảiđảm bảo sao cho những việc khác vẫn tiếp diễn bình thường trong cuộc sống của bạn.", "Queen of Cups thường chỉ những tin tức tốt liên quan đến tài chính. Bất cứ điều gì xảy ra trong cuộc sống tài chính của bạn, thì cũng hãy cho phép bản thân mình nghỉ ngơi, và hãy biết rằng bạn có thể tạo ra một sự khác biệt tích cực nếu bạn có nhu cầu vật chất nhiều hơn trong cuộc sống của mình. Queen of Cups hàm ý khả năng sáng tạo và bạn có thể yêu cầu sự giúp đỡ từ những người khác ngay bây giờ (đặc biệt là từ những người là nữ giới),nhận lấy sự giúp đỡ và nghe những lời khuyên bạn cần để tạo ra một sự khác biệt tích cực về mặt tài chính. Cũng vậy, hãy mong đợi những chuyển biến tích cực.", "Lá bài này hàm ý sự hỗ trợ và tình yêu trong bối cảnh về sức khỏe. Nếu bạn cần chữa bệnh, hãy tìm đến những người phụ nữ hoặc một ai đó có nguồn năng lượng nữ tính mạnh mẽ. Lá bài cũng nói đến nhu cầu yêu thương và hỗ trợ chính bản thân mình, đặc biệt nếu bạn đang đối mặt với những thách thức về sức khỏe. Nếu bạn đang trong giai đoạn chữa trị một loại bệnh lý nào đó, thì lá Queen of Cups có thể là thông điệp cho bạn biết rằng bạn cần tha thứ và trao cho chính mình nhiều tình yêu thương hơn nữa. Cho dù trong hoàn cảnh nào đi nữa thì cơ thể, sức khỏe của bạn chắc chắn sẽ rất tích cực theo nhiều cách. Hãy suy nghĩ tích cực và tìn tưởng vào năng lượng chữa lành của vũ trụ. Lá bàu cũng có thể mang những thông điệp hay tin tức tốt về sức khỏe.", "bạn gần như chạm vào được mối liên quan sâu xa bên trong mình ở thời điểm hiện tại. Queen of Cups cũng có thể đánh dấu những tăng trưởng trong trải nghiệm dự đoán tương lai. Hãy cởi mở với dòng năng lượng kỳ diệu của cuộc sống và cảm nhận tình yêu đang tuôn chảy trong mọi thứ xung quanh bạn. Một người phụ nữ có thể là lãnh đạo tinh thần của bạnở hiện tại. Cho dù bản thân bạn là nam hay tính nữ, hãy xem xét những nguyên mẫu và năng lươnjg thuộc tính nữ ngay lúc này. Họ sẽ dẫn dắt bạn tiếp cận những hiểu biết về tinh thần mới mẻ và sâu sắc hơn."),
                new TarotCard("King of Cups", R.drawable.king_of_cups,49, "Vua cúp", "quý ông, pháp luật, bất công, cướp bóc","Nhìn chung King of Cups đại diện cho sự tử tế và lòng trắc ẩn. Có một yếu tố của tình yêu thực sự và thuần khiết trong cuộc sống bạn bất kể tình huống bạn là gì khi ông ấy xuất hiện. Hãy chăm sóc bản thân bạn, và cho phép những người khác chăm sóc bạn, khi bạn nhận được lá bài này trong một giải bài tarot.","Trong bối cảnh công việc, lá bài này thường chỉ một ông chủ hoặc một người đồng nghiệp rất tốt trong nhóm của bạn. Đừng ngần ngại yêu cầu anh ta hỗ trợ bạn nếu bạn thấy cần. Nếu bạn đang tự hỏi người khác cảm thấy như thế nào về bạn trong công việc khi lá bài King này xuất hiện, hãy yên tâm rằng họ đánh giá cao và tôn trọng bạn và công việc của bạn. Nhiều thứ có thể đang được cải thiện khi lá King này xuất hiện.", "Với những câu hỏi về tình yêu, khi lá King này xuất hiện, những tin tức nhận được sẽ rất tốt. Có một vài lá bài mang ý nghĩa yêu thương hơn King of Cups. Nếu bạn đang tìm kiếm tình yêu, lá bài này hàm ý một ai đó rất cân bằng và tình yêu sẽ sớm được dẫn dắt theo hướng của bạn. Lá King là tin mừng về tình yêu.", "King of Cups có thể là lời nhắc nhở bạn cần đối mặt với những khó khăn về tài chính. Đừng né tránh thực tại hoàn cảnh tài chính của bạn. Đồng thời lá bài cũng có thể hàm ý rằng bạn nênsống phóng khoáng trong một số trường hợp bất kể tình cảnh tài chính đang được thắt chặt. Cho dù bạn có gặp khó khăn về tài chính đến mức nào đi nữa, thì vẫn luôn có những thứ chắc chắn bạn có thể và nên chia sẻ với người khác, ngay cả khi nó chỉ là một đồ vật hoặc chút ít thời gian của bạn. Điều này thường giúp đem lại nhiều sự thịnh vượng hơn cho cuộc sống của bạn. Hãy cho đi sự tử tế và lòng trắc ẩn, và luôn nhớ rằng “gieo nhân nào gặt quả ấy”. Bên cạnh đó, đừng ngần ngại yêu cầu giúp đỡ nếu bạn thật sự cần.", "Với những câu hỏi về sức khỏe, đặc biệt nếu bạn đang được chăm sóc bác sỹ, lá King of Cups này nhắc nhở chúng ta hãy trao trọn niềm tin tưởng vào người chữa bệnh cho mình. King of Cups có thể là dấu chỉ của những tin tức tốt lành, cũng như cho thấy những người thầy thuốc / trị liệu cho bạn là những người đáng tin cậy (đặc biệt nếu người đó là đàn ông). Hãy quan tâm chăm sóc cơ thể mình, luônchú ý đến những gì bạn mang vào cơ thể mình và những người xung quanh khi lá King này xuất hiện.", "Về tinh thần, King có thể là bề nổi của điều gì đó đang có mâu thuẫn, vì ông ta kết nối những phẩm chất chung của tính nữ về lòng trắc ẩn, tình yêu và mối quan tâm, với năng lượng tính nam của hành động, sự hoàn thành và nhận thức sáng suốt. Hãy suy niệm kỹ lưỡng về những sự kết hợp này, và hành động dựa theo đó. Bạn có thể tích cực thể hiện lòng trắc ẩn như thế nào? Làm cách nào bạn có thể bày tỏ lòng yêu thương mà vẫn tạo được giới hạn? Hãy bắt đầu tạo ranh giới cho chính bản thân mình trước khi bạn xem xét áp dụng ranh giới ấy cho người khác. “Suy nghẫm bằng cách đi lại” sẽ rất tốt và mang lại sự hiểu biết sâu sắc, đó là lời khuyên cho bạn khi lá King of Cups xuất hiện."),
                new TarotCard("Ace of Swords", R.drawable.ace_of_swords, 50, "Ách kiếm", "chiến thắng, vinh quang, vô vọng, tình yêu mù quáng, ích kỷ", "Nhìn chung, Ace of Swords kêu gọi chúng ta đừng ngại ngần thực hiện một bước nhảy nếu nó mạng lại lợi ích tốt nhất cho bản thân ta. Nói ngắn gọn, hãy dũng cảm lên. Như câu ngạn ngữ cổ nói, “Hãy cảm nhận sự sợ hãi và rồi cứhành động đi.” Bạn đã có một ý tưởng tốt về những gì bạn cần phải làm rồi đấy! Phần khó khăn nhất là bắt tay vào làm thật sự mà thôi. Hãy bắt đầu ngay hôm nay!", "Trong câu hỏi về bối cảnh công việc, Ace of Swords có thể mang nghĩa là bạn sẽ sớm thử nghiệm một chiến lượccông việc khác hoặc thậm chí có thể chọn rời bỏ công việc mà bạn đang làm. Nếu bạn đang quyết tâm ở lại nơi mà bạn đang làm việc, bạn có thể có một số ý tưởng tốt cho chính sách mới hoặc các quy trình có thể giúp cho công việc cũng như cuộc sống thường ngày của mọi người dễ dàng hơn, giúp giải quyết vấn đề từ điểm mấu chốt và khắc phục sản phẩm đầu ra. Đừng ngại trò chuyện với những đồng nghiệp xung quanh. Có một câu ngạn ngữ cổ nói rằng: “Nếu bạn không quan tâm đến việc ai được lợi, thì những gì bạn làm được sẽ vô cùng ấn tượng”. Bạn rồi sẽ nhận được sự tín nhiệm từ những điều tốt lành mà bạn mang đến. Việc xây dựng niềm tin không phải chuyện một sớm một chiều.", "Xét về mặt tình yêu, Ace of Swords có thể có nghĩa là bạn đã sẵn sàng để bắt đầu một khởi đầu mới mẻ, và nếu bạn đang trong một mối quan hệ tình cảm, thì lá bài này có thể hàm ý đến một vấn đề nào đótrong mối quan hệ sẽ khiến bạn muốn chấm dứt duy trì cuộc tình này. Đừng ngần ngại nói lên suy nghĩ của bạn. Nếu một điều gì đó trong mối quan hệ gây tổn thương hay không tốt cho bạn, và bạn mất đi người yêu vì những gì bạn đã hay sẽ nói ra – vậy thì thực ra bạn không hề mất mát gì cả đâu, mà chỉ đơn giản là bạn đang hướng tới một mối quan hệ tốt hơn cho bạn mà thôi. Hãy nói lên sự thật!", "Khi nói đến tài chính, Ace of Swords cho ta biết rằng khi nào đủ là đủ. Đây không phải là lúc để tìm kiếm cơ hội liên quan đến tiền bạc. Đừng đầu tư nếu bạn cảm thấy không đủ khả năng vì có thể bạn sẽ thua lỗ. Có thể sẽ có người hỏi vay tiền bạn. Cho dù bạn có cảm thấy thông cảm cho họ thế nào đi nữa thì có lẽ tốt nhất vẫn là không nên cho vay bất cứ thứ gì họ hỏi. Bạn có thể sẽ không được trả lại khoản vay đó, và tệ hơn nữa có thể bạn sẽ đánh mất mối quan hệ với người bạn cho mượn tiền. Chỉ nêncho mượn nếu bạn thật sự “sẵn sàng” chấp nhận rủi ro.", "Về sức khỏe, có vẻ như rốt cuộc thìbạn cũng đã tự mình nhận ra đươc một số thói quen xấu của bản thânvà ngăn chặn chúng. (Hàm ý này cũng có thể mang nghĩa ngược lại – rốt cuộc thì bạn cũng đã bắt đầu nhận ra điều gì đó là tốt cho bản thân!) Có thể bạn sẽ cảm thấy đôi chút căng thẳng khi nhận ra những điều này. Hãy chăm sóc bản thân thật tốt, vì chỉ khi tựquan tâm chính mình thì bạn mới biết được quan tâm đến người khác như thế nào là thích hợp nhất và tốt nhất. Các bài tập thể dục đặc biệt quan trọng đối với bạn, nhưng cũng đừng nên tậpquá sức.", "Bạn có thể nhận thấy đã đến lúc từ bỏ một số niềm tin không thích hợp và không tốt cho bản thân. Đừng tự ép mình nếu bạn cảm thấy những niềm tin ấy không còn phù hợp. Ai cũng từngcó lúc phạm sai lầm haybị mắc kẹt với những sai lầm. Bạn sẽ tìm ra những sự thật về tâm linh phù hợp với bạn và có thể là có ích cho bạn. Đừng ngại khi trao đổi với ai đó về những gì bạn nghĩ là sẽ có ích cho họ xét trên khía cạnh tâm linh."),
                new TarotCard("Two of Swords", R.drawable.two_of_swords, 51, "Hai kiếm", "cân nhắc, tân thủ, bội tín, trở mặt", "Nhìn chung, 2 of Swords cho chúng ta biết rằng các mối quan hệ của bạn có thể không được tốt, cho dù đó là tình bạn, tình yêu, hoặc bạn làm ăn. Tuy nhiên, bạn nên xem xét vai trò của mình trong (các) mối quan hệ này một cách bình đẳng, cũng như suy nghĩ xem bạn có đang được đối xử công bằng bởi (các) đối tác của bạn hay không? Nếu không, đây có thể là lúc để đưa mọi thứ trở lại vị thế cân bằng. Điều này gần như chắc chắn sẽ đòi hỏiở bạn sự giao tiếp cởi mở. Không gì có thể thay đổi mà không cần nỗ lực.", "Có thể bạn đang mãi chờ đợi một quyết định liên quan đến công việctừ người khác. Hãy cố gắng kiên nhẫn và kiềm chế, không hối thúc người khác trước khi họ sẵn sàng đưa ra quyết địnhấy. Nếu bạn đã làm tốt phần mình, vậy thì mọi thứ có thể sẽ diễn ra có lợi cho bạn. Hãy chắc chắn rằng bạn đã làm những gì cần phải làm một cách thật khôn ngoan;còn nếu không, hãy tìm ra cách để giải thích hoặc bù đắp lại cho những thiếu sót của mình.Điều đó thể hiện rằng bạn có trách nhiệm với việc mình làm. Đừng tỏ ra lo lắng hay sợ hãi.", "2 of Swords là một điềm tốt, đặc biệt là cho một mối quan hệ tình cảm đang đang tồn tại. Lá bài này biểu thị rằng mối quan hệ của bạn sẽtrở nên dễ dàng phát triển, ổn định và cân bằng hơn, trong đó việc cả hai người sẽ đối xử với nhau một cách công bằng sẽ là yếu tố đóng vai trò quan trọng. Nếu bạn đang tìm kiếm tình yêu, hãy nhìn nhận nghiêm túc về bản thân mình, về những cảm xúc nào ngoài tầm kiểm soát mà bạn cần phải giải quyết. Mọi người có thể dự đoán được tình cảnh củasự tuyệt vọng từ trước khi nó thật sự xảy ra, và đấy chính là một bước ngoặc quan trọng. Một người bạn đời không giúp bạn trở nên hoàn thiện; thay vào đó, một người bạn đời chỉ là phần trang trí, tô điểm thêm trên chiếc bánh cuộc đời đã được vẽ ra bởi chính bản thân bạn. Đừng nhìn ra bên ngoài chínhbản thân mình khi muốn tìm kiếm cho mình sự hạnh phúc. Một khi hiểu và làm được điều đó, mối quan hệ mà bạn thực sự muốn và cần sẽ đến thôi.", "Về tài chính, 2 of Swords cho thấy một yêu cầu về sự cân bằng và tính thận trọng. Hãy chắc chắn rằng bạn sử dụng tiền của mình đúng chỗ và khôn ngoan, đến từng đồng, từng xu. Trốn tránh vấn đề sẽ không giúp giải quyết chúng. Nếu bạn cần thêm thu nhập, hãy nhớ rằng bạn có thể làm cho nó xảy ra, những gì bạn cần làm chỉ là cần phải sáng tạo mà thôi. Điều dễ nhất để làm có lẽ là cắt giảm một số chi phí của bạn. Bạn có đang chi tiêu vào những khoản không phải là tuyệt đối cần thiết? Hãy cố gắng cân đối chi tiêu của bạn. Nếu bạn cần giúp đỡ để làm điều đó, hãy nói ra.", "Khi lá bài này xuất hiện trong bối cảnh của câu hỏi sức khỏe, nó mang thông điệp cần phải thật chú ý đến cảm xúc của người được giải bài. Bạn cảm thấy thế nào về những điều hay những người hiện có liên quan đến cuộc sống của bạn? Hãy ghi chép lại và nói những điều trên với một ai đó mà bạn tin tưởng là người quan trọng trong lúc này. Điều này có thể dẫn đến một sự đột phá tích cực cho sức khoẻ của bạn. Hãy giữ suy nghĩ thoải mái và nhớ rằng việc cân bằng thói quen là điều rất quan trọng ngay bây giờ. Ăn uống đủ chất, ngủ đủ giờ, tập thể dục đủ cường độ, và uống đủ nước.", "Khi lá 2 of Swords xuất hiệntrong một trải bài liên quan đến câu hỏi về tâm linh, nó cho thấy rằng bạn đang đi trên một con đường cân bằng hơn và tích cực hơn là bạn nghĩ (về khía cạnh tinh thần). Hãy giữ vững những gì bạn tin tưởng và cách bạn nhìn nhậnmọi thứ, ngay cả khi quan điểm hay phương thức nhìn nhận tinh thần của bạn không phù hợp với tất cả mọi người."),
                new TarotCard("Three of Swords", R.drawable.three_of_swords, 52, "Ba kiếm", "buồn khổ, vắng mặt, chậm trễ, rối loạn", "Lá này đề cập đến một vấn đề cảm xúc khá quan trọng mà người hỏi cần giải quyết trước khi bước tiếp. Phủ nhận nỗi đau không làm cho nó biến mất (chẳng hạn như việc phủ nhận cái chân gãy không làm cho nó lành lại). Tuy nhiên, cần phải chú ý đến sự cân bằng ở đây giống như đối với lá 2 of Swords. Hãy cho phép bản thân trải nghiệm nỗi đau, nhưng đừng chìm vào nó quá lâu hay quá sâu sẽ không tốt. Tuy nhiên chỉ có bạn có thể quyết định bao lâu là đủ. Không ai khác có thể quyết định thay cho bạn.", "Cảm xúc hay cái tôi của bạn sẽ bị tổn thương vì một vấn đề liên quan đến công việc. Hãy cố gắng ghi nhớ điều này:cuộc sống của bạn không chỉ (cũng không nên) toàn bao gồm những gì bạn hiểu rõ và làm tốt. Sẽ có lúc bạn cần đến một vài sự trợ giúp để tiếp cận với công việc tốt hơn, và khi đó đừng ngần ngại đưa ra lời yêu cầu. Nếu bạn đang trong giai đoạn tìm kiếm công việc, có thể bạn phải thử chuyển sang một lĩnh vực khác với định hướng mà bạn đang đeo đuổi. Hãy kiên trì!", "Lá bài này không phải là hồi chuông báo tử dành cho một mối quan hệ, nhưng nó là lời cảnh báo rằng mối quan hệ đang gặp nhiều khó khăn và hai bên đang chịu nhiều đau khổ. Để chomột mối quan hệ tình cảm tồn tại và đơm hoa, cả hai cần sẵn sàng hành động, trò chuyện và suy nghĩ về mối quan hệ đấy. Bạn có thể thấy rằng một người mà bạn nghĩ rằng quan tâm đến bạn thật ra lại không yêu bạn như bạn đã nghĩ. Nếu đó chính là vấn đề mà bạn đang mắc phải, vậy thì thì đừng chỉ ôm nỗi đau trong lòng cho riêng mình và cũng đừng đau buồn quá lâu. Bạn đang tìm được hướng đi đúng đắn cho mối quan hệ tình cảm phù hợp cho bạn đấy. Chúng ta không thể buộc mọi thứ diễn ra theo ý mình hay đòi hỏi một người nào đó quan tâm đến bạn nếu tự thâm tâm họ không muốn. Một người yêu đích thực sẽ luôn quan tâm – theo cách riêng của họ và hoàn toàn tự nguyện – mà không cần bạn phải nói hay yêu cầu.", "Nói đến tiền bạc thì lá bài này có thể là một thông điệp buồn. Nhưng nhớ rằng sợ hãi sẽ không giúp ích gì cho bạn cả. Hãy xem xét tình hình, không nao núng, sau đó nghĩ xem cần phải làm gì, từng bước một. Nếu tình hình hoàn toàn “bi đát”, đừng nên cố tưởng tượng ra viễn cảnh xa xôi. Hãybắt đầu với những gì trong tầm tay, ngay cả những việc nhỏ như bữa ăn sắp tới của bạn sẽ có những món gì. Đừng để lòng tự tôn ngăn bạnđưa ra lời yêu cầu giúp đỡ tài chính trong lúc này. Để người khác giúp bạn cũng là đem lại cho họ niềm vui đấy.", "Sức khỏe bạn có thể không tốt như bạn kỳ vọng. Những cảm xúc, sự trầm cảm hay lo lắng có thể sẽảnh hưởng đến sức khoẻ của bạn. Hãy giữ thái độ tích cực. Đó là điều quan trọng nhất lúc này, dù cho vấn đề hay tình hình hiện tại có ra sao đi nữa. Hãy nhớ rằng bác sĩ không phải thần thánh. Hãyđối xử với bản thân bằng tình yêu và suy nghĩ tích cực. Hãy tìm hiểu về những phương thức chữa bệnh hay. Nếu bạn cần sựgiúp đỡ để luôn duy trì được trạng thái tích cực, thìhãy cứyêu cầu điều đó. Hãy bao bọc bản thân với tinh thần khoẻ mạnh, và những ảnh hưởng tích cực hết mức có thể.", "Đây không phải là lúc nhân nhượng cho những sự kiện, ý tưởng, tình huống, hay những người áp đặt tư tưởng tinh thần của họvào cuộc sống của bạn. Bạn cần có thời gian để tạo ra những điều mới mẻ cho bản thân, cho trái tim, cảm xúc, tinh thần. Tạo không gian cho một mình bạnlúc này là tốt nhất. Bạn đã làm tất cả những nghi thức chữa lành hay giải phóng tinh thần thoải mái mà bạn cần phải làm chưa? Câu trả lời mà bạn tìm kiếm đang ở bên trong bạn, mặc dù bạn có thể cần giúp đỡ để tìm ra chúng. Hãy nói chuyện với ai đó mà bạn tin tưởng."),
                new TarotCard("Four of Swords", R.drawable.four_of_swords, 53, "Bốn kiếm", "đình chiến, nghỉ ngơi, tham vọng, di chúc, phòng ngừa", "Nhìn chung lá này thường có nghĩa là bạn đang cần có thời gian riêng và không gian riêng cho chính mình. Có thể bạn đang dành hơi quá nhiều thời gian và sự chú ý dành cho những người hay những điều khác. Đừng ngần ngại để vẽ lên ranh giới cho chính mình hoặc nói không. Ngược lại, khi những người khác nói không với bạn, hãy tôn trọng điều đó. Cố gắng lấn tới hay làm ngược ý họ trong trường hợp này chính là tự tìm lấy rắc rối mà thôi.","Đây là một khoảng thời gian tuyệt vời để nghỉ phép nếu bạn có thể (thậm chí hãy cân nhắc cả việc tận hưởng cả một kỳ nghỉ cuối tuần dài hơn bình thường). Bạn cần nghỉ ngơi và suy ngẫm đôi chút. Nếu có những thứ không diễn ra như bạn mong muốn, vậy thì đừng tự làm chúng một mình, hay khiến cho mọi thứ phức tạp hơn. Đôi khi chúng ta chỉ cần kiên nhẫn với quá trình thực hiện các công việc đó. Nhưng nếu bạn không thể kiên nhẫn được, có lẽ đã đến lúc để bắt đầu tìm kiếm một vị trí hay một nghề nghiệp khác đấy.","Nếu bạn đang trong một mối quan hệ yêu đương, bạn có thể cảm thấy bị mất kết nối đôi chút. Điều quan trọng là để cho nửa kia của bạn biết bạn đang nghĩ gì, nhưng hãy nhớ rằng cảm xúc của bạn là của riêng bạn, không phải lúc nào họ cũng phải làm cho bạn cảm thấy vui. Có thể hiện tại hai bạn chỉ cần đơn giản là tạm xa nhau một thời gian, mỗi người tự tìm cho mình không gian riêng. Đừng ngần ngại để làm như vậy. Và nếu bạn đang tìm kiếm tình yêu, thì bây giờ không phải là lúc vội vàng. Tốt hơn hết là bạn hãy tìm hiểu một cách rất, rất cụ thể và chính xác về những gì bạn đang tìm kiếm cho một mối quan hệ. Rồi cũng sẽ đến khi bạn sẽ gặp một ai đó thôi,nhưng có lẽ không phải trong thời gian này.","Bạn cần phải có một kế hoạch khác nếu là người chịu trách nhiệm cho khác khoản chi của mình và bạn không biết làm sao để kiếm được tiền. Hãy suy nghĩ những cách thức mới để có thêm thu nhập. Nếu bạn đang làm kinh doanh và đang cho vay tiền, hãy cố gắng nhanh chóng việc thu hồi các khoản vay đó lại. Nếu bạn chờ đợi người vay tự giác trả tiền cho bạn, thì bạn có thể sẽ phải chờ đợi rất lâu. Tình hình bế tắc tài chính này sẽ không kéo dài lâu. Nếu có thể, hãy xem xét việc góp một khoản nhỏ vào một công việc, quỹ hay mối làm ăn nào đó mà bạn thấy tin tưởng, miễn là bạn có thể vui vẻ làm điều này bằng chính trái tim mình. Khi đó, bạn sẽ có thể mở ra một dòng chảy mới của sự thịnh vượng cho cuộc sống của bản thân; còn nếu không có gì xảy ra, sự ủng hộ này vẫn sẽ khiến bạn cảm thấy thoải mái dù chỉ trong thời gian ngắn về những gì mình đang nắm giữ.","Lá bài này trong một câu hỏi bối cảnh về sức khỏe chính là thông điệp nhắc nhở bạn hãy hạnh phúc với những gì bạn đang có. Sức khỏe của bạn có thể không hoàn toàn được như bạn mong muốn, nhưng mặt khác, điều chắc chắn là sức khoẻ bạn vẫn tốt hơn khối người. Hãy hạnh phúc vì những gì bạn có. Lá 4 of Swords cũng có thể là lời kêu gọi sống chậm lại và nghỉ ngơi nhiều hơn. Việc gắng sức khi bạn đã quá mệt mỏi có thể làm bạn đổ bệnh hoặc bị chấn thương. Ai cũng cần được nghỉ ngơi và cần khoảng thời gian riêng tư của chính mình.","Trong bối cảnh câu hỏi về tinh thần, lá bài này cho thấy bạn đang cần không gian. Bạn cần thời gian và sự yên tĩnh để thực sự có được sự liên lạc với linh hồn của bạn và các nguồn năng lượng phổ quát xung quanh tất cả chúng ta. Ngay cả khi bạn làm việc toàn thời gian và có mười bốn đứa con, nếu bạn dành cho bản thân một sự ưu tiên, thì khi đó bạn vẫn có thể chắt chiu được một hoặc hai phút cho riêng mình. Hãy tìm kiếm, nắm bắt và tận dụng khoảng thời gian đó. Sự tỉnh táo của bạn, cũng như tâm hồn của bạn, đang kêu gọi bạn đáp ứng nhu cầu này đấy"),
                new TarotCard("Five of Swords", R.drawable.five_of_swords, 54, "Năm kiếm", "thất bại, tuyệt vọng, độc ác", "Nhìn chung, lá 5 of Swords có thể hàm ý rằng một ai đó mà bạn quan tâm có thể sẽ rời xa bạn trong một khoảng thời gian nào đó. Nếu đúng như vậy thì cũng đừng nên tỏ ra rằng bạn muốn níu giữ người ấy bạn mọi giá. Hãy nghĩ rằng rồi thì anh/cô ấy/họ sẽ quay trở lại bên bạn. Lá 5 of Swords cũng có thể hàm ý những xung đột, cả bên ngoài lẫn bên trong. Đừng làm leo thang các cuộc xung đột hiện tại một cách không cần thiết. Hãy suy nghĩ trước khi bạn hành động.","Về công việc, lá 5 of Swords là lời cảnh báo cho thấy rằng kẻ thù lớn nhất của bạn trong công việc là chính bản thân bạn. Đừng mong đợi toàn bộ hệ thống thay đổi chỉ vì bạn tin rằng nó cần phải khác đi. Cách bạn nghĩ cũng quan trọng không kém những gì bạn nói ra. Tuy có thể bạn đang chịu đựng một vài điều bất công nào đó tại nơi làm việc, nhưng hãy đảm bảo bạn cư xử và hành động một cách phù hợp","Thật không may khi lá 5 of Swords xuất hiện trong một trải bài liên quan về tình yêu, nó có nghĩa là xung đột trong tình yêu là viễn cảnh gần như chắc chắn. Mọi thứ có thể không diễn ra như bạn kỳ vọng trong mối quan hệ yêu đương hiện tại. Hãy suy nghĩ bình tĩnh và cẩn thận về điều gì gây ra những rạn vỡ trong mối quan hệ này. Hãy chân thật với bản thân mình trước, rồi hãy chân thật với người mình yêu. Hãy biết chấp nhận thực tế.","Cần phải thận trọng và kiềm chế khi xét đến vấn đề tiền bạc nếu lá bài này xuất hiện trong lượt trải bài cho câu hỏi liên quan đến khía cạnh tài chính. Hãy thể hiện sự khéo léo của mình khi cần thiết. Liệu có những thứ nào không cần thiết mà bạn có thể xem xét bán đi không? Liệu bạn có thể cắt giảm bớt chi phí nữa không? Điều quan trọng hơn cả là đừng từ bỏ. Đừng quá tự cao, không muốn đề nghị sự giúp đỡ nếu bạn thật sự cần nó. Hãy biết rằng sẽ có những người sẵn sàng hỗ trợ bạn.","Bạn có thể đang căng thẳng do nhiều yếu tố khác nhau, vì thế khi lá bài này xuất hiện bạn cần phải nhẹ nhàng và chú ý đến bản thân mình nhiều hơn. Nghỉ ngơi là điều cấp thiết. Mọi cảm giác khác như lo lắng hay cảm thấy có lỗi chỉ vô ích vào lúc này. Hãy quên chúng đi.","Ngay cả khi bạn bất ngờ tìm ra những câu trả lời cho vấn đề tinh thần mà bạn đang tìm kiếm bấy lâu, thì hãy nhớ rằng chúng là những giải đáp dành cho chính bạn mà không phải người nào khác. Bạn không cần phải cố gắng chuyển hóa bất kỳ ai khác nghe theo lối suy nghĩ của bạn. Lá bài 5 of Swords cũng có thể hàm ý một sự phá vỡ trong khía cạnh tinh thần vốn không còn hiệu quả với bạn. Hãy cho bản thân mình thời gian và không gian để tìm ra những câu trả lời mới."),
                new TarotCard("Six of Swords", R.drawable.six_of_swords_icon, 55, "Sáu kiếm", "thành công sau khó khăn, bất ngờ, kết quả không thuận lợi", "Nhìn chung lá bài 6 of Swords thể hiện sự gia giảm nhẹ về cảm giác căng thẳng và trạng thái bị kèm cặp. Mọi thứ có thể hiện tại không hoàn hảo, nhưng chúng sẽ dần thay đổi theo chiều hướng tốt hơn so với những gì xảy ra ở quá khứ gần đây. Hãy dành thời gian này để duy trì niềm hy vọng và quyết định nơi bạn sẽ đến. Thậm chí một ít thời gian nghỉ ngơi ngắn hoặc thay đổi hoàn cảnh cũng sẽ có nhiều lợi ích với bạn. Điều này không nhất thiết đồng nghĩa với việc phải tốn kém tiền bạc.","Bạn có thể quyết định hoặc đang trong quá trình rời bỏ công việc không còn phù hợp với bạn nữa. Hãy tin tưởng Tạo hóa sẽ dành cho bạn vị trí mà bạn muốn, và mọi chuyện xảy ra đều có lý do của nó. Đừng ngần ngại yêu cầu giúp đỡ hoặc tìm kiếm thông tin mới khi bạn gặp phải những áp lực trong môi trường công việc mới của bạn. Lá 6 of Swords cũng có thể hàm ý một thay đổi nơi bạn kinh doanh, theo nghĩa đen, và đó là một thay đổi tốt.","Về tình yêu, lá bài 6 of Swords có thể hàm ý về một nhu cầu thay đổi tình cảnh. Nếu bạn đang trong một mối quan hệ, hãy cùng người yêu mình đi đâu đó, có thể chỉ đơn thuần là xuống phố đi lòng vòng hay đại loại vậy cũng được. Nếu bạn đang tìm kiếm tình yêu, vậy thì lá bài này cho thấy bạn cần từ bỏ những thói quen cố hữu của bản thân nếu như bạn muốn gặp một ai đó mới. Hãy khám phá những phương thức và thói quen mới.","Sự xuất hiện của lá 6 of Swords có thể hàm ý rằng tình hình tài chính của bạn đang có xu hướng thay đổi: theo hướng tốt hơn nếu bạn đang cố gắng, hoặc theo hướng tệ hơn nếu thỉnh thoảng bạn quá “bốc đồng”. Nếu bạn đang ở vế trước, bạn có thể dành cho bản thân mình những món chi tiêu nhỏ nhặt nhưng đừng quá say mê, và hãy sử dụng dòng tiền của bạn một cách khôn ngoan. Nếu bạn đang ở vế sau, hãy chuẩn bị tiếp cận với thời điểm khó khăn sắp tới. Đây không phải lúc cho những đầu tư rủi ro.","Nếu bạn đang giải quyết các vấn đề sức khỏe, thì chuyên gia chăm sóc sức khỏe hay bác sĩ hiện tại của bạn có thể chưa phải là người tốt nhất để giúp bạn. Bạn có thể tham khảo thêm một số trung tâm hoặc phòng khám y tế uy tín ở khu vực của bạn. Ngoại trừ vấn đề sức khỏe nghiêm trọng, lá bài này có thể cho thấy rằng điều bạn thực sự cần làm là lên đường tham gia một chuyến du lịch nghỉ dưỡng hoặc trải nghiệm một sự thay đổi về cảnh quan. Hãy tìn cách đáp ứng nhu cầu này cho bản thân bạn, ngay cả khi cách duy nhất bạn có thể làm là tâm sự với người mà bạn chưa từng trò chuyện trước đây.","Đôi lúc một số giải đáp về tinh thần đã né tránh bạn giờ đây đang đi vào nhận thức của bạn một cách bất chợt. Đôi khi điều này được gắn chặt với một thay đổi về thói quen; việc hành hương hoặc trải nghiệm với những văn hóa tinh thần khác nhau. Hãy chú ý những giấc mơ của mình. Chúng thưởng chỉ cho bạn mọi thứ mà bạn bỏ lỡ trong suốt nhiều giờ đi bộ."),
                new TarotCard("Seven of Swords", R.drawable.seven_of_swords, 56, "Bảy kiếm", "tính toán, che dấu, cãi nhau, không chắc chắn", "Nếu bạn đang có liên quan đến một cuộc xung đột nào đó trong tháng này, bạn sẽ cần chú ý cẩn thận bị chơi xấu. Xung đột này có thể ứng nghiệm trong một phạm vi rất rộng từ việc gian lận trong chơi bài tây, đến việc ai đó đang cố gắng dàn xếp một điều gì đó nghiêm trọng trong công việc của bạn. Điều quan trọng là về cơ bản, để che đậy dấu vết của bạn, bạn cần đảm bảo rằng bạn đang làm mọi thứ theo cách đường hoàng, công khai; khi đó bạn không còn phải lo lắng gì nữa cả, ngay cả khi người nào đó quyết tâm đổ lỗi cho bạn.","Về công việc, sự xuất hiện của lá bài 7 of Swords có thể hàm ý rằng sếp của bạn đang xem xét công việc của bạn rất sát sao. Hãy làm việc chăm chỉ, và đảm bảo rằng công việc của bạn sẽ không bị khiển trách được. Cố gắng đừng dính vào các công việc chính trị. Nếu bạn đang kiếm việc, đừng ngần ngại nói chuyện với người mà bạn biết hoặc đã từng làm việc chung lúc trước.","Nếu bạn đang có một mối quan hệ gắn kết khi lá bài này xuất hiện, thì khi đó lá bài này truyền tải một thông điệp rất rõ ràng rằng sự tin tưởng là yếu tố cực kỳ quan trọng. Nếu bạn cảm thấy nghi ngờ vì một lý do nào đó về đối phương của mình, hãy hãy chuyện về điều này cho dù sẽ gặp khó khăn. Việc công kích thụ động sẽ chỉ khiến mọi chuyện tệ hơn thôi. Nếu bạn đang tìm kiếm tình yêu, bạn sẽ có cơ hội gặp một người rất thú vị vào thời điểm này, nhưng đừng cố tránh mặt cho dù bạn không hứng thú với một mối quan hệ; người tình tiềm năng sẽ dần tiến tới thành người yêu gắn kết hơn với bạn. Hãy cố gắng giữ cân bằng.","Khi lá bài 7 of Swords xuất hiện, đây hoàn toàn không phải là thời điểm để đầu tư rủi ro hoặc vay mượn tiền, vì bạn có thể mất nhiều hơn thế. Bạn có thể phải đưa ra một số quyết định khó khăn về tiền bạc, nhưng nhớ là đừng hi sinh sự chính trực của mình chỉ vì tiền bạc, để rồi cuối cùng lòng tự trọng và hạnh phúc tinh thần của bạn sẽ bị đe dọa. Tiền không thể mua được những thứ đó.","Đừng vội tin ngay vào những gì bạn nghe được lần đầu về sức khỏe. Hãy tìm kiếm thêm một lựa chọn thứ hai hoặc thứ ba trước khi khẳng định bất kỳ điều gì liên quan đến sức khỏe (ví dụ như một quá trình trị liệu lâu dài hoặc một cuộc phẫu thuật). Hãy cởi mở với việc chữa lành tinh thần, nhưng nếu bạn đang tranh đấu với một bênh lý nghiêm trọng nào đó, thì cần lưu ý rằng việc điều trị không thể đánh đồng với việc “chữa lành” này. Có nhiều cách để chữa lành. Hãy xem xét chính xác điều gì đang cần chữa lành trong bạn và trong cuộc sống bạn?","Hãy thận trọng về những người có ảnh hưởng hoặc các lãnh đạo tôn giáo hoặc tinh thần muốn bạn chi tiền để có cái gọi là “sự khai sáng”. Sự thực là bạn không cần một lãnh đạo tinh thần để học hỏi và phát triển về tinh thần cả. Hãy tự thân khám khá. Bên cạnh đó bạn cũng nên cẩn thận với những người bạn có cùng tư tưởng tinh thần mới gặp"),
                new TarotCard("Eight of Swords", R.drawable.eight_of_swords, 57, "Tám kiếm", "dồn sức quá nhiều vào việc nhỏ, tin buồn, chán nản, mẫu thuẫn, bất an", "Nhìn chung, lá bài 8 of Swords cho chúng ta biết rằng chúng ta phải nghĩ thoáng về thất bại để có thể giành được chiến thắng. Đừng sợ những gì chưa rõ, vẫn còn nhiều may mắn đang ở bên cạnh bạn. Hãy suy sét các suy nghĩ, phát ngôn và hành vi của bạn về các yếu tố tiêu cực và nhớ rằng những gì bạn nói và làm, và những lựa chọn mà bạn đưa ra đều ảnh hưởng đến kết quả cuộc sống của bạn. Nếu bạn cần giúp đỡ trong việc quản lý và giải quyết các nỗi sợ của mình, hãy nói ra.","Nếu bạn ghét công việc của mình, hãy tin rằng bạn đang nắm giữ quyền và khả năng thay đổi tình cảnh. Bạn có thể không thay đổi được toàn bộ môi trường công việc chuyên môn của mình, nhưng điều đó sẽ không ngăn bạn tìm kiếm một viễn cảnh mới mẻ khác biệt và tốt hơn, nếu đó là những gì tâm trí và tinh thần bạn đang đòi hỏi. Bạn không thể ôm giữ cả hai mà phải đưa ra lựa chọn: hoặc là bạn muốn thoát khỏi tình cảnh hiện tại của mình, hoặc là bạn chuẩn bị cho bản thân mình tiếp tục chấp nhận mọi thứ như cũ. Bạn sẽ chọn cách nào?","Về tình yêu, lá 8 of Swords cho thấy bạn cần đánh giá lại một mối quan hệ một cách nghiêm túc. Bạn có thể đang trong mối quan hệ với người không tốt với bạn, vì bạn cho rằng điều đó còn “hơn là không có gì”. Nếu vậy, hãy suy nghĩ lại. Việc có một mối quan hệ không lành mạnh thực sự là điều tồi tệ hơn nhiều so với việc “FA” (cô đơn). Nếu bạn đang tìm kiếm tình yêu, điều đầu tiên là xem bạn đã yêu bản thân mình như thế nào. Người khác không thể đi cùng bạn và chỉnh sửa bạn. Thế nên hãy bắt đầu từ chính bản thân bạn.","Lá bài 8 of Swords cho bạn biết rằng bạn cần nhận thức được lời tiên tri về việc tự thỏa mãn nhu cầu tiêu xài của mình. Nếu bạn nghĩ rằng những vận xui trong tài chính của bạn sẽ không bao giờ thay đổi, thì điều đó có thể là sự thật trong cuộc sống bạn. Hãy suy nghĩ thông suốt về kỹ năng của bạn và các lựa chọn bạn có là gì hoặc có thể là gì. Hãy khám phá ra những nguồn thu nhập mới. Hãy kỳ vọng những điều tốt đẹp nhất, ngay cả khi bạn đang chuẩn bị cho điều tồi tệ nhất.","Hơn bất kỳ mọi điều gì khác, khi nói đến sức khỏe, lá bài này hàm ý sự kết nối tinh thần/thể chất và nhu cầu cần thiết phải xem xét những suy nghĩ, dự tính và hành vi của chúng ta khi chúng ta điều trị những triệu chứng hoặc điều kiện sức khỏe khó chịu. Chúng ta không tạo ra các triệu chứng hay bênh tật (yếu tố di truyền và môi trường tất nhiên có vai trò trong việc gây ra bệnh tật), nhưng sức khỏe chúng ta sẽ luôn có khuynh hướng tệ đi khi chúng ta mãi suy nghĩ tiêu cực hoặc trong đầu lúc nào cũng đầy mối lo âu. Những thứ này có thể bị vùi lấp rất sâu trong tâm trí. Hãy tìm sự giúp đỡ nếu bạn cần để đào lên những khía cạnh tiêu cực gây hại cho sức khỏe này.","Hãy biết rằng từ tận thâm sâu, bạn đã có các câu trả lời mà bạn cần. Hãy dành thời gian cho không gian tĩnh lặng và thiền định, bạn sẽ thấy rất hữu ích. Nếu bạn vẫn cần định hướng, hãy yêu cầu tình thần và vũ trụ vạn vật cho bạn một dấu hiệu. Những dấu hiệu này có thể lẩn khuất đâu đó một cách tinh tế, ví dụ như khi bạn liên tục nhìn thấy một cụm từ hay nghe đi nghe lại một bài hát. Bạn sẽ không bị kìm hãm trừ khi bạn cho phép bản thân mình bị kìm hãm."),
                new TarotCard("Nine of Swords", R.drawable.nine_of_swords, 58, "Chín kiếm", "tuyệt vọng, khổ tâm, thất bại, trì hoãn, lo sợ có căn cứ, trói buộc", "Bạn sẽ cần phải đề phòng cảm giác bất an. Nếu bạn đang cảm thấy bất an, thì hãy xem xét liệu cảm giác đó có xuất phát từ chính bạn và do bạn hay không, hay liệu những thứ khác hoặc người khác trong cuộc sống bạn có đang góp phần gây ra mối lo âu này? Nếu vậy, hãy xem liệu có thể thay đổi một điều gì đó trong những mối quan hệ để khắc phục tình trạng này hay không. Khoảng thời gian hiện tại có thể có vấn đề gì đó khiến người khác cảm thấy giống như cuộc sống họ lẽ ra phải “tốt hơn” so với thực tại. Hãy cố gắng hài lòng và tận hưởng những điều bình dị mà cuộc sống đang trao cho bạn. Vẻ đẹp luôn toát ra từ những điều bình thường nhất.", "Việc lo lắng về công việc sẽ không giải quyết được gì. Hãy tập trung vào những gì bạn có thể kiểm soát được, và cố gắng loại bỏ cảm giác lo lắng khỏi tâm hồn. Nếu bạn đang trong giai đoạn tìm kiếm công việc, vậy thì hãy suy nghĩ tích cực lên, và đừng ngần ngại yêu cầu sự giúp đỡ trong việc tìm kiếm nếu cần thiết. Người tiếp theo bạn nói chuyện sẽ có thể là người biết đến một cơ hội làm việc phù hợp cho bạn.","Có một điều gì đó đang khiến bạn cảm thấy bất an về mối quan hệ tình cảm hiện tại. Đừng phớt lờ cảm giác đó. Nếu bạn đang cảm thấy có điều gì đó không lành mạnh về mối quan hệ của của mình, vậy thì rất có thể bạn đã cảm thấy đúng. Nếu bạn nghi ngại rằng có điều gì đó đang bị che giấu, cách tốt nhất là hỏi người yêu của bạn về mối nghi ngờ này. Ngoài ra, nếu gần đây bạn đã gặp một người mới mà bạn cảm thấy thích, hãy chú ý đừng quá bị lôi cuốn và ngập chìm trong niềm tin rằng mọi thứ đều tốt đẹp giống như vẻ ngoài. Sự tin tưởng luôn cần thời gian để bồi đắp. Đừng quá vội vã","Bạn có thể đang ở trong hoàn cảnh phải bớt xén chi tiêu cho khoản này để đắp vào chi phí của khoản kia. Nếu vậy, hãy chậm lại và suy xét tỉ mỉ về điều gì đang diễn ra trong hoàn cảnh tài chính của bản thân. Bạn có đang tự thân vận động quá mức? Không ai trong chúng ta đơn độc cả. Hãy tìm kiếm những nguồn lực và giúp đỡ sẵn có để giúp bạn nếu bạn cần và cảm thấy chúng hữu ích. Đừng lạm dụng những trò may rủi hoặc mạo hiểm với những kế hoạch làm giàu nhanh. Điều kỳ diệu thực sự sẽ xảy ra, nhưng đừng hành động ngu ngốc. Nếu bạn cảm thấy nghi ngờ trước khi đầu tư, hãy nêu ra câu hỏi.","Đây là thời điểm nên rất cẩn thận trong việc sử dụng các chất kích thích hay chất cồn, vì mặc dù bạn có thể nghĩ rằng đó là cách để bạn “tự điều trị” cho mình, nhưng trên thực tế những gì bạn cần làm chỉ là giải quyết những gì đang gây khó chịu cho bạn. Sự căng thẳng có thể là một vấn đề lớn. Hãy xem xét những cách thức làm dịu đi những yếu tố rủi ro và giải quyết căng thẳng. Nếu mọi nỗ lực đều thất bại, hãy suy nghĩ về việc thay đổi lối sống và những thứ mà bạn đang tiêu tiền vào đó. Đôi khi sự căng thẳng thật ra chỉ đến từ vấn đề tiền bạc hay cuộc sống dưới nhiều hình thức hơn là xuất phát từ mối lo ngại đại loại như bị cướp giật.","Khi lá bài 9 of Swords xuất hiện trong bối cảnh câu hỏi về tinh thần, đây là dấu hiệu rõ ràng rằng bạn phải chậm lại việc thúc ép trí não và giảm mức độ căng thẳng. Nếu bạn có thể thiền định thì bạn sẽ nhận thấy rất nhiều ích lợi. Nếu bạn không có ý tưởng gì về cách bắt đầu như thế nào, hãy đọc sách và tham dự một lớp học tĩnh tâm. Hãy tìm đọc các khái niệm triết lý về tinh thần."),
                new TarotCard("Ten of Swords", R.drawable.ten_of_swords, 59, "Mười kiếm", "lụi tàn, phá hủy, tiêu tan, đau khổ, cô đơn", "Nhìn chung, khi lá 10 of Swords xuất hiện liên quan đến câu hỏi như liệu bạn có thể tin lời của một người nào đó nói với bạn không, hoặc tin rằng một hoàn cảnh nào đó sẽ ổn hay không, thì đây sẽ là một câu trả lời hoàn toàn rõ ràng là KHÔNG. Tuy nhiên, hãy luôn ghi nhớ trong đầu rằng bất cứ khi nào một cánh cửa khép lại, thì cánh cửa khác sẽ được mở ra. Thông điệp của lá bài cũng chính là lời hướng dẫn để bạn tiến theo hướng tốt hơn. Hãy tin vào chính mình, tin vào vũ trụ.","Sự xuất hiện của lá bài này có thể hàm ý rằng hoàn cảnh công việc của bạn sắp đến hồi kết thúc. Tốt nhất là bạn nên cố gắng sắp xếp mọi việc diễn ra suôn sẻ và nghĩ thông suốt về điều bạn sẽ làm nếu bạn thực sự mất việc, vì bất kỳ lý do nào. Bạn có thể đã bị đối xử tồi tệ bởi một ai đó. Đừng lãng phí thời gian và công sức của mình để mưu tính việc trả thù. Hãy chuẩn bị cho giai đoạn tiếp theo của cuộc đời bạn. Nếu bạn đang tìm kiếm việc, vậy thì một vị trí công việc tiềm năng mà bạn đang đặt kỳ vọng có thể sẽ không thành. Trên hết, hãy cố gắng và đừng từ bỏ.","Liên quan đến câu hỏi tình yêu, đáng tiếc lá bài 10 of Swords không phải là một dấu hiệu tốt. Có điều gì đó trong tình yêu của bạn đang không thật sự ổn. Hãy tự hỏi bản thân mình các câu hỏi, chấp nhận thực tế, không nhắm mắt trước sự thật. Bạn không thể thúc ép một ai đó yêu bạn hay đối xử với bạn một cách trân trọng được đâu. Nếu bạn mới gặp ai đó mới gần đây (và bạn đang tìm kiếm tình yêu) thì hãy tiếp tục xem xét và cân nhắc. Có khả năng những người bạn đã gặp cho đến lúc này không giống như bạn nghĩ đâu.","Xét về vấn đề tiền bạc, bạn có thể đang đối mặt với sự thất vọng. Đây không phải thời điểm để mạo hiểm, nhưng là thời điểm bạn có thể xem xét mua cổ phiếu. Nếu tình hình tiền bạc đang có xu hướng đi xuống, hoặc một ai đó bạn đã tin tưởng đã không giữ lời, vậy thì hãy cẩn thận để không làm tổn thương bản thân mình. Đây chưa phải là kết thúc. Hãy thử suy nghĩ logic và sáng suốt.","Sức khỏe của bạn có thể không tốt như bạn đã hi vọng. Hãy cẩn thận với điều mà thầy thuốc của bạn nói và với phương pháp chữa bệnh mà bạn tin tưởng. Đừng ngần ngại viện tới các phương án chữa lành mà bạn có thể nghĩ tới, năng lực tinh thần, vũ trụ, ông bà tổ tiên, nguồn sức mạnh cao hơn, Thượng Đế, bất kể điều gì bạn có thể liên hệ tới. Cuộc sống không chỉ có những thứ nhìn thấy được bằng mắt thường, và phép màu vẫn xảy ra mỗi ngày. Tuy nhiên, bạn phải chắc chắn rằng bạn đang làm những gì cần làm. Bạn có thể làm gì vào lúc này để giúp tình hình sức khỏe của bạn tốt hơn? Hãy xem xét thêm những phương pháp chữa lành thay thế.","Rốt cuộc thì bạn cũng có thể duy trì được một niềm tin về tinh thần mà bạn đã kiên định tin theo trước giờ. Tuy nhiên, bạn phải cẩn thận với việc đặt niềm tin của mình vào các vị lãnh đạo tinh thần vốn trông quan thì rất quan tâm đến quyền và lợi ích của bạn nhưng thực sự lại chỉ muốn làm dày túi tiền của họ. Bạn đang đột phá so với quá khứ. Đây thực sự là điều tốt, dù nó có thể gây đôi chút tổn thương."),
                new TarotCard("Page of Swords", R.drawable.page_of_swords, 60, "Tiểu đồng kiếm", "thông minh, linh hoạt, nhìn xa, mơ hồ khó đoán, xảo quyệt, phù phiếm", "Nhìn chung, Page of Swords cho chúng ta biết rằng người đang hỏi bài có thể đang cố gắng quá sức, thức đẩy mọi thứ quá nhanh, hoặc nhìn chung đang tức giận một ai đó. Bạn có thể cần tìm hiểu ngôn ngữ cơ thể của người khác, và xem xét mức độ bạn có thể (hoặc không thể) chấp nhận lời phê bình có tính xây dựng từ người khác. Hiện tại có vẻ như bạn đang cảm thấy tràn trề năng lượng thể chất và tinh thần. Hãy sử dụng nó một cách khôn ngoan.","Liên quan đến câu hỏi công việc, lá Page of Swords có thể hàm ý rằng bạn sẽ có thể sớm giải quyết được một số bất đồng về công việc nếu bạn đang có việc làm. Nếu bạn đang tìm việc, bạn có thể cần cư xử khiêm tốn hơn trong các cuộc phỏng vấn để đạt được vị trí bạn đang mong muốn (nhưng đừng hạ thấp bản thân mình). Hãy cẩn trong những hợp đồng.","Trong những câu hỏi về tình cảm, lá bài Page of Swords hàm ý đến khả năng xuất hiện các xích mích nhẹ. Hãy biết lúc nào cần cương và lúc nào cần nhu, nhiều cuộc tranh cãi thật sự không đáng xảy ra (nếu nhu đối phương của bạn đang hành xử với bạn một cách tôn trọng). Hãy cho phép đối phương của mình có không gian riêng của người ấy. Đây là điều cốt yếu để có hạnh phúc. Nếu bạn chưa gắn kết tình cảm với ai đó, vậy thì hãy tìm kiếm và hẹn hò với một người phù hợp. Tình yêu sẽ không tự dưng từ trời rớt xuống đâu.","Liên quan đến những câu hỏi về tiền bạc, hãy cẩn thận đừng cho rằng mình biết nhiều hơn thực tế. Lá Page of Swords có thể hàm chỉ việc lướt qua bề mặt của vấn đề. Đừng ngần ngại yêu cầu giúp đỡ từ những người có chuyên môn nếu bạn cảm thấy bạn hoàn toàn không biết được về điều bạn đang làm xét trên khía cạnh vấn đề tiền bạc. Đừng vay mượn quá mức vượt khả năng chi trả của bản thân và cho rằng bạn sẽ kiểm soát được vấn đề sau đó. Bạn sẽ hối tiếc nếu làm vậy.","Khi lá Page of Swords xuất hiện liên quan đến những vấn đề về sức khỏe, có nhiều khả năng là bạn đã làm việc quá nhiều, quá nặng, và hành xử quá điên rồ. Hãy cho phép bản thân và những người xung quanh bạn có cơ hội nghỉ ngơi, những dây thần kinh đang căng cứng của bạn cần được thư giãn. Sự tĩnh lặng và nghỉ ngơi là điều tuyệt vời lúc này. Nếu bạn vừabắt đầu một chế độ luyện tập mới, thì đừng quá sức với bản thân.","Bạn có thể đang bỏ qua khía cạnh tinh thần của mình. Ngay cả nhà nhân loại học lý trí nhất trên thế giới cũng phải nhận ra rằng có những điều bí ẩn trên thế giới mà không nhất thiết phải hữu hình ngay trước mắt hoặc oha3i được nhận biết một cách logic. Hãy khám phá các hệ thống niềm tin tinh thần mà bạn thích, ngay cả nếu bạn không tin vào chúng. Hãy mở tâm trí bạn ra. Nếu bạn nghĩ bạn đã sẵn sàng mở lòng, hãy xem xét sâu hơn nữa và cởi mở hơn nữa."),
                new TarotCard("Knight of Swords", R.drawable.knight_of_swords, 61, "Kị sĩ kiếm", "năng động, thông minh, dũng cảm, hoang phí, dối trá, tàn bạo", "Nếu bạn đang chờ đợi tin tức từ một người hay một điều gì đó, vậy thì dường như bạn sẽ có được điều mình muốn nghe. Knight of Swords có thể đi kèm với rất nhiều hoạt động, như đi du lịch, gặp gỡ bạn bè, hoàn thành một điều gì đó. Bạn không có khuynh hướng như đang cảm thấy phải chờ đợi bất kỳ điều gì đến với bạn, như vậy là tốt. Hãy đi theo tiếng gọi của con tim.","Khi lá Knight of Swords xuất hiện trong trải bài liên quan đến những câu hỏi về công việc, thì đây là một dấu hiệu đặc biệt tốt nếu bạn đang tìm kiếm việc. Một công việc mà bạn đang mong mỏi có thể đột nhiên đến với bạn. Nếu hiện tại bạn đã đi làm, bạn sẽ thấy bận rộn hơn đôi chút, theo hướng tích cực. Bạn sẽ hoàn tất được nhiều thứ đến nỗi khiến cho những người ngang hàng với bạn trở nên thật kém cỏi. Đó chủ yếu là vấn đề của họ, nhưng đừng khó chịu về điều đó.","Bạn có thể đang sắp yêu một ai đó, dù bạn là nam hay nữ. Nếu hiện tại bạn đang trong một mối quan hệ đã tình cảm gắn kết, vậy thì mối quan hệ này dường như sẽ trở nên tươi sáng hơn, sâu đậm hơn và thỏa mãn hơn. Nếu bạn đang tìm kiếm tình yêu, thì nó sẽ có thể đến nhanh hơn mong đợi của bạn nhưng bạn phải giữ liên lạc với người vốn sẵn sàng mở cửa trái tim để đón bạn vào vườn hồng tình yêu. Hãy sửa soạn vẻ ngoài cho phù hợp và hẹn hò với người ấy. Bạn sẽ tìm được rất nhiều niềm vui.","Về tiền bạc, hoàn cảnh hiện tại của bạn trông như đang rất lạc quan khi lá Knight of Swords xuất hiện. Bạn có thể nhận được một số tiền ngoài dự kiến. Nhưng nhớ là đừng tiêu hết chúng vào một nơi. Tái đầu tư sẽ là lựa chọn tốt hơn. Hãy tiếp tục làm những gì bạn đang làm. Chia sẻ sự sung túc của bạn theo bất kỳ cách nào bạn có thể.","Knight of Swords hàm ý một tin tức tương đối tốt về sức khỏe, nhưng bạn có thể được khuyên nên nghĩ chậm lại một chút. Hãy làm theo lời khuyên này. Knight of Swords có một yếu tố năng lượng và hành vi tương đối phức tạp. Tuy vậy, việc tìm hiểu kỹ về nguyên nhân khiến bạn không được khỏe có thể sẽ đem lại nhiều ích lợi cho bạn. Bạn có thể kiểm soát nguồn năng lượng chữa lành của chính bạn vào lúc này.","Trong những câu hỏi về tinh thần, sự xuất hiện của Knight of Swords hàm ý một nhu cầu cần không gian tĩnh lặng và nơi chốn phù hợp để đào sâu kiến thức tinh thần của bản thân. Rất khó có thể tăng cường được khả năng tâm linh nếu bạn cứ đi loanh quanh không định hướng. Hãy nhớ đến một trong những xác quyết vĩ đại nhất của thế giới: “Hãy vững tin” (Mark 4 : 49)"),
                new TarotCard("Queen of Swords", R.drawable.queen_of_swordspng, 62, "Hậu kiếm", "cô đơn, buồn bã và gai góc, bối rối, lừa dối, kiện tụng","Nhìn chung Queen of Swords có thể hàm ý rằng bản năng của bạn đang hoạt động rất tốt. Bạn đã biết điều mình muốn và cần, giờ đây bạn chỉ phải theo đuổi chúng. Tuy nhiên hãy ý thức về những giới hạn của người khác khi bạn làm vậy. Lá Queen này mang dòng năng lượng mạnh mẽ mà một số người sẽ cảm thấy như bị làm phiền.","Ngay cả khi bạn có thể biết rất rõ ràng về những gì bạn muốn xảy ra trong môi trường làm việc, nhưng đây vẫn không phải là thời điểm để truyền đạt những quan điểm của bạn. Đừng hành động thái quá với bất kỳ sự xem thường hoặc thiếu tôn trọng. Nếu bạn đang tìm kiếm việc làm, thì hiện tại bạn đang trong giai đoạn tốt để thể hiện bản thân mình. Hãy cậy nhờ những người phụ nữ trong cuộc sống bạn để chỉ bạn đi đúng hướng.","Về tình yêu, lưu ý duy nhất về lá Queen of Swords là hãy cẩn trọng với những cơn bốc đồng quá mạnh mẽ. Tất cả chúng ta đều được dạy rằng chúng ta sẽ gặt hái được nhiều điều tốt đẹp hơn nếu chúng ta cư xử tử tế, và quan điểm đó hoàn toàn đó đúng trong trường hợp này. Mẹ của bạn hoặc một người giống mẹ của bạn có thể ngăn cản mối quan hệ của bạn, cho dù bà ấy có muốn hay không. Hãy tìm hiểu xem giới hạn của bạn nằm ở đâu với vấn đề này, và hãy cho người phụ nữ ấy biết.","Về tiền bạc, Queen of Swords có thể cho thấy rằng một người phụ nữ sẽ đóng vai trò quan trọng trong chuyện tài chính của bạn. Sẽ có nhiều thứ bạn có thể học hỏi từ cô ấy, nếu bạn có thể mở lòng đón nhận những gì cô ấy nói.","Bạn có thể đang kìm nén cảm xúc của mình, và điều này sẽ ảnh hưởng tiêu cực đến cơ thể của bạn. Bạn vẫn phải cần thận với cách thức, thời gian và nơi mà bạn có thể “xả” những phản ứng cảm xúc của mình. Bất cứ điều gì có thể giảm được căng thẳng của bạn đều có ích cho bạn, ngay cả việc đi bộ 5 phút mỗi ngày. Hãy dừng lại, bình tĩnh và đưa ra lựa chọn hợp lý.","Queen of Swords có thể hàm ý rằng những cảm xúc của bạn là cánh cổng để mở ra cuộc sống tinh thần của bạn. Hãy suy nghĩ về điều bạn đang cảm thấy và lý do dẫn đến suy nghĩ này. Sau đó dừng suy nghĩ và cảm nhận. Việc viết nhật ký giấc mơ có thể là một quyết định đặc biệt sáng suốt vào lúc này. Hãy cởi mở với những thông điệp từ tinh thần mỗi ngày. "),
                new TarotCard("King of Swords", R.drawable.king_of_swords, 63, "Vua kiếm", "thông tuệ, quyền lực, tham vọng, cố chấp thù hận, độc ác, xấu xa, ác ý", "Nhìn chung, King of Swords là dấu chỉ một nguồn năng lượng rất mạnh mẽ. Bạn không thể thay đổi suy nghĩ của một người đàn ông trong cuộc sống bạn hoặc khiến ông/anh ta làm điều bạn muốn. Bạn có thể cần một thay đổi lớn về hành vi, suy nghĩ, và/hoặc những kỳ vọng của mình. Đừng làm gì quá tầm với của mình.", "Một người đàn ông trong môi trường làm việc hiện tại của bạn có thể hành xử hoặc nói chuyện với bạn theo cách bạn cảm thấy khó chịu, nhưng sự thật có thể là do bạn quá nhạy cảm mà thôi. Anh ta chỉ làm việc của anh ta. Nếu bạn đang tìm việc, người sẽ thuê bạn có thể là nam giới, nhưng bạn sẽ phải gây ấn tượng với ông/anh ta theo mọi mức độ để được nhận việc. Hãy biết vạch ra giới hạn cho mình, đừng quá vội vã mà làm mếch lòng người khác.", "Trong mối quan hệ yêu đương, King of Swords có thể hàm ý đến một người đàn ông có chính kiến và mạnh mẽ có thể đóng một vai trò nào đó trong mối quan hệ này. Nếu người đàn ông này là người yêu bạn, nó có nghĩa rằng bạn sẽ phải chấp nhận anh ấy như những gì chính anh ấy có, và cho dù bạn là nam hay nữ, bạn sẽ đối xử tốt nhất với anh ấy bằng cách phát huy phần nữ tính về phía bạn. Tuy nhiên, cũng đừng chịu đựng nếu bạn bị ngược đãi. Người đàn ông này có thể đang chơi vơi giữa bản chất đàn ông và vượt quá các giới hạn của mình. Hãy biết tự bảo vệ mình.", "Khi đề cập đến tài chính, King of Swords cho biết rằng bạn cần hiểu rõ và đối mặt với thực tế về hoàn cảnh tài chính của mình. Việc giấu diếm sẽ không giải quyết được gì. Lá bài cũng hàm ý kêu gọi bạn hào phóng với người khác. Bạn có thể chưa cảm thấy sẵn sàng để hào phóng, nhưng bạn nên làm như vậy. Phần thưởng mà bạn nhận được sẽ nhiều hơn giá trị bạn cho đi.", "Nếu bạn đang có những triệu chúng không rõ ràng, hãy nhận thức rằng bác sĩ, đặc biệt nếu họ là bác sĩ tây y, sẽ đề xuất được nhiều phương pháp xét nghiệm tốt hơn. Hãy trang bị cho mình đủ kiến thức trước khi bước vào phòng bác sĩ. Hãy hợp tác tốt với các liệu trình điều trị của bác sĩ hay người trị liệu dành cho bạn.", "Để bạn cảm thấy được kết nối để thiết lập niềm tin tinh thần, chúng phải tạo được ý nghĩa với bạn về khía cạnh tinh thần. Bạn có thể đắm chìm vào việc nhận thức tinh thần và luyện tập bằng cách tích cực tìm kiếm những nguồn thông tin và hiểu biết mới. Hãy thử những điều mới mẻ, thậm chí một giải bài tarot bạn thực hiện cho chính mình cũng đôi khi giúp bạn thông suốt hơn. Việc thực hành suy ngẫm tích cực cũng hữu ích vào lúc này."),
                new TarotCard("Ace of Pentacles", R.drawable.ace_of_pentacles, 64, "Ách tiền", "mãn nguyện, hài lòng, vui sướng, có tin tức xấu, ám ảnh tiền bạc", "Có lẽ bạn đang cảm thấy tràn đầy sức sống, rất háo hức và ngập tràn hy vọng về tương lai. Mọi thứ nhìn chung sẽ tiến triển tốt. Bạn sẽ sớm bắt đầu một điều gì đó mới mẻ một cách hoàn hảo, có thể liên quan đến công việc hay cuộc sống cá nhân, nhưng dù là thuộc về khía cạnh nào đi nữa thì đó cũng là một khởi đầu mới nhiều hứa hẹn."," Có thể bạn sẽ chuyển công tác sang một vị trí khác, hoặc tiếp nhận những trách nhiệm mới. Nếu bạn đang phân vân xem có nên rời bỏ công việc hiện tại hay không thì đây là một lá bài mang tính khuyến khích đầy tích cực. Sự lựa chọn vị trí công việc mới cũng có thể sẽ xuất hiện dù cho bạn có đang tìm kiếm hay không. Dù sao nhìn chung thì đây vẫn là tin tức tốt.","Nếu bạn đang có một mối quan hệ tình cảm mà hai bên đã hứa hẹn với nhau về tương lai, vậy thì đây là thời điểm mối quan hệ đó có thể đạt đến tầm cao mới trong không bao lâu nữa. Nếu bạn vẫn đang tìm kiếm tình yêu cuộc đời, vậy thì đã đến lúc bước ra ngoài và gặp gỡ mọi người, tìm kiếm những khởi đầu mới đầy hứa hẹn về khả năng trở thành tình yêu của bạn. Hãy sẵn sàng. Hãy nghĩ tích cực. Hãy tận hưởng niềm vui.","Tiền bạc có vẻ đang dư dả đối với bạn khi bạn rút được lá bài này. Bạn sẽ có thể trả hết các khoản nợ nếu có và có điều kiện chia sẽ sự đủ đầy về tài chính của mình với những người kém may mắn hơn bạn. Tuy vậy thì đây không phải là lúc để tiêu xài hết mọi thứ bạn có đâu. Hãy luôn nghĩ đến khả năng “cháy túi” trong tương lai. ","Đây là lúc tốt nhất để thử nghiệm một phương pháp chăm sóc sức khỏe cho bản thân nhất là mảng chăm sóc phòng ngừa. Tập thể dục, ăn uống lành mạnh và đảm bảo rằng bạn ngủ đủ giấc cũng là những phương pháp thông thường mà bạn có thể áp dụng. Bạn có thể tìm thấy được nhiều thông tin bổ ích về sức khỏe. Đừng ngại xem xét thử áp dụng những ý kiến mới về chăm sóc sức khỏe, nếu bạn thấy cần thiết. Nhìn chung, tình trạng sức khỏe của bản thân bạn đang khá hứa hẹn đấy.","Hãy thử những phương pháp ổn định tinh thần mới mẻ. Đi thăm viếng một nơi thờ tự khác, và nếu bạn luôn giữ quan điểm cá nhân về lĩnh vực tinh thần thì hãy thử tìm đến những người có cùng suy nghĩ như bạn."),
                new TarotCard("Two of Pentacles", R.drawable.two_of_pentacles, 65, "Hai tiền", "thay đổi, cân nhắc, cân bằng, bối rối, khó khăn, thiếu kiểm soát", "Có hai vấn đề chính là điều quan trọng mà bạn cần lưu ý  trong tâm trí. Bạn cần ngưng việc chú tâm vào quá nhiều thứ. Làm nhiều việc không phải là điều phù hợp với bạn lúc này, có thể bạn sẽ cần cắt giảm đi bớt những gì bạn đang làm để chỉ tập trung vào hai mối ưu tiên chính nào đó mà thôi. Tình hình tài chính của bạn có thể cần phải được cân bằng lại, và hãy đảm bảo rằng bạn tập trung chú ý vào điều đó.","Có thể đây là lúc bạn cần xem xét việc chấp nhận rủi ro. Bạn có thể giới hạn cơ hội hay chọn lựa mà bạn muốn đón nhận, nhưng bạn không thể hoàn toàn tránh được nếu những chọn lựa đó xuất hiện. Đừng rối lên như gà mắc tóc. Hãy dành sự tập trung nhiều nhất vào duy nhất vào hai nhiệm vụ chính.Nếu bạn đang tìm việc, hãy tìm kiếm hai nơi tiềm năng nhất để nộp hồ sơ và chuẩn bị kỹ càng cho buổi phỏng vấn hơn là phân tán cơ hội bằng cách gửi CV đi khắp nơi mà thiếu sự chăm chút và tìm hiểu kỹ về nơi ứng tuyển.","Nếu bạn hiện tại đang trong một mối quan hệ tình cảm mà hai bên đã hứa hẹn cùng nhau, thì có khả năng bạn và người yêu nhiều khả năng đang gặp chút rắc rối trong việc dành thời gian và sức khỏe cho nhau. Hãy đảm bảo rằng mối quan hệ của các bạn vẫn luôn được duy trì ở mức độ ưu tiên cao trong cuộc sống hàng ngày; nếu không thì người ấy sẽ không còn ở đó khi bạn quay lại tìm đâu. Còn nếu như bạn đang tìm kiếm tình yêu thì đừng tự huyễn hoặc bản thân rằng bạn đã sẵn sàng nếu thật sự bạn vẫn chưa hề sẵn sáng cho một mối quan hệ nghiêm túc. Bạn không cần phải là một người hoàn hảo ngay từ đầu nhưng bạn sẽ cần phải sẵn sàng dành thời gian, không gian và sức khỏe cho sự lãng mạn của tình yêu.","Hãy chú ý đến kết quả. Hãy đảm bảo rằng bạn hiểu rõ tình hình tài chính của bản thân. Có thể bạn cần tạo cho mọi thứ sự cân bằng, ví dụ như tiêu xài bớt đi hay dành ra nhiều tiên hơn để lo cho nguồn tài chính cá nhân trong tương lai. Đây là thời điểm tốt để đầu tư (một cách khôn ngoan). Nhưng đây không nhất thiết phải là thời điểm tốt để tiêu xài hay đầu cơ.   ","Bạn cần phải cân bằng các nỗ lực dành cho sức khỏe với trách nhiệm bản thân trong đời sống cá nhân và trách nhiệm trong công việc chuyên môn. Hãy đảm bảo rằng bạn dành thời gian cho cuộc sống của chính bản thân, nghỉ ngơi phù hợp, thư giãn, và chữa bệnh. Nếu bạn xếp bản thân mình dưới cùng trong danh sách những điều cần dành thời gian chăm sóc thì chắc chắn tình trạng sức khỏa của bạn sẽ trở nên tồi tệ. Hãy bắt đầu ngay. Cần nhớ rằng mục tiêu của bạn là tiến triển dần dần chứ không phải là ngay lập tức trở nên hoàn hảo.", "Những trách nhiệm to lớn có thể làm bạn cảm thấy như thể bạn không có thời gian để lo cho khía cạnh tinh thần của bản thân nữa. Nhưng hãy thử suy nghĩ lại đi. Có thể đúng là bạn không có nhiều thời gian, nhưng thậm chỉ chỉ 5 phút đi bộ giết thời gian để chiêm nghiệm là một lựa chọn tốt, còn hơn là không làm gì. Hãy nhớ rằng bạn phải cung cấp chất dinh dưỡng không những cho cơ thể, tâm trí, mà còn cho cả tinh thần nữa thì mới thật sự cân bằng."),
                new TarotCard("Three of Pentacles", R.drawable.three_of_pentacles, 66, "Ba tiền", "công việc thực tại, hợp tác, tạo dựng, vinh quang, nhỏ nhen, trẻ con, yếu đuối, bảo thủ", "Lá 3 of Pentacles cho bạn biết rằng những người xung quanh bạn đánh giá rất cao bạn và những nỗ lực của bạn, cũng như nhận thấy được chất lượng công việc bạn làm ngay cả khi bạn không được trả lương cho những công việc đó. Lá 3 of Pentacles nhắc nhở bạn hãy tiếp tục duy trì và phát huy những gì đang làm vì bạn đang đi đúng hướng (mặc dù vẫn còn khả năng tiến triển hơn nữa). Bạn không cần phải loại bỏ hay chấm dứt điều gì đó rồi bắt đầu lại đâu.", "Lá 3 of Pentacles là một dấu hiệu hết sức tốt đẹp khi nói đến công việc cũng như những câu hỏi về sự nghiệp. Lá bài cho bạn biết rằng những gì bạn làm đang được đánh giá cao và nhìn chung được chú ý khá nhiều. Đó vốn là đặc điểm tôt để bạn phát huy chính mình. Cũng tương tự, nếu bạn đang tìm việc làm, vậy thì lá bài này là dấu hiệu cho biết rằng bạn hoàn toàn có thể kỳ vọng vào sự thành công trong thời gian sắp tới.", "Trong một trải bài về tình yêu, lá 3 of Pentacles cũng là một dấu hiệu tốt. Đặc biệt nếu bạn đã có ai đó để mà yêu thương và quan tâm, thì lá bài này đem lại thông điệp rằng người đó nhìn thấy ở bạn một ánh sáng vô cùng tích cực và rằng bạn là người hội đủ những tiêu chuẩn mà họ đang tìm kiếm, mặc dù tình yêu vẫn chưa phát triển. Vì vậy, hãy kiên nhẫn. Nếu bạn chưa có người yêu nhưng đang tìm kiếm, vậy thì lá bài này cho biết rằng bạn nhiều khả năng sẽ gặp người đó trong quá trình làm việc (của bạn hoặc của họ). Hãy luôn để ý và nhạy bén trong cuộc tìm kiếm này!", "Lá bài 3 of Pentacles là một dấu hiệu tốt khi xét đến khía cạnh tiền bạc. Lá bài có thể hàm ý rằng cơ hội của bạn đang tới, cho dù vẫn kèm theo thông điệp cảnh báo rằng bạn vẫn sẽ phải làm nhiệm vụ của bạn. Đây không phải lá bài ám chỉ việc bạn sẽ nhận được “lộc trời cho” nhưng là lá bài về sự trả công và tưởng thưởng công bằng cho những gì bạn làm cũng như cho chất lượng của những việc đã làm đó. Hãy giữ thái độ tích cực, luôn luôn nỗ lực, rồi thì phần thưởng sẽ đến. Mọi thứ sẽ diễn biến tốt đẹp.", "Sức khỏe của bạn có khuynh hướng được cải thiện một cách tích cực, không phải như một phép lạ, mà đó là kết quả của những gì bạn đang làm để nâng cao sức khỏe bản thân mình. Do vậy, lá bài này xuất hiện trong trải bài sức khỏe chính là dấu hiệu cho thấy bạn đang đi đúng hướng, và hãy tiếp tục duy trì, phát huy nhé.", "Hãy thử xem xét tìm hiểu những tổ chức, cộng đồng thời xa xưa cũng như những truyền thống cổ xưa, bạn sẽ có thể chiêm nghiệm ra rất nhiều điều đấy. Bạn không nhất thiết phải tham gia vào những tổ chức hay cộng đồng nào đó trừ khi bạn thật sự muốn. Thực tế thì những kiến thức, sự chiêm nghiệm này có thể nhận thấy được đơn giản bằng cách tìm đọc và suy niệm về hệ thống niềm tin của những nơi đó. Hãy tìm hiểu về các nghi lễ trong quá khứ, chúng sẽ giúp bạn đào sâu thêm kiến thức và trải nghiệm tinh thần của bản thân mình."),
                new TarotCard("Four of Pentacles", R.drawable.four_of_pentacles, 67, "Bốn tiền", "đảm bảo vật chất, quyền lực, chướng ngại vật, chậm trễ, đối lập và lo âu", "Nhìn chung, lá 4 of Pentacles có thể là dấu hiệu cho thấy người được giải bài (querent) đang có những lo lắng/lo sợ về vấn đề tiền bạc và/hoặc dính vào điều gì đó khiến cho họ cảm thấy không an toàn. Điểm mấu chốt cần nhận thực trong trường hợp này là sự lo lắng và sợ hãi KHÔNG HỀ đem lại cho bạn sự an toàn. Nếu có lá bài nào trong lượt trải khiến bạn liên tưởng đến câu nói từ xa xưa “Nếu bạn yêu ai, hãy để anh/cô ấy tự do…”, vậy thì đó đúng là điều mà bạn nên làm: quên đi nỗi sợ và lo lắng.", "Khi xuất hiện trong bối cảnh câu hỏi về công việc, lá 4 of Pentacles cho thấy có vẻ như bạn đang làm điều gì đó mà bạn không thích chỉ vì công việc đó đem lại nguồn thu để bạn đủ trang trải cuộc sống. Công việc và hoàn cảnh trong trường hợp này là sự bổ khuyết cho nhau, nhưng để cải thiện tình hình, bạn cần phải đánh giá cân nhắc những suy nghĩ về tinh thần và niềm tin của chính mình. Hãy phân tích từ những suy nghĩ của bạn xem bạn đang muốn gì? Bạn cần làm gì để đạt được (những) mục tiêu đó? Hãy tin rằng bạn có thể làm được, và bắt tay vào những gì bạn cần làm thôi.", "Lá 4 of Pentacles là lá bài của sự sợ hãi, ngần ngại và về cơ bản thì những cảm giác này có liên quan đến tình yêu. Có thể là bạn hoặc người yêu bạn đang cố “kiểm soát” người kia quá nhiều, hoặc cũng có thể đang có một người nào đó khác đang muốn kiểm soát bạn (trong vần đề tình cảm). Việc kiểm soát hay “lụy tình” này rất không tốt, vì trong tình yêu đích thực mọi cặp đôi phải luôn dành cho nhau đủ không gian riêng, và người kia không bao giờ được phép “kiểm soát” người còn lại dù với bất cứ lý do gì. Nếu bạn vẫn còn đang độc thân và nghĩ rằng bản thân mình đã sẵn sàng cho một mối quan hệ nghiêm túc, vậy thì hãy xác định tư tưởng rằng bạn cần bước chân ra khỏi “vùng an toàn” của mình để có thể khiến mối quan hệ đó đến với bạn. Hãy nhớ rằng tình yêu đích thực không rơi từ trên trời xuống bao giờ.", "Bạn đang có một số cảm giác bối rối, lo lắng về chuyện tiền bạc và đang cố tiết kiệm từng đồng. Cảm giác này hoàn toàn có thể hiểu được. Tuy vậy hãy nhớ rằng luôn có một ranh giới rõ ràng giữa cảm giác lo sợ hết tiền bạc, tính toán chi tiêu hợp lý và thận trọng quá mức về việc tiêu xài. Bạn có thể loại trừ nỗi lo ngại thái quá về tiền bạc không cần thiết bằng cách tâm niệm rằng dù sao đi nữa thì bạn vẫn có điều kiện tài chính tốt hơn nhiều người khác. Hãy thử cho đi một ít số tiền bạn đang có bằng một trái tim rộng mở, ngay cả khi số tiền đó chỉ đáng vài nghìn lẻ. Thông thường thì khi cho đi, bạn sẽ được trải nghiệm cảm giác thoải mái về sự phồn vinh mà mình đang có (vì bạn đang may mắn và giàu có hơn những người bạn cho tiền), từ đó tái tạo lại cảm giác an toàn tài chính trong bạn. Tất cả chúng ta đều cùng sống trong cuộc đời này mà!", "Lá bài 4 of Pentacles có thể là dấu hiệu nhắc nhở bạn buông bỏ những thói quen sai lầm và dễ gây tổn thương bản thân để có thể tiến về phía trước. Lá bài cũng có thể là lời cảnh báo rằng có những người khác đang cố kìm hãm bạn cũng như gây phương hại đến sức khỏe bạn bằng những cách không đem lại ích lợi gì cho bạn hay cho chính họ. Hãy giúp những người thân của bạn hiểu ra sự thật rằng không phải mọi thứ ta đều có thể kiểm soát hết được. Thiền định và suy ngẫm quanh các chủ đề về sự tự do và thư giãn chính là một liều thuốc hữu hiệu ở thời điểm này.", "Đây là lúc bạn phải nhìn nhận bản thân mình một cách nghiêm túc về những gì bạn đang lo lắng hay sợ hãi, hay đang có bám víu vào điều gì. “Không dính líu” cũng là một hình thức giải thoát. Hãy tìm hiểu về các cơ quan năng lượng trong cơ thể người, suy ngẫm và thiền định xem những cơ quan đó trong cơ thể bạn đang hoạt động ra sao. Hãy mở lòng với thay đổi, vì thay đổi luôn diễn ra không ngừng. Hãy bắt đầu từ chính nơi bạn đang ở."),
                new TarotCard("Five of Pentacles", R.drawable.five_of_pentacles, 68,"Năm tiền", "lo phiền, khó khăn vật chất, rối loạn, hủy hoại, mất đoàn kết", "Nhìn chung, lá 5 of Pentacles đại diện cho cảm giác bị bỏ rơi trong lạnh giá như thể mọi người khác trên thế giới đang có mọi thứ còn bạn là kẻ duy nhất phải chịu đau khổ vậy. Hãy tỉnh táo nhận ra rằng mọi sự không phải như bạn nghĩ đâu. Hãy cố gắng lo liệu trong khả năng của mình với những gì bạn đang nắm giữ, và cũng hãy nhớ rằng một kẻ khôn ngoan sẽ biết cách đề nghị nhận được sự trợ giúp từ bên ngoài. Chỉ cần hỏi, sự hỗ trợ sẽ đến.", "Khi lá bài này xuất hiện trong câu hỏi về công việc, có hai khả năng hoặc công việc hiện tại của bạn đang là một gánh nặng hoặc bạn không thể tìm được việc làm, và tâm trí bạn như bị kẹt cứng. Hãy biết rằng bạn không cần thiết phải giam hãm mình trong một hoàn cảnh thiếu lành mạnh hay không đem lại ích lợi cho bản thân mình. Hãy có niềm tin vào chính mình và vũ trụ (vận mệnh) rằng mọi thay đổi và cải thiện đều có thể xảy ra. Bạn không cần phải tự mình làm mọi thứ. Nếu bạn muốn một vị trí công việc mới, hãy trao đổi với những người liên quan. Mọi chuyện sẽ được dần cải thiện. Hãy vững tin!", "Lá 5 of Pentacles không phải là một dấu hiệu của sự hạnh phúc khi xuất hiện trong câu hỏi tình cảm. Có thể bạn đang trao trái tim mình cho một người mà bạn cảm thấy không đối xử với bạn một cách phù hợp. Khả năng là bạn cần thôi ảo tưởng về người đó. Lá bài này gợi lên cảm giác bị bỏ rơi trong lạnh giá rất rõ ràng. Bạn cần nhớ rằng thế giới này không chỉ có một người duy nhất có thể sánh đôi cùng bạn, chia sẻ với bạn mọi đắng cay, ngọt bùi, và cùng bạn duy trì một mối quan hệ tình cảm tốt đẹp. Khi tình yêu đến đúng lúc với đúng người, tự thân nó sẽ diễn ra một cách tốt đẹp. Hãy mở rộng phạm vi suy nghĩ của mình theo hướng tích cực. Bạn không phải là kẻ không đáng được yêu!", "Có thể bạn sẽ trải qua cảm giác như bị mất sạch tiền. Hãy hít một hơi thật sâu và suy nghĩ lại. Bạn không hề cô đơn và không nhận được trợ giúp, ngay cả khi bạn không biết rõ bất cứ ai quanh bạn. Hãy suy xét kỹ lưỡng xem có những thứ gì, kỹ năng gì hay hoàn cảnh gì mà bạn có thể đã bỏ qua không xét đến không. Nếu bạn cần sự trợ giúp về mặt tài chính, vậy thì hãy vứt bỏ sĩ diện đi và đưa ra lời yêu cầu. Luôn có cách để giải quyết mọi vấn đề. Thời điểm hiện tại chỉ là tạm thời. Hãy chắc chắn rằng bạn đang nhận được những lời khuyên tốt và hiệu quả.", "Nhiều yếu tố gây căng thẳng khác có thể đang khiến các vấn đề sức khỏe nhỏ nhặt của bạn trở nên nghiêm trọng. Hãy đảm bảo rằng bạn có thể đánh giá được mọi thứ (cho dù bạn không có nhiều tiền và không có bảo hiểm y tế thì vẫn luôn có những cách điều trị phù hợp miễn là bạn kiên định), và nỗ lực hết sức có thể để kiểm soát từng vấn đề một. Thiền định và thư giãn là chìa khóa để cải thiện sức khỏe. Hãy thiền định, ngay cả khi bạn chỉ có thể làm được mỗi lần có 30 giây.", "Hãy nhắc nhở bản thân mình một thực tế rằng không gì tồn tại mãi mãi, nhất là nếu bạn đang trải qua một giai đoạn khó khăn như lá bài này hay hàm ý. Bạn cũng hãy luôn giữ lý trí về một thực tế rằng tất cả chúng ta đều có mối liên kết và rằng cho phép người khác giúp bạn cũng chính là trao cho họ (và cho bạn) một cơ hội nhận được niềm vui và ơn lành. Đừng khăng khăng làm hết mọi thứ. Hãy có niềm tin rằng mọi chuyện rồi sẽ tốt dần lên."),
                new TarotCard("Six of Pentacles", R.drawable.six_of_pentacles, 69, "Sáu tiền", "thành công sau nỗ lực, quà tặng, hài lòng, tự do, ghen ghét, đố kị", "Lá 6 of Pentacles là lá bài của sự hào phóng, hàm nghĩa rằng bạn sẽ là người được hướng hoặc là người cho đi một thứ gì đó có giá trị. Hãy chia sẽ những gì bạn đang có vì sẽ luôn có những người cần đến sự sẻ chia này. Sự hào phóng tất nhiên không nhất thiết phải luôn liên quan đến tài chính; một đôi tai biết lắng nghe, một sự ủng hộ về mặt tinh thần… là những thứ mà tiền cũng chưa chắc mua được. Và hãy nhớ đừng quá sĩ diện không đón nhận những gì được cho, nếu bạn chính là người được nhận.", "Trong công việc, có thể bạn đang nhận ra rằng mọi thứ đang chuyển biến theo hướng tốt hơn, gần như đã thoát khỏi giai đoạn khó khăn, tăm tối. Nhiều khả năng bạn đang nhận được sự ủng hộ của một người có quyền lực lớn nào đó. Lá bài này hàm chỉ đây là thời điểm tốt để bạn đưa ra lời đề nghị tăng lương hay tìm kiếm công việc có thể trả lương cao hơn cho bạn. Nói chung, công việc/sự nghiệp của bạn đang trải qua những ngày tháng rất tích cực. Nếu bạn đang tìm kiếm nguồn tài chính thông qua ngân hàng cho công việc kinh doanh của chính bạn, thì lá bài này là dấu hiệu dự báo rằng bạn sẽ thành công hơn là bạn tưởng. Hãy cứ tiến lên!", "Nếu bạn đã có một mối quan hệ tình cảm mà hai bên đã cam kết chuyện lâu dài, thì lá 6 of Pentacles là lời tiên đoán mối tình này của bạn sẽ vô cùng hạnh phúc, thoải mái, công bằng và tốt đẹp, ít nhất là trong một khoảng thời gian đáng kể. Hãy đón nhận những gì người yêu của bạn trao, dù là về khía cạnh vật chất hay tinh thần, đồng thời hãy dành lại cho người ấy những điều tốt đẹp nhất một cách công bằng, để mối quan hệ này luôn ở thế vững chắc. Nếu bạn đang tìm kiếm người sẽ gắn kết với bạn cả cuộc đời, thì sự xuất hiện của lá bài này là thông điệp cho thấy trong không bao lâu nữa bạn sẽ có thể gặp gỡ một người mới, và đó là người có suy nghĩ rất tích cực, tử tế và quảng đại. Mối quan hệ mới sắp tới đó có thể do một người nào đó mà bạn biết giới thiệu. Hãy chắc chắn là mọi người xung quanh bạn biết rõ bạn đang “tìm” người yêu.", "Lá bài 6 of Pentacles là một dấu hiệu đặc biệt tốt khi nói đến vấn đề tài chính.\n" +
                        "Điềm tốt này không thể hiện dưới dạng những may mắn trên trời rơi xuống như trúng số, mà thiên về những thành quả tài chính mà bạn xứng đáng được hưởng sau thời gian làm việc chăm chỉ. Sẽ có nhiều tiền hơn đến với bạn trong tương lai gần. Ở một mặt khác, lá bài này cũng là lời kêu gọi bạn hãy sẻ chia sự thịnh vượng của mình cho những người khác kém may mắn hơn. “Gieo nhân nào, gặt quả đó”. Hãy làm việc tốt và bạn sẽ được nhận lại phần của mình.", "Sự xuất hiện của 6 of Pentacles là dấu hiệu cho thấy sức khỏe và sức sống của bạn đang dần được cải thiện. Nếu bạn đang phải chống chọi với căn bệnh phức tạp nào đó, thì lá bài này cho thấy khả năng lớn trong thời gian tới bạn sẽ gặp được một chuyên gia trị liệu xuất sắc. Người này sẽ rất hữu ích trong việc giúp bạn vượt qua căn bệnh. Hãy luôn giữ suy nghĩ tích cực và đợi chờ điều tốt đẹp, đừng ngại ngần đưa ra lời yêu cầu được chăm sóc sức khỏe nếu bạn thấy cần, cho dù khả năng tài chính của bạn có hạn hay bạn không có bảo hiểm y tế đi nữa, vì bạn vẫn luôn có thể tìm thấy những sự trợ giúp phù hợp trong khả năng mình ở mọi nơi.", "á 6 of Pentacles là dấu hiệu khá rõ ràng yêu cầu bạn nên chia sẻ những hiểu biết về tinh thần của bạn cho những người khác. Có thể bạn chưa nhận ra nhưng thực tế là bạn có nhiều thứ để chia sẻ hơn là bạn nghĩ (về khía cạnh tinh thần). Cũng vậy, lá bài nhắc nhở bạn đừng giữ khư khư TẤT CẢ sự thật cho riêng mình biết, và bạn nên chắc chắn rằng bạn cũng đang học hỏi những kiến thức tinh thần khác từ những người khác xung quanh. Nói tóm lại, hãy luôn trò chuyện bất cứ khi nào bạn có thể; đừng giữ riêng cho mỗi bản thân mình. Cuộc sống của bạn sẽ trở nên có chiều sâu hơn rất nhiều nếu bạn biết cho đi. "),
                new TarotCard("Seven of Pentacles", R.drawable.seven_of_pentacles,70, "Bảy tiền", "thành công chưa thực hiện được, tiền bác, lợi nhuận, chờ đợi, lo lắng", "Nhìn chung, lá bài 7 of Pentacles đem lại một cảm giác an toàn hơn cho bạn trong một khoảng thời gian nào đó. Mọi thứ sẽ diễn biến tốt, và bạn sẽ tìm thấy niềm hy vọng vào tương lai với lý do tốt. Bạn có thể sẽ cần đưa ra quyết định xem nên đầu tư công sức vào những việc, những người, hay những hoàn cảnh mà bạn có liên quan nào đó, hay là sẽ dành sức lực cho những thứ đem lại nhiều trái ngọt hơn cho cuộc sống của chính bản thân mình. Hãy đưa ra một lựa chọn thật khôn ngoan.", "Công việc của bạn nhiều khả năng sẽ diễn ra một cách êm đẹp và bạn sẽ nhận được những phần thưởng hay thành quả cho những nỗ lực từ trong quá khứ. Thành quả này có thể đã đang đến với bạn rồi, nếu không thì nó sẽ đến trong tương lai rất gần.  Tình hình tài chính có vẻ sẽ khá tích cực, dù không giàu có đến mức “lăn trong đống tiền” nhưng chắc chắn bạn sẽ nhận được nhiều hơn trước.","Xét trong bối cảnh tình yêu, lá 7 of Pentacles là lời dự báo mọi thứ liên quan đến người bạn yêu sẽ diễn ra một cách tốt đẹp. Nhưng nếu bạn chưa có người yêu, thì có thể bạn cần phải nghĩ thoáng hơn và mở lòng cho những mối quan hệ tiềm năng khác. Không ai trong chúng ta chỉ có mỗi DUY NHẤT một người mà ta có thể gắn bó suốt cuộc đời. Đừng để bản thân rơi vào những cảm giác nghi ngại hay sợ hãi. Mọi thứ sẽ tốt lên thôi.", "Lá bài này là một dấu hiệu cực tốt xét trên khía cạnh tài chính. Bạn sẽ có một vụ đầu tư khôn ngoan nào đó, có thể là một trong những dạng đầu tư theo đúng nghĩa truyền thống, hoặc cũng có thể là đầu tư thời gian và sức lực. Dù vậy bạn cũng nên nhớ rằng đây không phải lúc đầu tư một cách may rủi. Thận trọng và suy nghĩ chín chắn về việc đầu tư những gì bạn có (nhất là những gì bạn vừa có) là chìa khóa để bạn có một quyết định thành công.", "Tình trạng sức khỏe hiện tại của bạn là kết quả của lối sống và hành xử trong quá khứ. Nhưng cải thiện sức khỏe vẫn là một nhiệm vụ không bao giờ quá trễ để làm. Những gì bạn gieo trồng hôm nay sẽ là những gì bạn đặt hái sau này. Hãy xem xét và đánh giá chính mình. Bạn nhận thấy mình đang có triệu chứng bị bệnh hay đang là một người hoàn toàn khỏe mạnh? Hãy nhớ cho dù thời gian và năng lượng của bạn có cạn kiệt vì cuộc sống thường ngày, thì bạn vẫn luôn có thể dành ra vài giây để suy nghĩ tích cực.", "Bạn sẽ được hướng đến sự thực rằng cuộc sống là một chuỗi luân hồi, và rằng những gì ta làm hôm nay chắc chắn sẽ đem lại kết quả/hậu quả trong tương lai. Hãy nhớ dằng khi bạn chắc chắn mình đang sống quảng đại bất kể xét về thời gian, năng lượng hay tiền bạc, thì đều là đầu tư cho tương lai của chính mình. Hãy biết cho đi những gì bạn có, và hãy đón nhận một cách biết ơn những gì bạn được cho."),
                new TarotCard("Eight of Pentacles", R.drawable.eight_of_pentacles, 71, "Tám tiền", "công việc, việc làm, kỹ năng thủ công, tham lam, khôn khéo, mưu đồ", "Nhìn chung lá bài 8 of Pentacles hàm ý những nỗ lực cần thiết. Bạn sẽ có nhiều thứ cần phải hoàn tất. Sự cống hiến và chú tâm vào các nhiệm vụ sắp tới là rất quan trọng (và đáng ngưỡng mộ), nhưng nếu bạn làm vì mục đích riêng thì sẽ có thể dẫn đến tác dụng ngược. Hãy suy xét rõ ràng về công việc bạn đang cố gắng hoàn tất. Hãy cố gắng duy trì sự cân bằng. Không ai có thể làm việc điên cuồng mọi nơi mọi lúc.", "Nếu bạn đang tìm kiếm công việc, thì lá bài này có thể hàm ý rằng bạn sẽ được tuyển dụng vào một vị trí mới vốn sẽ đòi hỏi nhiều kỹ năng của bạn và rất khắt khe. Hãy có niềm tin vào chính mình rằng bạn có thể làm được những gì cần thiết để dẫn đến thành công ở vị trí đó. Nếu bạn đã đang có công việc rồi, thì có khả năng bạn sẽ bị dồn nhiều trách nhiệm và nhiệm vụ hơn khả năng bạn có thể hoàn thành tốt. Đừng ngần ngại yêu cầu sự hỗ trợ được ưu tiên từ những người giám sát hay cấp trên. Bạn không thể làm việc quá khả năng được; hãy biết thực tế", "Nếu bạn hiện đang trong một mối quan hệ mà hai bên đã cam kết lâu dài, thì khối lượng công việc mà bạn hay người yêu của bạn đang phải gánh vác nhiều khả năng đang ảnh hưởng đến mối quan hệ của hai bạn. Hãy luôn chú ý dành đủ thời gian và không gian cho mối quan hệ của mình, không để dính đến công việc cá nhân, bằng không mọi thứ sẽ trở thành gánh nặng cho bạn. Nếu bạn đang trong giai đoạn tìm kiếm tình yêu, thì sự xuất hiện của lá bài này thường cho biết rằng bạn đang thực sự quá tập trung vào chuyện tiền bạc và lăn lộn trong cuộc sống đến mức khó có thể thực sự tạo ra đủ không gian cho một mối quan hệ mới. Hãy xem xét các ưu tiên của bạn và nhìn nhận bạn đang đầu tư thời gian và năng lượng của bản thân vào đâu. Nếu bạn muốn yêu, hãy chắc chắn rằng bạn để dành chỗ (thời gian và không gian) cho tình yêu đến.", "Lá bài 8 of Pentacles cho bạn biết rằng bạn có thể sẽ nhận được sự hỗ trợ tài chính mà bạn đang chờ đợi hoặc đang kỳ vọng. Tuy nhiên, đừng lãng phí nguồn tài nguyên này. Hãy thận trọng. Hãy suy nghĩ cho lâu dài. Đây không phải là lúc để đầu tư rủi ro hay mạo hiểm. Nên nhớ rằng tiền bạc đang đến với bạn ở hiện tại là kết quả từ công việc và những nỗ lực của bạn, không phải từ may mắn.", " Bạn có thể cần thực hiện vài hành động để cải thiện tình trạng sức khỏe của mình. Ngay cả khi bạn cảm thấy như thể chẳng có cách nào để cải thiện tình trạng sức khỏe hiện tại, thì thực tế vẫn có nhiều phương án mà bạn có thể chủ động làm để hỗ trợ chính bản thân mình. Hãy suy nghĩ xem những phương thức đó có thể là gì, và hãy làm theo. Mỗi hành động đều tạo ra một khác biệt. Hãy suy nghĩ tích cực lên.", "Hãy xem xét khía cạnh tinh thần của cuộc sống trong cuộc sống công việc của bạn cho dù nghề nghiệp của bạn có phải là một công việc truyền thống thông thường hay không. Tại sao bạn làm điều bạn đang làm? Bạn đang khơi tạo cho sự phát triển gì? Bạn sẽ nhận được nhiều lợi ích từ việc tích cực đọc nhiều sách về tinh thần và nói chuyện với những người mà có cùng niềm tin với bạn hơn là chỉ nói chuyện với chính bản thân bạn. Hãy cởi mở, và bạn sẽ nhận được nhiều lợi ích từ nó."),
                new TarotCard("Nine of Pentacles", R.drawable.nine_of_pentacles, 72,"Chín tiền", "khôn ngoan, an toàn, dối trá, niềm tin sai lệch", "Lá bài Nine of Pentacles là lá bài nói đến sự hạnh phúc và thành công, xét trên cả cuộc sống vật chất lẫn tinh thần của bạn. Bạn có thể tự hào về nhiều thứ. Hãy kỳ vọng điều tốt nhất sẽ xảy ra. Bạn sẽ ở vào vị thế giúp đỡ những người kém may mắn hơn bạn. Hãy chú trọng không những vào sự đủ đầy vật chất mà còn cả khía cạnh tinh thần nữa.", "Nếu bạn đang kiếm việc, lá bài Nine of Pentacles có thể là dấu hiệu cho biết rằng một công việc tốt hơn nhiều so với bạn mong đợi đang tìm đến với bạn. Ngay cả khi bạn đã đang có công việc rồi, thì bạn vẫn có thể kỳ vọng vào một vị trí mới tốt hơn, cả về điều kiện làm việc lẫn chế độ lương bổng. Sẽ có rất nhiều điều bạn cảm thấy biết ơn.", "Lá bài Nine of Pentacles là một lá bài tuyệt vời về tình yêu. Nếu bạn đang torng một mối quan hệ gắn kết với ai đó, vậy thì bạn có thể kỳ vọng rằng mối quan hệ này sẽ thăng tiến lên một nấc thang mới xét về sự thân mật và niềm vui. Còn nếu bạn đang tìm kiếm tình yêu, thì lá bài này cho bạn biết rằng có thể một người mới có khả năng đem đến cho bạn một tình yêu sâu đậm và ý nghĩa đang tìm đến với bạn. Hãy cười lên. Ai cũng thích nhìn thấy những cặp đôi hạnh phúc mà.", "Khi đánh giá tình hình tài chính của bạn, bạn cũng cần phải xem xét mức độ giàu có phồn vinh của bạn theo những hình thức khác ngoài tiền bạc. Sức khỏe của bạn có ổn không? Bạn có một cuộc sống gia đình hạnh phúc viên mãn không? Bạn cần phải tính cả những yếu tố đó trong quá trình tính toán sổ sách tài chính của mình. Xét đến một khía cạnh thiên về vật chất hơn, thì lá bài này cũng dự báo rằng chu kỳ tài chính của bạn cũng có thể được cải thiện, có lẽ hơn cả những trí tưởng tượng phong phú nhất. Hãy làm việc siêng năng, chăm chỉ. Bạn sẽ nhận được phần thưởng xứng đáng. Hãy chia sẻ những gì bạn đang có. Đó cũng là một yếu tố rất quan trọng.", "Lá bài Nine of Pentacles là một lá bài rất khích lệ về sức khỏe. Bạn sẽ cảm thấy tràn đầy sức sống, niềm hy vọng và lạc quan về tương lai. Nếu bạn đang chờ đợi các kết quả chẩn đoán sức khỏe, hãy yên tâm rằng nhiều khả năng đó là những kết quả rất tích cực. Hãy nghĩ đến lối suy nghĩ thông thường, và nhớ rằng cách bạn nghĩ cũng sẽ có những tác động đến sức khỏa của bạn. Hãy nghĩ tích cực hết mức có thể.", "Khi lá bài này xuất hiện trong bối cảnh câu hỏi về tinh thần, hẳn là bạn đang cảm thấy tràn ngập bình yên và hạnh phúc. Bạn cảm thấy lòng tự trọng của mình dâng cao hơn bao giờ hết, và đó là điều tốt. Ở vị thế của mình hiện tại, bạn sẽ trở thành người khuyên giải, tư vấn cho những người khác và chia sẻ những kiến thức về tinh thần mà bạn đã tích lũy được đến giờ. Hãy đi ra thế giới, sẻ chia niềm hạnh phúc bạn đang có."),
                new TarotCard("Ten of Pentacles", R.drawable.ten_of_pentacles, 73, "Mười tiền", "thịnh vượng, thành công, may rủi, mất mát, món quà", "Lá bài Ten of Pentacles nhìn chung cho bạn biết rằng bạn sẽ cảm thấy hạnh phúc và biết ơn vì nhiều thứ. Các mối quan hệ, tình hình tài chính, sức khỏe… tất cả sẽ diễn biến theo chiều hướng rất tốt. Hãy xem xét xem làm thế nào để bạn đạt được tất cả những điều này, và nỗ lực hết sức mình, cũng như đảm bảo rằng bạn chia sẻ những gì bạn có cho những khác kém may mắn hơn cho dù đó chỉ là vài lời khuyên tử tế cho một người đang cần an ủi. chúng ta sẽ nhận lại được những gì đã cho đi.", "Bạn sẽ được tăng lương bổng hoặc được thăng chức ngoài mong đợi khi lá bài Ten of Pentacles xuất hiện. Hiển nhiên bạn sẽ rất thích những tình huống đó. Lá 10 of Pentcales là lời tiên đoán cực tốt khi bạn đang trong giai đoạn tìm kiếm việc làm. Nếu bạn kiên nhẫn tìm kiếm, thì bạn sẽ sớm được nhận vào một vị trí mà bạn yêu thích hoặc được trả lương cao cho công việc được giao. Dù cho thế nào đi nữa thì cũng đừng bao giờ từ bỏ. Mọi thứ đang đi lên kể cả khi bạn không thấy nhiều dấu hiệu chứng tỏ điều đó sẽ xảy ra.", "Lá bài Ten of Pentacle là một dấu hiệu rất hạnh phúc về tình yêu. Nếu hiện bạn đã có một mối tình đã hứa hẹn và cam kết, thì lá bài này có thể hàm ý rằng mối quan hệ tình cảm của bạn sẽ sớm tiến lên một mức độ mới; chung sống cùng nhau, đính hôn, kết hôn, sinh con… mọi thứ đều có thể là thông điệp mà lá bài muốn truyền tải. Nhiều khả năng bạn sẽ sớm được trải nghiệm một giai đoạn yên bình và ngập tràn hạnh phúc trong cuộc sống yêu đương. Hãy tận hưởng những cảm giác đó và đảm bảo rằng người yêu của bạn biết được rằng trong mắt bạn, họ có ý nghĩa với bạn nhiều như thế nào. Nếu bạn chưa có một mối quan hệ yêu đương nào, thì lá bài này có thể hàm ý rằng một tình yêu mới sẽ có thể bước vào cuộc sống của bạn rất nhanh chóng, đến mức khiến đầu óc bạn bị quay cuồng. Hãy luôn cởi mở.", "Khi lá bài Ten of Pentacles xuất hiện, câu trả lời cho mọi thắc mắc về tài chính của bạn nhiều khả năng sẽ vô cùng tích cực. Tiền bạc sẽ đến với bạn nhiều hơn trước đây. Bạn sẽ có nhiều hơn số tiền mà bạn cần để đáp ứng các nhu cầu của cuộc sống. Hãy biết đầu tư cho tương lai lâu dài và cố gắng tiết kiệm một ít từ nguồn tiền dồi dào đang đến với bạn.", "Cũng như với các khía cạnh khác, lá 10 of Pentacles cũng là một dấu chỉ tích cực khi xét trong bối cảnh câu hỏi về sức khỏe. Những thông tin liên quan đến sức khỏe của bản thân bạn sẽ rất tốt. Ít nhất thì bạn cũng sẽ cảm thấy cực kỳ khỏe mạnh ngoài mong đợi trong một khoảng thời gian nào đó, cũng như lạc quan về tương lai. Hãy suy nghĩ xem bạn có thể làm gì để cải thiện tốt hơn nữa cũng như duy trì được tình trạng sức khỏe ấn tượng này trong tương lai.", "Đây sẽ là thời điểm gợi lên sự thanh bình, niềm vui và sự sung túc dành cho bạn. Làm sao chia sẻ được những điều này? Làm cách nào bạn có thể đào sâu hơn nữa sự phát triển và khám phá tinh thần khi tự thân bạn đã đang cảm thấy quá hạnh phúc và tốt đẹp rồi? Hãy tận hưởng những cảm giác tốt này, và chia sẻ chúng dưới mọi hình thức trong khả năng của bạn. Hãy ghi nhớ lại những khoảnh khắc này để khi những lúc khó khăn xuất hiện lại (vì cuộc sống luôn là một chu kỳ thay đổi), bạn có thể hồi tưởng lại khoảng thời gian hạnh phúc này và lạc quan rằng những điều tốt đẹp trong quá khứ sẽ còn quay lại."),
                new TarotCard("Page of Pentacles", R.drawable.page_of_pentacles, 74, "Tiểu đồng tiền", "người trẻ thực tế, tầm nhìn ngắn hạn, lãng phí, rộng tãi, tin tức không thuận lợi", "Nhìn chung lá bài Page of Pentacles tượng trưng cho nguồn năng lượng tuyệt vời sẽ hỗ trợ bạn khi bạn cảm thấy chán ngấy không thể hoàn thành được công việc của mình. Page of Pentacles có thể đem lại cho bạn sự tập trung, nghị lực, và nguồn lực tài chính cần thiết để theo đuổi một nhiệm vụ nhàm chán hay thiếu thú vị. Hãy nhớ đừng chỉ nỗ lực một cách miễn cưỡng, nếu không bạn sẽ nếm mùi thất bại.", "Lá Page of Pentacles có thể hàm ý rằng bạn có nhiều công việc phải làm hơn quỹ thời gian thực tế mà bạn có để hoàn thành chúng. Đừng tuyệt vọng, hãy xắn tay áo lên và bắt đầu công việc ngay. Nhìn chung tin tức về công việc đều ổn, dù có thể bạn sẽ cảm thấy quá tải và không hứng thức. Hãy nhớ câu chậm mà chắc. Nếu bạn cần sự giúp đỡ để hoàn thành công việc, thì đừng ngại ngần yêu cầu. Luôn có sẵn sự hỗ trợ từ nhiều nguồn dành cho bạn.", "Nếu bạn đang trong một mối quan hệ tình cảm gắn kết khi lá bài này xuất hiện, thì sự xuất hiện của lá bài này có thể là dấu hiệu cho thấy cuộc tình này có đôi chút thiếu lãng mạn. Tuy không hàm ý một hồi chuông cảnh tỉnh, nhưng lá bài vẫn là lời kêu gọi bạn và người yêu bạn nhanh chóng hành động và thảo luận với nhau. Để tạo ra sự thay đổi và cải thiện mối quan hệ này, bạn cần phải hết sức chân thật về cảm giác của mình. Nếu hiện tại bạn chưa ràng buộc chuyện tình cảm, và cảm thấy như thể bạn không có cơ hội gặp gỡ được đúng người, vậy thì hãy xem xét cẩn thận những gì bạn đang giữ trong lòng mình. Bạn có thể vẫn còn vương vấn tình cũ, và có thể cần buông bỏ những ký ức xưa đi trước khi bạn có thể kỳ vọng mọi chuyện sẽ diễn biến tốt đẹp hơn.", "Về mặt tài chính, lá Page of Pentacles hàm ý đến những phần thưởng và thành quả mà bạn sẽ nhận được nhờ làm việc tốt và siêng năng. Dù vậy đây không phải thời điểm để đầu tư may rủi hay làm điều gì đó không thực tế. Ngược lại đây là lúc đưa ra một đánh giá chân thực về giá trị của các kỹ năng và nỗ lực mà bạn đã bỏ ra. Nếu bạn không được trả công hay nhận được những thứ xứng đáng được nhận, thì đây có thể là lúc để tạo ra thay đổi. Hãy biết đầu tư, đừng tiêu xài phung phí. Hãy chọn đổ tiền vào những lĩnh vực tư an toàn và mang tính truyền thống.", "Bất kỳ vấn đề sức khỏe nào biểu hiện ở thời điểm hiện tại đều là hậu quả của những vấn đề rối loạn trong cảm xúc. Liệu bạn có cần được quan tâm không? Nên nhớ bạn không nhất thiết phải là một bệnh nhân thì mới đáng được quan tâm chăm sóc. Hãy nói ra điều bạn cần. Tuy nhiên, hãy đảm bảo rằng bất kỳ triệu chứng nào liên quan đến sức khỏe của bạn cũng sẽ được đánh giá đúng và phù hợp bởi một chuyên gia chăm sóc sức khỏe có chuyên môn. Nếu họ nói không có vấn đề gì xảy ra với bạn, vậy thì hãy xem xét thêm các trạng thái về tình thần và cảm xúc. Trong phần lớn thời điểm bạn vẫn nắm giữ được chiếc chìa khóa tìm đến nguồn sức khỏe và sức sống dồi dào.", "Nếu bạn đang cảm thấy bất ổn về tinh thần, vậy thì đây là thời điểm tốt để xem xét những điều gì có thể tạo được niềm hứng khởi cho bạn. Bạn đang tận hưởng và trải nghiệm điều gì? Bạn đang quan tâm điều gì? Bạn đang muốn khám phá những gì? Hãy chắc chắn rằng bạn đã rời bỏ con đường cũ mòn và thử nghiệm những hướng đi mới mẻ. Hãy tạo ra nguồn năng lượng và niềm hăng hái cho chính bạn. Hãy tìm kiếm những người có nhận thức tinh thần tích cực và lạc quan nhất bạn biết, và nói những điều khích lệ họ. Bạn sẽ học hỏi được rất nhiều điều hữu ích."),
                new TarotCard("Knight of Pentacles", R.drawable.king_of_pentacles, 75, "Kị sĩ tiền", "khôn khéo, kiên trì, hám lợi, chán nản, bất cần", "Lá Knight of Pentacles nhắc nhở chúng ta dành sự tập trung vào các vấnđề thuộc thế giới vật chất và những lĩnh vực bình thường của cuộc sống. Có câu nói rằng “Có công mài sắt có ngày nên kim”. King of Pentacles yêu cầu bạn nhìn nhận và xem xét những phương cách bạn đang dùng để giải quyết những vấn đề của mình.", "Đây không phải là thời điểm đề xuất giải quyết công việc bằng những phương án phá cách hơn so với bình thường. Bạn cần phải làm việc bằng những phương thức trong khuôn khổ sẵn có và hoàn thành những gì được giao. Nếu bạn đang tìm kiếm công việc, thì đây có thể là một dấu hiệu tốt rằng vị trí bạn cần đang sắp tìm đến bạn rồi, tuy nhiên bạn cũng cần chú ý khi tham gia các buổi phỏng vấn, hãy đảm bảo rằng bạn nỗ lực mang lại những ấn tượng cho nhà tuyển dụng về mức độ đáng tin cậy và tính kiên định, vì họ không thích những nhân viên thiếu nghiêm túc trong công việc đâu", "Trong bối cảnh về tình yêu, là bài này hàm chỉ những trách nhiệm và nghĩa vụ của mỗi bên trong một mối quan hệ yêu đương. Tình yêu không phải lúc nào cũng chỉ có sự mê đắm và lãng mạn. Một tình yêu thực sự sẽ bao gồm sự sự sẵn lòng hợp tác cùng nhau để vượt qua các thử thách và xây dựng mối quan hệ được bền vững. Làm cách nào bạn có thể đáp ứng tốt được các trách nhiệm của mình? Nếu bạn đang tìm người yêu khi rút được lá bài này, thì lá bài chính là một dấu hiệu rõ ràng cho biết rằng bạn cần thay đổi những suy nghĩ và hành động theo lối mòn của mình và thử nghiệm những điều mới mẻ nếu bạn muốn gặp một người mới. Bạn cần phải đánh đổi điều gì đó để có được cái mình mong muốn.","Khi lá King of Pentacles xuất hiện trong câu hỏi về vấn đề tài chính, lá bài cho bạn biết rằng bạn sẽ sớm có thêm nhiều tiền bạc hơn dưới nhiều hình thức, ví dụ như được tăng lương, được trúng số, được thừa kế gia sản… Bất cứ điều gì cũng hoàn toàn có thể xảy ra. Cho dù vậy, bạn vẫn cần phải làm việc chăm chỉ và đừng đổ tiền đầu tư nhiều hơn mức độ bạn chấp nhận bị thua lỗ.", "Liên quan đến vấn đề sức khỏe, Knight of Pentacles hàm ý rằng vấn đề sức khỏe lớn nhất của bạn thục ra chính là sự bi quan. Hãy thử suy xét từ những điều cơ bản: liệu bạn có ngủ đủ giấc, tập luyện thể thao đủ giờ, và ăn uống đủ chất chưa? Cần biết rằng bạn không nhất thiết phải trải qua một chế độ ăn uống hà khắc và không thoải mái để có thể cảm thấy sức khỏe mình trở nên tốt hơn? Hãy tiến từng bước một, đó có thể sẽ là phương án cải thiện cuộc sống mà bạn có thể thích nghi cũng như duy trì lâu dài được.","Bạn có thể đột nhiên nhận thấy bản thân mình đang bị quá tải xét trên khía cạnh tinh thần mà không có lý do cụ thể. Hãy thử kiểm soát từng mảng miếng nhỏ trong tinh thần của bản thân, vì bạn không cần phải cố gắng khắc phục toàn bộ mọi vấn đề trong cuộc sống của mình cùng lúc. Bạn đang ở lưng chừng giai đoạn thay đổi trong tinh thần khi điều này xảy ra. Hãy ghi nhớ rằng bạn thay đổi là để tốt hơn."),
                new TarotCard("Queen of Pentacles", R.drawable.queen_of_pentacles, 76, "Hậu tiền", "giàu có, quảng đại, sang trọng, lòng tin đặt nhầm chỗ, dễ thay đổi", "Lá bài này có thể hàm ý rằng bạn sẽ dành thời gian, tiền bạc, và năng lượng cho gia đình của mình, để gia đình trở thành một nơi thoải mái và yên bình hơn. Đây là một lời khuyên tốt, miễn là bạn đừng để rơi vào nợ nần vì những mục đích này. Nhiều người sẽ tìm đến bạn để được sáng trí và xin lời khuyên. Bạn sẽ có thể giúp được họ nếu bạn sẵn sàng. Đây là giai đoạn bạn có thể đạt được nhiều thành tựu.","Công việc của bạn có vẻ đang rất tiến triển. Một người phụ nữ nào đó có thể đóng vai trò quan trọng trong sự nghiệp của bạn. Nếu có một người như thế thật sự, hãy yên tâm rằng cô ấy là “đồng minh” của bạn trong công việc. Nếu có thể, hãy theo mang một số vật dụng từ nhà đến nơi làm việc. Điều này sẽ giúp bạn cảm thấy môi trường làm việc trở nên gần gũi như ở nhà hơn, nhờ đó bạn sẽ làm việc hiệu quả hơn.", "Trong bối cảnh câu hỏi về tình yêu, Queen of Pentacles là một lá bài thiên về tính nữ và hàm chỉ định hướng, đặc biệt dành cho những người hiện chưa có sự gắn kết tình cảm với bất kỳ ai. Định hướng chiến thuật tìm kiếm tình yêu là một định hướng thiên về tính nữ. Đừng chỉ theo đuổi tình yêu, thay vào đó hãy làm cho bản thân mình sẵn sàng đón nhận tình cảm. Tuy nhiên cũng đừng quá tuyệt vọng đến mức bất chấp tất cả để thiết lập mối quan hệ tình cảm với một người mới mẻ trong cuộc sống của bạn. Khi thời điểm thích hợp đến, tình yêu sẽ tìm đến mà bạn không cần làm gì khác. Hãy luôn lạc quan, và tâm niệm rằng bạn đáng được yêu thương.", "Queen of Pentacles là một dấu hiệu tuyệt vời khi xét trong bối cảnh tài chính tiền bạc. Sự thịnh vượng và đủ đầy tài chính của bạn rất có thể sẽ còn tăng cao hơn. Đừng e ngại việc chi ra một khoản trong tổng số tiền bạn kiếm được. Việc tiết kiệm là cần thiết, nhưng việc tận hưởng cuộc sống khi bạn có điều kiện cũng quan trọng không kém. Hãy chia sẻ những gì bạn có khi bạn có thể.", "Xét trong bối cảnh sức khỏe, Queen of Pentacles là lời đề nghị bạn dành nhiều thời gian và không gian hơn để tập trung vào chính mình trước, từ đó sức khỏe của bạn mới thực sự được cải thiện. Nếu bạn liên tục chỉ dành thời gian và không gian cho những người khác thay vì ưu tiên cho chính bản thân bạn và những nhu cầu của bạn trước, thì sức khỏe của bạn sẽ sớm bị đe dọa. Hãy biết cân bằng mọi thứ. Nên nhớ bạn cũng quan trọng không kém những người khác.", "Một điều quan trọng cần nhớ là bạn phải có niềm tin vào bản thân mình. Tuy bạn có thể không nhìn nhận những thứ thuộc về tinh thần theo cách mà người khác thấy, nhưng hệ thống niềm tin của bạn là của riêng bạn, đó mới là điều quan trọng nhất. Khi nhắc đến đời sống tinh thần, tuy việc lắng nghe từ người khác là rất quan trọng nhưng sau cùng thì chính chúng ta mới là người đưa ra quyết định cho riêng mình. Nếu người khác không hiểu quyết định của bạn, đó đơn giản là vấn đề của họ."),
                new TarotCard("King of Pentacles",R.drawable.king_of_pentacles,77, "Vua tiền", "dũng cảm, thực hiện kinh doanh, thông minh, năng khiếu, nguy hiểm, xấu xa", "Nhìn chung King of Pentacles thúc giục chúng ta nhanh chóng trở thành những người phục vụ tốt xét trong cả cuộc sống bản thân, các nguồn năng lượng và những nguồn lực cụ thể mà ta có thể sử dụng. Nếu lá bài hàm chỉ một người đàn ông trong cuộc sống bạn, thì người đó nhiều khả năng sẽ là người mà bạn có thể trông cậy và tín thác hoàn toàn.", "Một người đàn ông chững chạc, uy tín sẽ đóng vai trò chủ đạo trong môi trường làm việc của bạn (ngay cả khi bạn đang làm việc ở nơi toàn là phụ nữ). Người đàn ông này khôn ngoan và công bằng, nhưng có đôi chút vội vã bỏ qua việc đánh giá. Người ấy có thể trở thành một người hỗ trợ đáng tin cậy của bạn cho dù bạn chưa gặp người ấy được bao lâu. Hãy luôn sẵn sàng xem xét mọi thứ kỹ lưỡng – bạn sẽ yên tâm khi bạn như vậy.", "Lá King of Pentacles là một dấu hiệu tốt khi xuất hiện trong bối cảnh câu hỏi về tình yêu. Một ai đó có thể xuất hiện và thu hút sự chú ý của bạn ngay từ cái nhìn đầu tiên. Hãy cứ tiến lên theo tiếng gọi con tim, nhưng đừng cố tỏ ra là một người khác mà không phải là chính mình. Người này sẽ chỉ yêu chính con người bạn mà thôi. Hãy sống chân thật và mọi chuyện sẽ trở nên ổn thỏa cho bạn. Nếu bạn đang trong một mối quan hệ gắn kết với một ai đó, vậy thì mối quan hệ này rất nhiều khả năng sẽ tiến triển lên một vị thế thoải mái hơn nữa (theo nghĩa ẩn dụ). Nhưng nếu bạn không cảm thấy hài lòng về bất cứ điều gì trong cuộc tình này, thì bạn cần phải nói ra điều đó để kỳ vọng vào một sự thay đổi. Đừng mong đợi nửa kia của mình sẽ hiển nhiên biết được suy nghĩ của bạn.", "King of Pentacles là một lá bài tuyệt vời khi xét trong những vấn đề tài chính. Gần như tất cả những khoản đầu tư của bạn đều đang tiến triển rất tốt và còn sinh lợi nhiều hơn nữa nhờ những nỗ lực hoặc phương thức đầu tư khôn ngoan mà bạn đã thực hiện. Dù vậy đây vẫn không phải thời điểm để suy nghĩ vội vàng. Hãy cứ chấp nhận những rủi ro có tính toán, nhưng đừng bị sa đà. Hãy san sẻ của cải của mình khi bạn cảm thấy nên làm vậy. Hãy nhớ rằng tiền vẫn chỉ là tiền, thứ mà bạn sẽ không thể mang theo mãi mãi được.", "Tình trạng sức khỏe bạn có thể được cải thiện nếu bạn chắc chắn rằng bạn dám đối mặt và giải phóng những cảm xúc tiêu cực của bản thân. Tuy nhiên, bạn cần phải rất thông suốt về cách thức đối phó với những cảm xúc tiêu cực này, cũng như nhận thức rõ ở đâu và khi nào bạn sẽ làm như vậy. Những truyền thống văn hóa và nghi thức thanh tẩy có thể giúp bạn phần nào cảm thấy thoải mái hơn vào lúc này. Những gì làm bạn thoải mái về tâm hồn và tinh thần cũng sẽ làm cơ thể bạn thoải mái. Những nghi thức về sức khỏe nào bạn đang áp dụng? Nếu bạn chưa từng thử bất kỳ phương thức nào trước đây, hãy thử trải nghiệm cho đến khi cảm thấy thoải mái. Sức khỏe của bạn sẽ nhận được rất nhiều lợi ích từ những phương thức điều trị như vậy.","Có thể bạn không hiểu rõ những gì bạn cần để làm cho tinh thần mình được thỏa mãn, có lẽ vì trong nhiều năm qua bạn đã dành tâm trí của mình chủ yếu cho việc đảm bảo nguồn tài chính ổn định cho cuộc sống mà thôi. Hãy suy nghĩ lại một chút về vấn đề này. Nếu bạn chưa có bất kỳ nền tảng khái niệm nào trong văn hóa tinh thần, hãy tìm đến Internet hay nguồn thông tin từ thư viện địa phương để học hỏi về những nguồn tư tưởng mới. Hãy dành thời gian để nuôi dưỡng khía cạnh tinh thần của bản thân. Dù bạn tin hay không, thì tình hình tài chính của bạn cũng sẽ nhờ đó mà được hưởng nhiều lợi ích về sau.")
        };

        // Chèn dữ liệu vào bảng TarotCard
        for (TarotCard card : cards) {
            ContentValues values = new ContentValues();
            values.put("name", card.getName());
            values.put("image", card.getImage());
            values.put("cardNumber", card.getCardNumber());
            values.put("otherName", card.getOtherName());
            values.put("keyword", card.getKeyword());
            values.put("overview", card.getOverview());
            values.put("job", card.getJob());
            values.put("love", card.getLove());
            values.put("finance", card.getFinance());
            values.put("health", card.getHealth());
            values.put("spirit", card.getSpirit());
            db.insert(TB_TAROTCARD, null, values);
        }
    }

